(window.webpackJsonp=window.webpackJsonp||[]).push([[195],{"k+k0":function(e,l,t){"use strict";t.r(l);var a=t("q1tI"),n=t.n(a),r=t("cvUp"),c=t("CKC9"),m=t("gEOm");l.default=function(){var e=c["IR-14"].cities["IR-1405"];return n.a.createElement("div",{className:"w-full"},n.a.createElement(m.a,{menu:{introduction:"آشنایی با شهرستان",analyze:"تحلیل شهرستان زنجان",jobs:{value:"تعیین کسب‌وکارهای مزیت‌دار شهرستان",children:{handicrafts:"صنایع دستی",food_industry:"صنایع غذایی",livestock:"دامداری",conversion_complementary_industries:"صنایع تبدیلی و تکمیلی",birds:"طیور",services:"خدمات",fisheries:"شیلات",gardening:"باغداری",agriculture:"زراعت",industry:"صنعت"}}},city:e},n.a.createElement(r.a,{id:"introduction",title:"آشنایی با شهرستان"},n.a.createElement("p",null,"شهرستان زنجان مرکز استان زنجان است و براساس آمار منتشر شده دارای ۵۲۱٬۳۰۲ نفر جمعیت در سال ۱۳۹۵ خورشیدی بوده و بیستمین شهر کشور از لحاظ جمعیت محسوب می‌شود. نام زنجان عربی‌شده واژه زنگان است. مردم منطقه هنوز هم تلفظ زنگان را به کار می‌برند. این شهرستان از شمال به استان‌های آذربایجان شرقی و اردبیل، از شرق به شهرستان‌های طارم و ابهر، از جنوب به شهرستان‌های ایجرود و خدابنده و از غرب به شهرستان ماهنشان و از شمال غربی به شهرستان میانه محدود می‌شود. شکل زیر موقعیت شهرستان زنجان را نشان می دهد."),n.a.createElement("p",null,"دین اغلب مردم زنجان، اسلام است و اهالی زنجان پیرو مکتب شیعه به‌شمار می‌روند. هر ساله دسته عزاداری حسینیه اعظم زنجان از خیابان فردوسی تا بلوار آزادی که فاصله چند کیلومتری است در هشتم محرم با قدمت بیش از یکصد ساله برگزار می‌شود که از نمادهای عزاداری شماری از مردم زنجان است. حسینیه اعظم زنجان یکی از بزرگترین مراکز مذهبی جهان تشیع است و روز هشتم محرم هر سال دسته عزاداری بزرگی را برگزار می‌کند که قدمتش به دوران قاجاریه می‌رسد. این دسته دومین قربانگاه جهان اسلام پس از منا در عربستان و نخستین قربانگاه جهان تشیع در زنجان به‌شمار می‌رود. این اجتماع به عنوان دهمین میراث معنوی کشور ثبت گردیده‌است. از زنجان با عنوان پایتخت شور و شعور حسینی نیز یاد می‌شود."," ")),n.a.createElement(r.a,{id:"analyze",title:"تحلیل شهرستان زنجان"},n.a.createElement("p",null,"مزيت‌سنجي و اولويت‌بندي كسب‌وكارهاي موجود در هر شهرستان برمبناي يک روش تحقيق انجام مي‌شود. در اين روش تحقيق، يک شناسنامه براي هر شهرستان و يک شناسنامه براي هر كسب‌وكار تدوين مي‌شود. اين شناسنامه‌ها به عنوان ورودي به نرم‌افزار داده‌شده و خروجي نرم‌افزار هم‌پوشاني اين دو شناسنامه و تعيين مشاغل مزيت‌دار شهرستان است. اين اولويت‌بندي‌ها توسط كارشناسان و افرادخبره هر شهرستان بازبيني و اصلاح مي‌شوند. يكي از منابع تحليل همين ليست مزيت‌سنجي اصلاح‌شده‌است. يكي ديگر از منابع تحليل، مصاحبه با كارشناسان و متخصصين حوزه اشتغال هر شهرستان است. در هر شهرستان با حداقل 3 نفر از اين افراد مصاحبه انجام مي‌شود. اين مصاحبه‌ها پياده‌سازي و ارزيابي مي‌گردد. در صورت وجود تناقض و ابهام بين اين 3 مصاحبه و ليست مزيت‌سنجي، مصاحبه‌هاي تكميلي و حتي مصاحبه بيشتر هم صورت مي‌گيرد تا تيم تحليل به نتيجه نهايي مطلوب دست‌يابد. بنابراين خروجي هر شهرستان از تجميع مصاحبه‌ها و ليست مزيت‌سنجي حاصل مي‌شود. در ادامه تحليل شهرستان زنجان ارائه‌شده‌است.")),n.a.createElement(r.a,{id:"jobs",title:"تعیین کسب‌وکارهای مزیت‌دار شهرستان"},n.a.createElement("p",null,"همانطوريكه در قسمت مقدمه اشاره‌شده مبناي تحليل، مصاحبه‌ها و ليست مزيت‌سنجي است. از بررسي اين دو منبع رسته‌هاي رايج و اولويت‌دار شهرستان تعيين شده‌است. مبناي اين اولويت‌بندي، فراواني و ميزان اشاره افراد كارشناس به كسب‌وكارهاي رايج در هر شهرستان و اولويتي كه اين مشاغل در ليست مزيت‌سنجي دارند، است. در هر رسته كسب‌وكارهاي رايج به ترتيب اولويت مشخص شده‌است. در تحليل به مشاغل موجود، مشاغل قابل توسعه و مشاغلي كه هنوز در شهرستان راه‌اندازي نشده و پتانسيل ايجاد آن‌ها در شهرستان وجوددارد، اشاره شده‌است. مزيت‌هاي شغلي به ترتيب اولويت در ادامه آورده شده‌است.")),n.a.createElement(r.a,{title:"صنایع دستی",id:"handicrafts",size:"h3"},n.a.createElement("p",null,"از ديرباز و از زمان‌هاي قدیم، تقريبا در سراسر ميهن عزيز، هرگاه سخن از زنجان به ميان آيد، نقش چاقوهاي زنجان در اذهان پديدار مي‌گردد و در بسياري از اقلیم های دور و نزديك چاقوهاي ظريف و بادوام زنجان با هويت آن عجين گشته و گويي شناسنامه اين شهر گرديده است. البته بدین معني نيست كه در اين شهر صنعتگران و هنرمندان ديگر رشته هاي فني و صنعتي فاقد اعتبارند، بلكه به این معنی است كه چاقوي آن بر خلاف صنايع بومي ديگر چون مورد استفاده بيشتر مردمان قرار گرفته و از اين نظر شناسايي شده، مقام ارجمندي را به خود اختصاص داده است. چاقوهاي تولید شده در زنجان به سبب وجود تنوع و تمايز خاص از نظر مصالح فن، طرفداران زیادی پیدا کرده اند. با توجه به اینکه این شهرستان قطب تولید چاقو است و محصولات تولیدی آن در این زمینه صادر شده و یا توسط مهمانان و گردشگران خریداری می‌شوند، توسعه این صنعت، ارزش افزوده زیادی را به دنبال خواهد داشت."),n.a.createElement("p",null,"صنعت دستي مس در زنجان به بيش از هزار سال قبل (دوره ساسانيان) باز مي‌گردد و گويا مهمترين مقطع رشد صنايع مس زنجان، در دوره سلجوقيان بوده كه صنعتگران زنجاني براي اين اقوام تازه استقرار يافته ترك، آفتابه لگن‌هاي مسي با نقش حيوانات اساطيري مي‌ساختند. بعدها در دوره ايلخانان با ورود تعدادي هنرمند چيني به زنجان، صنعت مس زنجان تكامل بيشتري يافت. صنایع مسی نیز مانند چاقوی زنجان، به دلیل کیفیت بالایی که دارند، صادر می‌شوند و باتوجه به استقبال عظیمی که از صنایع مسی شده است، توسعه آنها، توجیه اقتصادی دارد."),n.a.createElement("p",null,"ملیله کاری همواره به عنوان یکی از اصیل ترین صنایع دستی استان زنجان مطرح است. ملیله به مفهوم ظریف کاری نقره است که با استفاده از مفتول‌ها و نوارهای ظریف نقره و بکار گیری طرح های اسلیمی و منظم ساخته می شود، مصنوعات ساخته شده شامل: سرویس چایخوری، شکلات خوری، گلدان، شمعدان، قاب عکس و انواع زیورآلات و….، است. این مورد هم با توجه به سوددهی خوب آن، قابلیت توسعه را دارد."),n.a.createElement("p",null,"هنر قلمزنی عبارت است از کندن نقوش بر روی سطوح فلزی به ویژه مس، طلا، نقره و برنج به وسیله انواع قلم‌ها که با ضربه چکش بر روی اجسام فلزی انجام می گیرد. این هنر نیز از جمله صنایع دستی مطرح و شناخته شده زنجان است که بازار خاص خود را دارد؛ بنابراین توسعه آن دارای مزیت است."),n.a.createElement("p",null,"زنجان نقش مهمی در ارتقاء هنر فرشبافی ایران داشته است، چرا که طراحان و تولیدکنندگان فرش سهم قابل توجهی در رونق و احیاء صنعت فرشبافی و ورود آن به بازار جهانی داشته‌اند. فرش افشار زنجان به دلیل کیفیت و دوام بالا به فرش آهنین ایران شهرت دارد و در بازارهای بین‌المللی شناخته شده است. خوش‌نامی فرش افشار زنجان از بافت ظریف، استحکام بالا، کیفیت بالای نخ، رنگرزی ماهرانه، استفاده از پشم محکم و انعطاف‌پذیر، اصالت، طرح و تنوع رنگ نقشه و طول عمر بالای فرش نشأت می‌گیرد. فرش‌های دستباف به سه دسته شهری، عشایری و روستایی تقسیم‌بندی می‌شوند، فرش افشار زنجان در دسته فرش‌های روستایی قرار می‌گیرد. البته دارهای قالیبافی بسیاری در شهر زنجان برپا است، اما تعداد دارهای شهری کمتر از دارهای روستایی است. وجود چراگاه‌های وسیع و رونق کشاورزی و دامپروری در ناحیه افشار زنجان باعث شده که بخش اعظم پشم مورد نیاز برای بافت فرش به صورت محلی تهیه شود. در ضمن با توجه به افزایش قیمت ها، تمایل مردم به بافت فرش، مجددا افزایش یافته است؛ بنابراین گسترش این مورد نیز صرفه اقتصادی دارد."),n.a.createElement("p",null,"در زنجان نوعی چاروق تولید می‌شود که رویه آن چرم قرمز رنگی (که دباغان زنجان تهیه می کردند) است و معمولاً هم بدون بند و تسمه می‌باشد و نوکی عقابی و برگشته دارد. این نوع چاروق که تولید آن از حدود 30 سال پیش نیز رواج دارد، فرم و شکل خاص و ظریفی دارد و دارای حالتی زینتی است و بیشتر برای راه رفتن روی قالی مورد استفاده قرار می گیرد. کار کردن در این حوزه نیز از مزیت های خوب این شهرستان است."),n.a.createElement("p",null,"گليم بافي يكي از شاخه هاي قاليبافي است و در آن از طرح هاي مختلف محلي استفاده مي شود. برخی از مردم زنجان با مهارت گلیم بافی آشنا هستند و این کار را انجام می‌دهند. این مورد نیز در صورت تامین بازار، همچنان جای کار دارد.")),n.a.createElement(r.a,{title:"صنایع غذایی",id:"food_industry",size:"h3"},n.a.createElement("p",null,"راه‌اندازی کسب و کار فراوری محصولات کشاورزی مهم ترین روش برای ایجاد اشتغال پایدار و کاهش وابستگی به درآمد های نفتی و عاملی مهم در توسعه پایدار امنیت غذایی کشور است. ایجاد صنایع غذایی، علاوه بر اشتغالزایی، باعث ارتقای سطح درآمد ارزی می‌شود که کاهش هدررفت محصولات کشاورزی و ایجاد ارزش افزوده از منابع را تا حد امکان ممکن می‌کند."),n.a.createElement("p",null,"طبق بررسی های انجام شده و با استناد به لیست مزیت سنجی، تهیه نان، شیرینی پزی و تولید رشته آشی از مزیت های خوب این شهرستان است که سود بالایی به همراه دارد؛ زیرا هم مواد اولیه آنها موجود است و هم فعالان این زمینه مهارت های لازم برای تولید انواع نان و شیرینی را دارند. شیرینی از دیرباز مورد توجه ایرانیان با ذائقه های مختلف بوده و در تمامی مراسم ها و جشن ها کاربرد داشته است. پخت شیرینی در ابعاد وسیع و کارگاهی و حتی در محیطی کوچک (در خانه) امکان پذیر است. شیرینی محصولی است که در انواع متفاوت و با طعم های متنوعی تهیه می شود. به علاوه این که با تغییر در مواد تشکیل دهنده آن می توان برای افراد رژیمی هم شیرینی تهیه کرده و این افراد را نیز به بازار هدف خود اضافه کرد. این کسب و کار را می توان با تجهیزات کم و سرمایه پایینی شروع کرد و در زمان کوتاه با فروش محصول به سود بالایی رسید. پخت شیرینی یکی از مشاغل زودبازده است و یک فرصت شغلی مناسب مخصوصا برای زنان خانه دار محسوب می شود. یافتن بازار فروش محصول یکی از ضروریات این حرفه می‌باشد که با تبلیغات مناسب مخصوصا در فضای مجازی می توان به آن دست یافت. توجه به این نکته ضروری است که یکی از لازمه های موفقیت در این شغل گذراندن دوره های آموزشی مرتبط با پخت شیرینی است. لازم به ذکر است تولید رشته آشی و برند سازی برای آن مانند رشته آشی گوزدره سوددهی خوبی را خواهد داشت. در ضمن تولید سایر فراورده های گندم مانند پفک گندم، برشته گندم و... نیز موفق خواهند بود که در اولویت‌های آخر این رسته قرار می‌گیرند."),n.a.createElement("p",null,"با توجه به وجود باغات میوه، فراوری این موارد شامل تهیه چیپس میوه، میوه خشک، آبمیوه و سایر فراورده های میوه مانند مربا، لواشک، کمپوت و ... پتانسیل های لازم برای سرمایه گذاری و بازدهی را دارد. خشک کردن میوه‌جات، یکی از روش‌هاي جلوگیري از فساد میکروبی آن‌ها است. در این روش، در اثر کاهش میزان رطوبت، امکان فساد میکروبی بسیار کم شده و سرعت دیگر واکنش‌هاي مضر نیز، به مقدار قابل توجهی کاهش می‌یابد. خشک کردن، ضمن اینکه روي محصول اثر حفاظتی دارد، وزن و حجم آن را نیز به میزان چشمگیری کاهش می‌دهد، در نتیجه از هزینه‌های حمل و نقل و ذخیره‌سازي نیز می‌کاهد. با توجه به اینکه بخشی از بازار میوه خشک ایران، سهم محصولات وارداتی است، با تولید مشابه داخلی این میوه‌ها (از نظر نوع و کیفیت)، می‌توان مانع واردات و خروج میلیون‌ها دلار ارز از کشور شد. همچنین نیاز بیش از حد برخی از کشورها به این گونه محصولات (به دلیل شرایط ویژه اقلیمی آن‌ها، هزینه بالای حمل و نقل سبزیجات و میوه‌های تازه و محدود بودن زمان نگه‌داری آن‌ها)، فرصت مناسبی برای رونق تولید و صادرات چیپس میوه فراهم آورده است."),n.a.createElement("p",null,"آبمیوه یکی از بهترین جایگزین‌ها برای نوشابه‌های گازدار در سراسر دنیا است. این نوشیدنی تهیه شده از انواع میوه، از نظر سلامتی ارزش غذایی بالایی دارد و بر این اساس میزان مصرف آن در کشورهای مختلف جهان دائما در حال افزایش است. صنعت آبمیوه در هر کشوری از صنایع اساسی در بخش غذایی به شمار می‌رود و این موضوع موجب شده تا بازار جهانی انواع آبمیوه و کنسانتره بسیار پررونق و گرم باشد؛ اما نکته ای که بر بازار فروش آبمیوه تاثیر دارد و باید به آن دقت شود نوع بسته بندی محصول است. بسته بندی شکیل تر در کنار کیفیت خوب محصول تاثیر بسیار خوبی در افزایش محبوبیت کالا خواهد داشت."),n.a.createElement("p",null,"از انگور فراورده های غذایی مختلفی مانند شیره، کشمش، مویز، سرکه، روغن هسته انگور و آبغوره به دست می‌آید که هر کدام ارزش غذایی و بازار مصرف خاص خود را دارند. روغن هسته انگور از جمله روغن‌های گیاهی برشمرده می‌شود که با بالابردن کلسترول خوب بدن و پایین آوردن کلسترول بد به پاکیزگی رگ‌های خونی و شست‌وشوی آنها کمک کرده و از بیماری‌های قلبی- عروقی و سکته قلبی و مغزی جلوگیری می کند. در اروپا از این روغن هم برای خام خواری و هم برای سرخ کردن استفاده می‌شود و در تهیه انواع سالادها، غذاها و همچنین تهیه شیرینی و گریل مورد استفاده قرار می‌گیرد. همین چند منظوره بودن یکی دیگر از مزیت‌های این روغن به شمار می‌آید. از طرفی در این روغن، اسیدهای چرب امگا ۳، ۶ و ۹ بالاتری نسبت به سایر روغن‌های گیاهی وجود دارد که مصرف متعادل آن می‌تواند به ارتقای سطح سلامت کمک کند. همچنین با توجه به اینکه بیش از 9۰ درصد روغن مصرفی کشور از خارج تامین می‌شود؛ توجه به ضایعات و هسته‌های میوه‌های فرآوری‌شده مانند انگور می‌تواند در کاهش وابستگی کشور موثر باشد و مورد حمایت دولت هم قرار بگیرد. روغن‌کشی از این دانه علاوه بر بحث خوراکی در بسیاری از موارد صنعتی مانند صنایع نساجی، تولید کاغذ، داروسازی، لاستیک سازی و ... هم پرکاربرد است. با توجه به این که تعداد رقبای موجود نیز در تولید این محصول کم است، سرمایه گذاری در این حوزه می تواند صرفه اقتصادی بسیاری داشته باشد."),n.a.createElement("p",null,"با توجه به اینکه توسعه باغات زیتون از جمله اولویت های اول حوزه باغداری این منطقه است، فراوری آن توجیه اقتصادی دارد. لازم به ذکر است که فراوری آن شامل تهیه زیتون پرورده، کنسرو، ترشی، روغن کشی و ... می‌شود و در حال حاضر زنجان فاقد چنین واحدی به صورت صنعتی است."," "),n.a.createElement("p",null,"سرمایه گذاری در بخش فراوری سیب زمینی شامل تولید خلال و چیپس سیب زمینی هم به سبب وجود مواد اولیه آنها در این منطقه، توجیه اقتصادی دارد. چیپس سیب زمینی یکی از محصولاتی است که به واسطه طعم جذاب خود مخاطبین زیادی در میان تمام اقشار جامعه دارد و اکثر افراد به ویژه کودکان و نوجوانان مخاطب استفاده از این محصول هستند. از آنجا که سیب زمینی در مقایسه با سایر محصولات کشاورزی مانند گندم و چغندر، در خاک‌های ضعیف و شرایط نامساعدتر آب و هوایی قابل کشت است و بیشترین ماده خشک را در مقایسه با سایر محصولات کشاورزی دارد، توجه کشورهای صنعتی به سیب زمینی‌های پربازده و مصرف آن برای محصولات فرآوری شده، معطوف شده است. همچنین امکان استفاده از تکنولوژي هاي به روز دنیا در کشورهاي تولیدکننده سیب زمینی، زمینه را برای تولید محصولاتی با ارزش افزوده بالا فراهم می آورد. بنابراین چیپس به عنوان یک ماده غذایی لذیذ و پرطرفدار در بازار مصرف مواد غذایی کالایی رقابتی است. افرادی که قصد ورود به این حرفه را دارند باید به این نکته توجه کنند که حتماً محصولات تولیدی توانایی رقابت با نشان‌های تجاری موجود در بازار را داشته باشند."),n.a.createElement("p",null,"با توجه به اینکه کاشت گل محمدی در این شهرستان انجام می‌شود، گلاب گیری گزینه شغلی مناسبی برای سرمایه گذاری است."," "),n.a.createElement("p",null,"سوسیس و کالباس به دلیل آماده سازی سریع و همچنین طعم و مزه مطلوب، در بین مردم طرفداران زیادی دارد. برای حفظ این جایگاه و افزایش تقاضای این محصولات، علاوه بر انتخاب روش های مناسب تولید و فراوری، باید به نوع و شکل بسته بندی آنها نیز توجه نمود. سوسیس و کالباس از رایج ترین فراورده های گوشتی عمل آوری شده هستند که مورد توجه مردم قرار گرفته اند. سوسیس و کالباس حاوی پروتئین های حیوانی کافی، چربی، کربوهیدرات و ویتامین بوده و می‌توان آن‌ها را از جمله غذاهای آماده در سفره خانواده ها نام برد؛ بنابراین ایجاد واحدی برای تولید سوسیس و کالباس با توجه به وجود مواد اولیه آنها در این شهرستان، سودآور خواهد بود.")),n.a.createElement(r.a,{size:"h3",id:"livestock",title:"دامداری"},n.a.createElement("p",null,"بخش کشاورزي در بیشتر کشورهاي دنیا و به ویژه ایران نقش مهمي در توسعة ملي ایفا مي‌کند. امور دام به عنوان یکي از زیربخش‌هاي مهم کشاورزي، عهده‌دار تأمین پروتئین کشور در کنار ایجاد اشتغال و صنایع وابسته است."," "),n.a.createElement("p",null,"دامداری در شهرستان زنجان به صورت صنعتی و سنتی انجام می‌شود. مردم این شهرستان در زمینه دام سبک بیشتر گوسفند افشاری و در زمینه زمینه دام سنگین بیشتر گاو هلشتاین دورگ و گاو سمینتال دورگ را پرورش می‌دهند. پرورش تمام موارد فوق با اقلیم شهرستان سازگار است و فرصت مناسبی برای سرمایه گذاری می‌باشد. گوسفند افشاری یکی از نژادهای بسیار مهم گوسفند داشتی است که بومی استان زنجان است. مدیریت پرورشی بهینه این نژاد می تواند سودآوری واحد گوسفندداری را افزایش دهد. دلایل زیادی باعث شده است که امروزه گوسفندان نژاد افشاری جزء پر فروش ترین گوسفندان باشند. این نژاد به آب و هوای سرد عادت داشته و دارای جثه ای بزرگی است. گوسفند افشاری جزء گوسفندان سنگین ‌دنبه کشور محسوب می‌گردد. سرعت رشد بالا، تولید شیر خوب و پرچرب و بهترین گوشت قرمز (که باعث‌شده است که جزء گوسفندان دو منظوره گوشتی – شیری قرار بگیرد.)، بهبود کمی و کیفی پشم گوسفند، سازگاری و عادت پذیری با شرایط آب و هوایی مناطق سردسیر، قدرت راهپیمایی و کوه پیمایی خوب، توان چند قلو زایی، مقاومت در برابر امراض و انگلهای منطقه و بالا بودن ضریب تبدیل و داشتن بازده اقتصادی مطلوب از جمله مزایای پرورش گوسفند افشاری است؛ بنابراین توسعه پرورش این دام از اولویت های اول حوزه دامداری است؛ زیرا هم پتانسیل های لازم برای پرورش در این شهرستان را دارد و هم از نظر اقتصادی ارزش افزوده زیادی خواهد داشت."),n.a.createElement("p",null,"امروزه به منظور بهره برداری بهینه، از گاو های دورگ استفاده می‌شود. که این موارد حاصل تلقیح نژاد اصلی و نژاد بومی هستند. گوساله ای که از طریق آمیخته گری متولد می شود هم برخی خصوصیات ارثی گاو نر اصیل را دارا می باشد و هم برخی خصوصیات ارثی گاو ماده بومی را دارد. در نسل های بعدی به تدریج صفات منتقل شده از گاو های اصیل در گوساله ها ظاهر می شود و تولیدات شیر و گوشت اغلب آنها نیز به نحو قابل توجهی بیشتر می شود. در شهرستان زنجان نیز پرورش گاو هلشتاین دورگ و گاو سمینتال دورگ انجام می‌شود و با توجه به سازگاری که با اقلیم منطقه داشته، همچنان قابلیت توسعه و سرمایه گذاری را دارد."),n.a.createElement("p",null,"زنبورداری نیز در برخی از مناطق شهرستان اخیرا آغاز شده و مردم نیز استقبال خوبی از آن داشته اند؛ بنابراین قابلیت سرمایه گذاری و توسعه را دارد. پرورش زنبور عسل از جمله مشاغلی است که می تواند سود خوبی به همراه داشته باشد. باردهی گل ها و میوه ها با گرده افشانی توسط زنبور عسل افزایش می یابد. این مسئله باعث شده ارزش اقتصادی پرورش زنبور عسل بسیار بیشتر از ارزش ریالی حاصل از تولید عسل باشد. در طی فرآیند پرورش و نگهداری زنبورعسل، با ازدیاد کندوها می توان به فروش کندو نیز فکر کرد و به سود خوبی دست یافت. در مناطق گرمسیری از هر کندو یک بچه کندو گرفته می شود. در ضمن محصول مزرعه پرورش زنبور عسل صرفاً کندو و عسل نیست؛ موم، زهر و ژل آن نیز قابل فروش است.")),n.a.createElement(r.a,{title:"صنایع تبدیلی و تکمیلی",id:"conversion_complementary_industries",size:"h3"},n.a.createElement("p",null,"با وجود تنوع تولید محصولات کشاورزی در زنجان، بررسی‌ها نشان می‌دهد که سالانه مقداری از آن در مراحل کاشت، داشت و برداشت به ضایعات تبدیل می‌شوند. از سوی دیگر برداشت و یا تولید بسیاری از محصولات کشاورزی در یک بازه زمانی مشخصی رخ می‌دهد؛ اما مصرف آن‌ها در طی سال صورت می‌پذیرد. لذا صنایع تبدیلی می‌تواند زمان استفاده را افزایش دهد و از میزان ضایعات آن بکاهد. همچنین گسترش این صنایع موجب اشتغال‌زایی، ایجاد درآمد و جلوگیری از اتلاف و ضایع شدن محصولات می‌گردد."),n.a.createElement("p",null,"در شهرستان زنجان سردخانه برای محصولات کشاورزی وجود دارد؛ ولی توسعه یافته و پیشرفته نیست؛ بنابراین در این بحث دلالان ورود پیدا کرده اند و محصولات را از کشاورز با قیمت پایین خریداری کرده و با قیمت بالاتر در بازار می‌فروشند؛ بنابراین احداث سردخانه در زنجان از جمله مزیت هایی است که سود خوبی را ایجاد می‌کند. از مهمترین مزایای استفاده از سردخانه میوه و سبزی می توان به جلوگیری از فساد محصول اشاره کرد. روند انواع فسادها از قبیل فساد فیزیکی، فساد شیمیایی یا تغییر رنگ محصول، فساد ناشی از وجود میکروارگانیسم ها و غیره با نگهداری در سردخانه ها به شدت کند شده و حفظ کیفیت محصول در زمان طولانی تر حاصل می گردد. از دیگر مزایای با اهمیت احداث سردخانه میوه، کنترل قیمت بازار است. ذخیره کردن میوه در سردخانه موجب می شود در ماه هایی که مصرف میوه بسیار است و فصل برداشت آن محصول خاص نیست، بازار با کمبود مواجه نشده و حباب قیمت ایجاد نگردد. در واقع با ساخت سردخانه میوه تعادلی در عرضه و تقاضا ایجاد می گردد و موجب خواهد شد از افزایش بی رویه قیمت که به ضرر مصرف کننده است و از کاهش بیش از حد آن که به ضرر باغداران است جلوگیری به عمل آید؛ بنابراین نقش سردخانه های میوه جات به عنوان حد واسط بازار و باغداران در این بین بسیار پر رنگ و حیاتی است."),n.a.createElement("p",null,"امروزه استفاده از كودهاي حيواني در باغ ها و مزارع كشاورزي انجام می‌شود و اين روش سنتي كه به منظور تقويت خاك و حاصلخيزي بيشتر، رواج دارد، با استقبال بيشتري نيز مواجه شده تا جايي كه فروش كودهاي حيواني براي برخي افراد به منبعی براي كسب درآمد و معيشت اقتصادي تبديل شده است؛ بنابراین ضمن اینکه باغداری، زراعت و دامداری در این منطقه رونق دارد، تولید کمپوست (افزودن مقدار مشخصی از کودهای شیمیایی به کود حیوانی) و بسته بندی آن، سوددهی خوبی را به همراه خواهد داشت."),n.a.createElement("p",null,"با افزایش جمعیت، رشد تکنولوژی و افزایش سطح استانداردهای زندگی در جوامع امروزی، مصرف محصولات با محتوای بسته بندی های کوچک افزایش یافته است. جامعه مصرفی، نیازمند محصولاتی با سایزها و بسته بندی های متفاوت است و اهمیت صنعت بسته بندی به تدریج برای بنگاه‌ها و برنامه ریزان اقتصادی در حال آشکار شدن است. در بیشتر موارد شاهد هستیم که محصولات با بسته بندی های نامناسب در بازار عرضه شده یا به صورت فله ای و با قیمت های ناچیز به بازرگانان خارجی فروخته می‌شوند. این محصولات بعد از انتقال به خارج و انجام بسته بندی های مناسب بر روی آنان، با ارزش افزوده بالا و قیمت های چند برابری و به اسم و برند همان کشورها به بازار کشورهای دیگر عرضه می شوند و این کار ضمن برندسازی برای آن کشورها، تجارت آن ها را هم توسعه می‌دهد و این نشان دهنده رابطه مستقیم توسعه صنعت بسته بندی با رشد و توسعه تجاری کشورهاست. با توجه به نکات گفته شده و تولیدات میوه های آجیلی، حبوبات و غلات در این شهرستان، بسته بندی خشکبار، حبوبات و غلاتی مانند بلغور، توجیه اقتصادی دارد."),n.a.createElement("p",null,"با توجه به کشت زیتون در شهرستان، بسته بندی زیتون در صورت گسترش باغات آن، سود خوبی به همراه دارد. با بسته بندی محصولات می‌توان بر روی صادرات به سایر نقاط نیز کار کرد."),n.a.createElement("p",null,"فراوری پوست دام شامل استفاده از پوست گاو در صنعت چرم دوزی و پشم و کرک گوسفند و بز در صنعت ریسندگی، ضمن ایجاد درآمد مناسب برای سرمایه گذار، سود خوبی هم نصیب دامدار می‌شود."),n.a.createElement("p",null,"لازم به ذکراست مشاغل فوق با ایجاد بیش از یک یا دو واحد به حد اشباع می‌رسند و همچنین سرمایه گذار قبل از سرمایه گذاری باید بازار مصرف و میزان تولید مواد اولیه آنها در شهرستان را مورد بررسی دقیق قرار دهد.")),n.a.createElement(r.a,{title:"طیور",id:"birds",size:"h3"},n.a.createElement("p",null,"در سال‌های نه‌چندان دور، بیشتر نیاز شهرها به گوشت مرغ از طریق روستاها و پرورش‌دهندگان سنتی تأمین می‌شد؛ اما با رشد جمعیت و محدود بودن منابع روستایی، پرورش متراکم مرغ رونق یافت. پرورش جوجه‌های گوشتی تجاری، داخل سالن‌های مجهز به تجهیزات کنترل دما و تهویه انجام می‌گیرد، به همین علت در اکثر نقاط کشور می‌توان پرورش مرغ گوشتی را انجام داد تا علاوه بر سود اقتصادی که برای پرورش‌دهنده دارد، موجب کاهش نیاز کشور به واردات این محصول و ایجاد اشتغال برای جوانان ‌شود. از مزیت‌های اقتصادی این طرح می‌توان به بازار مطمئن‌تر گوشت مرغ، بازگشت سریع سرمایه، دسترسی نسبتاً آسان به مواد اولیه و خوراک، ضریب تبدیل غذایی کم و تولید گوشت بیشتر و وجود اتحادیه و تعاونی های متعدد اشاره کرد."," "),n.a.createElement("p",null,"پرورش مرغ تخمگذار علاوه براینکه به تامین منابع غذایی طبیعی و رونق تولید کمک می کند نقش به‌سزایی را در امر اشتغالزایی، بهبود وضع اقتصادی و خودکفایی در تولید محصول ارگانیک و طبیعی ایفا می‌کند. در صورتی که شخص بخواهد مشغول به پرورش مرغ بومی تخم‌گذار به صورت محدود شود نیاز به تکنولوژی پرهزینه‌ای ندارد و با بهره گیری از امکانات کم و موجود در روستا یا محل زندگی خود می تواند شرایط اجرای طرح را فراهم کند. پرورش مرغ تخم‌گذار حتی به صورت صنعتی و با تمام تجهیزات مدرن می‌تواند در صورت پیدا کردن بازار مناسب، سالیانه حدود 20 تا 30 درصد سودآوری دارد که رقم مناسبی است. همچنین پیدا کردن بازار فروش با حاشیه سود بالا با تبلیغات مناسب مخصوصا در فضای مجازی امکان پذیر است؛ بنابراین توسعه این مورد سوددهی خوبی خواهد داشت."),n.a.createElement("p",null,"پرورش بوقلمون نیز از جمله مزیت های خوب این شهرستان در حوزه طیور است که در حال حاضر به صورت سنتی و صنعتی انجام می‌شود و با توجه به سازگاری با اقلیم، قابل سرمایه گذاری است. در طي چند دهه گذشته توجه به بوقلمون و محصولات آن موجب شده تا پرورش آن به صورت صنعتي در ايران و جهان آغاز و طي چند سال اخير نيز به مرز قابل قبولی برسد تا جائيکه امروزه شاهد افزايش روز افزون مصرف گوشت و ساير فرآورده هاي آن در سراسر جهان هستيم. از آنجا که گوشت بوقلمون دارای خواص غذائي ويژه ای بوده و محدوديت مصرف خاصي نيز ندارد؛ مي تواند به عنوان جايگزين مناسبی براي گوشت قرمز علی الخصوص در بيمارانی که مصرف گوشت قرمز برای آنها با محدوديت هایی همراه است، مطرح گردد. از ديگر امتيازات گوشت بوقلمون وجود هر دو نوع گوشت سفيد و قرمز در يک لاشه است. گوشت سفيد 65 درصد و گوشت قرمز 35 درصد وزن يک لاشه 8 کيلوئي را تشکيل مي دهد. تولید بوقلمون گوشتی به دلیل به صرفه بودن تولید، در دسترس بودن نهاده‌های مصرفی بوقلمون، نیاز بازار داخلی و نیز امکان انجام صادرات به کشورهای منطقه حائز اهمیت است. بوقلمون تقریبا به آب وهواهای مختلف ایران سازگار است ولی دمای مناسب برای هر مرحله از پرورش متفاوت می‌باشد. در شروع دوره باید هوای محیط گرم باشد و به تدریج رو به خنک شدن ببرد."),n.a.createElement("p",null,"طبق بررسی‌های انجام شده، درحال حاضر پرورش بلدرچین و شتر مرغ به میزان کم انجام می‌شود و همچنان جای کار برای این موارد وجود دارد. بلدرچین پرنده ای با جثه کوچک، در برابر بسیاری از بیماری ها مقاوم است. پرورش بلدرچین به دلیل هزینه های جاری پایین، اشباع نبودن بازار فروش، کوتاه بودن دوره پرورش(40 روز) و عدم پرورش انحصاری توسط شرکت های بزرگ دارای مزیت است. محصولات این پرنده شامل گوشت، تخم بلدرچین و کود است. گوشت و تخم این پرنده دارای ارزش غذایی و دارویی و همچنین سرشار از پروتئین است. با توجه به موفقیت واحد های پرورش بلدرچین موجود، ایجاد چنین کسبب و کاری، بازده ای بالایی خواهد داشت."),n.a.createElement("p",null,"کسب و کار در زمینه پرورش شترمرغ پرواری در کشور ما قابلیت و استعداد پیشرفت فراوانی دارد؛ زیرا کشور ما چهار فصل بوده و شرایط طبیعی پرورش شترمرغ در بسیاری از مناطق مختلف ایران فراهم است. شترمرغ در گروه پرندگان طبقه بندی شده و گوشت آن جزء گوشت‌های قرمز محسوب می‌شود که نسبت به دیگر گوشت‌های قرمز کم چرب‌تر و سالم‌تر است به طوری که ۹۰ درصد آن قابلیت هضم داشته و بعد از گوشت ماهی در رتبه دوم قرار دارد. پوست این پرنده از بهترین نوع چرم بوده، مقاومتش3 برابر چرم گاو است، ظرافت خاصی دارد و در تولید کیف و کفش با قیمت بالا به کار می رود. علاوه بر آن، از پر و تخم شترمرغ، ناخن و قرنیه چشم آن برای مصارف صنعتی، دارویی، زینتی و نیز تولید نخ بخیه استفاده می شود. همچنین با استفاده از پوکه تخم این پرنده در زمینه صنایع دستی و خلق آثار هنری، می توان اشتغال زایی کرد.")),n.a.createElement(r.a,{title:"خدمات",id:"services",size:"h3"},n.a.createElement("p",null,"از آن جایی که ایران جزء کشورهایی با تاریخ کهن و غنی است، در هر گوشه از خاک این سرزمین پهناور بناها و جاذبه های تاریخی بسیار قرار گرفته است که هر کدام به نوبه ی خودشان می توانند سبب جذب گردشگران بسیار باشند. اما برای این که بتوانیم با استفاده از این جاذبه ها گردشگرانی را از داخل و خارج از کشور به سمت ایران جذب کنیم می بایست زیرساخت های لازم را برای آن فراهم آوریم. گرچه توجه به توسعه گرشگری در ایران با تاخیر در مقایسه با دیگر کشورها آغاز شد اما در طول دهه گذشته اقدام های موثری برای رونق این بخش مهم انجام شده تا سهم درآمدهای غیر نفتی این سرزمین پهناور را از روزنه آن چه که امروز به صنعت پاک معروف است، افزایش دهد. در شهرستان زنجان نیز فعالیت‌هایی انجام شده و همچنان می‌توان در زمینه احداث رستوران‌های بومی و اقامتگاه بومگردی کار کرد."),n.a.createElement("p",null,"رستوران‌های بومی، مکان‌هایی با فضای سنتی و متناسب با آداب ‌و ‌رسوم هر منطقه هستند که در آنها بیشتر به‌جای استفاده از میز و صندلی‌های مدرن از تخت یا سایر وسایل سنتی استفاده می‌شود و علاوه بر سرو غذاهای محلی، ترشیجات خانگی، نوشیدنی‌هایی مانند چای و قهوه نیز داده می‌شود. موقعیت شهرستان زنجان نیز با وجود تنوع آب هوایی و جغرافیایی ، در راه اندازی رستوران های سنتی و محلی همخوانی دارد. رستوران‌ها جزء مهم‌ترین نمادهای فرهنگی برای هر کشوری در جهان محسوب می‌شوند و می تواند در خانه های قدیمی موجود در هر منطقه اجرا شود. بسیاری از گردشگران و توریست های غذایی برای امتحان کردن غذاهای مختلف به سفر می روند و این مسئله برای مناطق مختلف ایران قابل استفاده و بهره برداری است؛ بنابراین ایجاد رستوران بومی نیز از کسب و کار های زود بازده است؛ زیرا هم تمایل مردم به سمت سرو غذا های محلی بیشتر شده و هم معاملات این کسب و کار به صورت نقدی انجام می‌شود."),n.a.createElement("p",null,"اقامتگاه های بوم گردی فرصت مناسبی است تا ضمن گردشگری در هر منطقه و بازدید از جاذبه های توریستی، حس ایرانی زیستن به سبک سنتی را تجربه کنند. همچنین طبخ غذاهای محلی در برخی اقامتگاه ها، مصداق دیگری از مهمان نوازی ایرانی است."),n.a.createElement("p",null,"در این شهرستان تعدادی کارگاه خیاطی وجود دارد که برخی از آنها به دلیل نوسانات بازار غیر فعال شده اند؛ اما اگر این کارگاه ها ساماندهی شوند و زنجیره فروش وجود داشته باشد، قابلیت توسعه را دارند و می توانند مشاغل زیادی درزمینه تولید پوشاک ایجاد کند ."," "),n.a.createElement("p",null,"طبق لیست مزیت سنجی تعمیر لوازم منزل نیز از کسب و کار هایی است که راه اندازی آن، سوددهی خوبی خواهد داشت.")),n.a.createElement(r.a,{title:"شیلات",id:"fisheries",size:"h3"},n.a.createElement("p",null,"شیلات از حوزه های شغلی سوددهی است که ظرفیت بسیارخوبی در کشور ما برای آن وجود دارد که اگر از این ظرفیت های شیلاتی استفاده شود ارزآوری بسیار خوبی برای کشور ایجاد خواهد شد. طبق بررسی‌های انجام شده در این شهرستان، پرورش ماهیان سردآبی انجام می‌شود. ماهیان سردآبی در محدوده دمایی 18-12 درجه سانتیگراد بهترین رشد را دارند. ماهی قزل آلا یکی از معروف ترین گونه های ماهیان سردآبی است. قزل آلا یکی از منابع خوب آهن است، آهن در خونسازی و همچنین در حفظ مقاومت بدن در برابر میکروبها نقش مهمی به عهده دارد. علاوه بر آن، آهن موجود در قزل آلا، جذب آهن منابع گیاهی را هم افزایش می دهد؛ بنابراین نقش مهمی در پیشگیری از کم خونی ناشی از فقر آهن به عهده دارد. نتایج مطالعات نشان می دهند امگا ۳ ماهی قزل آلا می تواند فشار خون و ضربان قلب را در بیماران صرع تنظیم کرده و از تعداد تشنج های آن به طور قابل توجهی بکاهد؛ بنابراین این نوع ماهی با توجه به قیمت و طعم مناسب طرفداران زیادی در بین مردم دارد؛ لذا راه اندازی پرورش آن، علاوه بر ایجاد اشتغال پایدار، سوددهی خوبی را خواهد داشت.")),n.a.createElement(r.a,{title:"باغداری",id:"gardening",size:"h3"},n.a.createElement("p",null,"بخش باغداری به دليل تأمين غذاي مورد نياز جامعه يكي از مهمترين حوزه هاي اقتصادي كشور محسوب مي شود. اين بخش از يك سو سهم بالايي در ارزش افزوده اقتصاد كشور دارد و از سويي ديگر سهم بالايي را در حوزه اشتغال به خود اختصاص داده است؛ لذا باغداری فرصت‌های بسیار گسترده‌ای برای پیشرفت فراهم می‌آورد. به همین دلیل توسعه فضای باغداری یا فعالیت‌های کشاورزی در کشور به شدت استقبال می‌شود."),n.a.createElement("p",null,"گردو، درختی چندساله و مقاوم است که پس از رشد کامل، هر درخت تا 18 کیلوگرم تولید محصول خواهد داشت. محصول نهایی قیمت بالایی دارد که هم در داخل کشور طرفداران زیادی داشته و هم قابلیت بالای صادرات دارد. همچنین در صورت احداث باغ گردو، تا سال های سال نیاز به سرمایه گذاری و کشت دوباره وجود ندارد؛ زیرا درخت گردو دارای عمر طولانی است و تا ده ها سال قابلیت برداشت دارد. میوه گردو هم جزء معدود میوه هایی است که هیچ گونه نیازی به هزینه انبار در سردخانه ندارد و به دلیل ماندگاری طولانی، می تواند چند ماه و حتی چند سال مورد مصرف قرار گیرد. در ضمن چوب درخت گردو هم برای منبت کاری، خراطی و کنده کاری، ساخت مجسمه و اشیا دکوری، ساخت آلات موسیقی و همچنین در دکوراسیون داخلی منزل و ساخت روکش های چوب بسیار پرکاربرد است. این چوب نیمه سنگین و نیمه سخت بوده و مقاومت مناسبی در برابر فشار، خمش، رطوبت و کشش دارد. با توجه به موارد گفته شده، گسترش باغات گردو از مزیت هایی است که ارزش افزوده زیادی را به دنبال دارد."),n.a.createElement("p",null,"با توجه به موضوع مهم کم‌آبی در کشورمان، کاشت درختان دیم و همچنین محصولات دیم بسیار پراهمیت و موردتوجه است. از درخت بادام به‌عنوان یک درخت مقاوم در برابر خشکی یاد می‌شود و در شرایطی که بی‌آبی وجود دارد، درخت بادام میوه خوبی می‌دهد و همچنین ازلحاظ شرایط کاشت در زمین‌های آهکی هم تا حد زیادی مقاومت دارد. با توجه به موارد گفته شده و ارزش افزوده خوبی که کاشت این محصول دارد، ایجاد باغات بادام، سوددهی خوبی را خواهد داشت."),n.a.createElement("p",null,"زیتون به موجب مقاومت به کم‌آبی، سازگاری با خاک‌های فقیر و کم بازده و تولید محصول ارزشمند، از نظر اقتصادی حائز اهمیت می‌باشد و به محصول ثروتمند خاک‌های فقیر مشهور است. زیتون به دلیل استقامت و طول عمر و سازش با محیط‌های مختلف به دیگر درختان برتری دارد تا جایی که علاوه بر کشت در باغات و اراضی کشاورزی در توسعه فضای سبز شهری نیز از این درخت استفاده می‌شود. زیتون علاوه بر خواص درمانی، دارای مزیت‌های اقتصادی ارزشمندی از جمله تولید روغن، کنسرو زیتون، کمک به معیشت کشاورزان با زیر کشت بردن اراضی کم بازده فقیر و شور، اشتغال‌زایی (جذب قابل‌ملاحظه‌ای از جمعیت جویای کار) و جلوگیری از مهاجرت است. با توجه به اینکه در حال حاضر کاشت درخت زیتون در این شهرستان انجام می‌شود و با اقلیم این منطقه سازگاری دارد، توسعه باغات زیتون سودآوری خوبی را به همراه خواهد داشت."),n.a.createElement("p",null,"میزان مصرف قارچ در سال های اخیر در ایران رشد خوبی داشته و سرانه مصرف بالا رفته است. مشتری داشتن این محصول از یک سو و غنی از پروتئین و ویتامین بودن آن از سوی دیگر، باعث شده که جایگزین مناسبی برای گوشت باشد. در برخی از شهرها و روستاها به دلیل عدم آشنایی با فواید قارچ، میزان مصرف این محصول پایین است که می توان با تبلیغ و معرفی این محصول، میزان مصرف را بالاتر برد. همچنین پرورش قارچ از شغل هایی است که می توان با سرمایه اولیه کم و محیط کوچک شروع کرد. یک دوره کامل پرورش قارچ 60 روز طول می کشد که قابلیت کاهش به 35 روز را دارد؛ بنابراین توسعه پرورش قارچ نیز از کسب و کار های خوب این شهرستان است که در صورت بازاریابی، سوددهی خوبی خواهد داشت."),n.a.createElement("p",null,"کار کردن در حوزه کاشت گل محمدی، از کسب و کارهایی است که در صورت راه اندازی، مثمرثمر خواهد بود؛ زیرا گل محمدی، گیاهی مقاوم به کم آبی، آفات و امراض است، که علاوه بر خواص درمانی، دارای مزیت‌های اقتصادی فراوانی مانند ارزش صادراتی، جذب گردشگر و اشتغال زایی است. غنچه خشک، مایع گلاب، روغن و اسانس، فرآورده‌های برگرفته از گل محمدی است که علاوه بر عرضه در بازارهای داخلی، قابلیت صادرات به کلیه کشورهای جهان را دارا هستند."),n.a.createElement("p",null,"با توجه به اینکه بخش عمده ای از باغات این شهرستان، محصولات خوب و مرغوبی مانند سیب، زردآلو، انگور، هلو و گلابی تولید می‌کنند، گسترش چنین باغاتی در صورت مدیریت صحیح منابع آبی، قابلیت توسعه دارند؛ اما به دلیل اسقبال کمتر در اولویت های آخر قرار می‌گیرند.")),n.a.createElement(r.a,{title:"زراعت",id:"agriculture",size:"h3"},n.a.createElement("p",null,"بخش زراعت در روند رشد و توسعه اقتصادی کشورهای مختلف نقش‎های متعددی بر عهده دارد که از جمله مهم‎ترین آن‎ها می‎توان تامین مواد خام و اولیه مورد نیاز صنایع بالادستی، تامین نهاده‎هایی مانند نیروی کار، مصرف تولیدات سایر بخش‏های صنعتی از قبیل کودها و سموم شیمیایی و ماشین‌آلات کشاورزی، ارزآوری برای کشور و تامین مالی سایر بخش‎های زیربنایی را نام برد."),n.a.createElement("p",null,"با توجه به بررسی های انجام شده، کشت زعفران با اقلیم این شهرستان سازگار است و توسعه آن ارزش افزوده زیادی را به همراه دارد. مقاومت زعفران در برابر سرما زیاد بوده و به آب کمی نیاز دارد. زعفران به علت داشتن رنگ زیبا، عطر و طعم بسیار مطبوع، در صنایع غذایی کاربرد دارد. دمنوش زعفران آرام بخش بوده و استرس و نگرانی را برطرف می کند. کسب و کار کشت زعفران به علت خاصیت ضد سرطانی این گیاه اهمیت دو چندان پیدا می کند. ایران اولین کشور تولیدکننده زعفران بوده که قریب به 90 درصد از زعفران جهان را تأمین می کند. مصرف زعفران در ایران بالا است و نه تنها می تواند نیاز منطقه زیرکشت را تامین کند بلکه درسراسر ایران نیز مشتری دارد."),n.a.createElement("p",null,"در میان محصولات کشاورزی، کاشت گندم نسبت به بقیه کم دردسرتر است.گندم در بازه زمانی حدودا هشت ماهه به ثمر نشسته و به مرحله سود دهی می رسد. خرید تضمینی گندم همه ساله توسط دولت انجام می گیرد. البته خود کشاورز هم می تواند گندم را بفروشد و درآمد خوبی کسب کند. در ضمن این محصول مورد حمایت دولت هم واقع می‌شود و کود شیمیایی و بذر به شکل یارانه ای در اختیار کشاورزان قرار می گیرد؛ بنابراین گسترش تولید گندم ارزش افزوده زیادی را به دنبال دارد."),n.a.createElement("p",null,"جو بعد از گندم بیشترین سطح زیر کشت را در ایران به خود اختصاص داده است. در ایران اراضی وسیعی وجود دارد که بدلیل شوری و عمق کم خاک و شور بودن آب زراعی صرفا برای تولید جو مناسب است؛ ولی متاسفانه با توجه به تنوع آب و هوایی، ارقام مناسب هرمنطقه معرفی نشده است. جو از دیر باز در سبد تغذیه دام جایگاه ویژه داشته و به صورت دانه و علوفه سبز مورد استفاده قرار می‌گیرد. جوشانده جو داروي خوبي براي مبتلايان به تب و كم خوني و سوء هاضمه است و پایین آورنده کلسترول می‌باشد. از جو برای تهیه نان و پخت سوپ استفاده می شود. این گیاه در تهیه غذای کودک نیز کاربرد دارد. لذا افزایش تولید این مورد نیز زمینه های لازم برای رشد و پیشرفت را دارد؛ زیرا با اقلیم این شهرستان سازگار است و هم اکنون نیز به صورت انبوه کشت می‌شود."),n.a.createElement("p",null,"با توجه به اهمیت تولید حبوبات در اقتصاد کشاورزی و مستعد بودن شرایط اقلیمی از نظر نوع خاک که سبک و غیر اسیدی است و آب و هوای معتدل این شهرستان، توسعه کشت حبوباتی مانند عدس و لوبیا، از جمله مواردی است که ارزش افزوده زیادی را به دنبال دارد."),n.a.createElement("p",null,"سیب زمینی بعد از گندم، برنج و ذرت، بیشترین سهم را در میزان تولید محصولات غذایی داراست و نقش مهمی در تغذیه و سبد غذایي جمعیت جهان دارد. در کشـورهاي درحال توسعه، اهمیت غذایی سیب زمینی به مراتب بیشتر است و در ایران نیز بعد از گندم، رتبه دوم را به خود اختصاص داده است. سیب زمینی بیشتر از هر نوعی از غلات، مواد مغذی مورد نیاز انسان را در زمان كمتر و با زمین كمتر و در شرایط آب و هوائی دشوار تر می تواند تامین كند. بـرای تولید پایدار محصول سیب زمینی، آب مهم ترین عامل است که با توجه به مشکل کم آبی در کشور، تلاش در جهت استفاده بهینه از آبهاي استحصالی و کاهش برداشت از منابع آب زیرزمیني، اجتناب ناپذیر است. با در نظر گرفتن اهمیتی که این محصول دارد، توسعه کشت آن مزیت دارد.")),n.a.createElement(r.a,{title:"صنعت",id:"industry",size:"h3"},n.a.createElement("p",null,"پوشاک پردرآمدترین صنعت موجود در کشور ماست. امروزه افراد زیادی در کشور ما در صنعت پوشاک یعنی در زمینه ی طراحی، دوخت و تولیدی لباس فعالیت می کنند. همچنین با وجود شبکه های اجتماعی و رشد روزافزون این شبکه ها و نفوذ آن در میان مردم، تعداد مزون هایی که در زمینه‌ی پوشاک فعالیت می کنند به طرز چشمگیری افزایش یافته است. همچنین در مناسبت هایی مانند عید نوروز و شروع مدارس، افراد زیادی اقدام به خرید لباس می کنند و همین امر باعث رونق بیش از پیش این صنعت شده‌است؛ بنابراین ایجاد و گسترش تولیدی های پوشاک نیز از کسب و کار هایی است که قابلیت سرمایه گذاری دارد."),n.a.createElement("p",null,"یکی از امتیازات شهرستان زنجان، وجود معادن سرب و روی است. استخراج از این موارد صورت می‌گیرد؛ اما متاسفانه فراوری نمی‌شوند و به صورت شمش به سایر مناطق صادر می‌شود. البته درکنار این موارد مس هم تولیدات زیادی دارد که پسماندهای محدود آن هم در صنعت جواهرسازی استفاده می شود. امروزه سرب کاربردهای فراوانی در بخش‌های مختلف صنایع دارد. مهم‌ترین این کاربردها شامل حمل و نقل (انواع مختلف باتری‌ها و لحیم‌های مورد استفاده در صنایع حمل و نقل)، ساختمان (مواد ساختمانی مانند صفحات و لوله‌ها، لحیم‌ها، ترکیبات مورد استفاده برای عایق صوتی و حفاظت در برابر پرتوها و صنعت رنگ) و کالاهای الکتریکی (باتری‌ها و انواع کابل‌های الکتریکی) می‌شود. یکی از مصارف بسیار مهم و رو به رشد سرب در سال‌های اخیر باتری‌های اسید ـ سرب است."," "),n.a.createElement("p",null,"روي، پس از فولاد، آلومينيوم و مس در رده‌ی چهارم مصرف قرار دارد. روي در ساخت سيمان، دندانسازي، ساخت كبريت، كف‌سازي، ظروف سفالين، لوازم لاستيكي، اتومبيل سازي، لوازم آشپزخانه، روکش فولاد (گالوانيزه کردن)، تهيه آلياژهاي برنز و برنج، لحيم کاري، قوطي هاي خمير دندان، چسب فلز ماشين‌تحرير، نقره آلماني و... است. از اکسيد و سولفور روي به عنوان ماده رنگي سفيد در رنگ سازي و تهيه پلاستيک، از سولفات روي در رنگرزي و ساخت چسب و از کلرور روي در لحيم کاري و جلوگيري از فساد چوب استفاده مي شود. پائين بودن نقطه ذوب اين فلز كه شكل‌پذيري در ريخته‌گري را آسان مي‌كند. با توجه به موارد گفته شده فراوری روی و سرب از جمله گزینه های شغلی مناسبی است که در صورت سرمایه گذاری، توجیه اقتصادی دارد."),n.a.createElement("p",null,"جوراب بافی، کارخانه ریسندگی و تولید نخ و پارچه، صنایع شیر پاستوریزه و تولید دستمال کاغذی از صنایع فعال این شهرستان هستند."," "))))}}}]);
