(window.webpackJsonp=window.webpackJsonp||[]).push([[285],{vWAT:function(e,t,l){"use strict";l.r(t);var a=l("q1tI"),n=l.n(a),r=l("cvUp"),c=l("CKC9"),i=l("gEOm");t.default=function(){var e=c["IR-17"].cities["IR-1702"];return n.a.createElement("div",{className:"w-full"},n.a.createElement(i.a,{menu:{introduction:"آشنایی با شهرستان",analyze:"تحلیل شهرستان",jobs:{value:"تعیین کسب‌وکارهای مزیت‌دار شهرستان",children:{gardening:"باغداری",agriculture:"زراعت",livestock:"دامداری",food_industry:"صنایع غذایی",conversion_complementary_industries:"صنایع تبدیلی و تکمیلی",birds:"طیور",services:"خدمات",handicrafts:"صنایع دستی",industry:"صنعت",fisheries:"شیلات"}}},city:e},n.a.createElement(r.a,{id:"introduction",title:"آشنایی با شهرستان"},n.a.createElement("p",null,"شهرستان ارسنجان از شهرستان‌های استان فارس در جنوب ایران و مرکز آن، شهر ارسنجان است. جمعیت این شهرستان بر طبق سرشماری سال ۱۳۹۵، برابر با 42 هزار و 725 نفر بوده ‌است. این شهرستان از غرب به شهرستان‌های مرودشت و پاسارگاد، از شرق به شهرستان‌های نی ریز و بوانات، از شمال به شهرستان‌های پاسارگاد و بوانات و از جنوب به شهرستان خرامه محدود است. ارسنجان در واژگان به معني محفل و جايگاه نشيمن است و در حقيقت به دليل داشتن آب و هوایی مناسب، آن را ارسنجان ناميده اند. بيشتر محصولات زراعي و باغي در خاك و آب و هواي این شهرستان قابلیت رشد دارند. گرماي ارسنجان در تابستان زياد نيست و در زمستان چندان سرد نمي شود. حداكثر درجه گرماي هوا در اوج گرماي تابستان بين 37 تا 40 درجه و حداقل سرماي آن از 5 تا صفر درجه متغیر می باشد. شکل زیر موقعیت شهرستان ارسنجان را نشان می دهد.")),n.a.createElement(r.a,{id:"analyze",title:"تحلیل شهرستان"},n.a.createElement("p",null,"مزيت‌سنجي و اولويت‌بندي كسب ‌و كارهاي موجود در هر شهرستان بر مبناي يک روش تحقيق انجام مي‌شود. در اين روش تحقيق، يک شناسنامه براي هر شهرستان و يک شناسنامه براي هر كسب ‌و كار تدوين مي‌شود. اين شناسنامه‌ها به عنوان ورودي به نرم‌افزار داده ‌شده و خروجي نرم‌افزار هم‌پوشاني اين دو شناسنامه و تعيين مشاغل مزيت‌دار شهرستان است. اين اولويت‌بندي‌ها توسط كارشناسان و افراد خبره هر شهرستان بازبيني و اصلاح مي‌شوند. يكي از منابع تحليل همين ليست مزيت‌سنجي اصلاح ‌شده ‌است. يكي ديگر از منابع تحليل، مصاحبه با كارشناسان و متخصصين حوزه اشتغال هر شهرستان است. در هر شهرستان با حداقل 3 نفر از اين افراد مصاحبه انجام مي‌شود. اين مصاحبه‌ها پياده‌سازي و ارزيابي مي‌گردد. در صورت وجود تناقض و ابهام بين اين 3 مصاحبه و ليست مزيت‌سنجي، مصاحبه‌هاي تكميلي و حتي مصاحبه بيشتر هم صورت مي‌گيرد تا تيم تحليل به نتيجه نهايي مطلوب دست ‌يابد؛ بنابراين خروجي هر شهرستان از تجميع مصاحبه‌ها و ليست مزيت‌سنجي حاصل مي‌شود. در ادامه تحليل شهرستان ارسنجان ارائه ‌شده ‌است.")),n.a.createElement(r.a,{id:"jobs",title:"تعیین کسب‌وکارهای مزیت‌دار شهرستان"},n.a.createElement("p",null,"همانطوريكه در قسمت مقدمه اشاره‌ شده مبناي تحليل، مصاحبه‌ها و ليست مزيت‌سنجي است. از بررسي اين دو منبع رسته‌هاي رايج و اولويت‌دار شهرستان تعيين شده‌ است. مبناي اين اولويت‌بندي، فراواني و ميزان اشاره افراد كارشناس به كسب ‌و كارهاي رايج در هر شهرستان و اولويتي كه اين مشاغل در ليست مزيت‌سنجي دارند، است. در هر رسته كسب ‌و كارهاي رايج به ترتيب اولويت مشخص شده‌است. در تحليل به مشاغل موجود، مشاغل قابل توسعه و مشاغلي كه هنوز در شهرستان راه‌اندازي نشده و پتانسيل ايجاد آن‌ها در شهرستان وجود دارد، اشاره شده ‌است. مزيت‌هاي شغلي به ترتيب اولويت در ادامه آورده شده ‌است.")),n.a.createElement(r.a,{id:"gardening",title:"باغداری",size:"h3"},n.a.createElement("p",null,"باغداری یکی از حوزه‌های رایج در شهرستان ارسنجان است. تولید اشتغال توسط رسته باغداری بسیار زیاد می‌باشد و به اقتصاد شهرستان و فضای کسب و کار رونق می‌بخشد. کشاورزی و از جمله باغبانی در توسعه ملی و افزایش رفاه عمومی، فقرزدایی، تنوع بخشیدن و پایدار کردن الگوی توسعه شهری، توزیع درآمد و عدالت اجتماعی موثر است. در یک نگاه کلی می توان گفت نقش کشاورزی و به خصوص باغداری در کاهش فقر بسیار چشمگیر بوده و در برقراری الگوی صحیح توسعه شهری مؤثر است و در صورت بی توجهی به این بخش، توسعه شهرنشینی، الگوی متمرکز و ناسالمی به خود می‌گیرد که پیدایش شهرهای بزرگ با هزینه زندگی بسیار بالا مثال بارز آن است."),n.a.createElement("p",null,"در این شهرستان تولید صیفی جاتی نظیر گوجه فرنگی به صورت آزاد و گلخانه ای و در اولویت های پایین تر، خیار گلخانه ای و فلفل دلمه ای گلخانه ای انجام می شود. در شهرستان ارسنجان بیش از 800 هکتار گوجه فرنگی کاشته شده و از این سطح حدود 50 هزار تن گوجه برداشت می شود که نشان از سازگاری بالای گوجه فرنگی در این شهرستان است و همچنان ظرفیت افزایش تولید به دلیل بازار تازه خوری زیاد و امکان فرآوری آن وجود دارد. به دلیل مصرف بالای آب در روش فضای آزاد و کمبود آب در شهرستان، گلخانه های تولید صیفی جات می توانند راه حلی برای تولید صیفی جات بیشتر باشند. در واقع گلخانه محیطی است که به راحتی می توان در داخل آن شرایط محیطی را تحت کنترل درآورد و به سمت رشد مطلوب گیاه سوق داد. گلخانه‌ها که از نظر اندازه متغیرند گیاهان را از سرمای بیش از حد زمستان یا گرمای بیش از حد تابستان حفظ می‌کنند؛ در نتیجه تولید صیفی جات به صورت گلخانه ای برای شهرستان مزیت دارد."," "),n.a.createElement("p",null,"حدود 1500 هکتار باغ انار در شهرستان ارسنجان وجود دارد و درنتیجه شغل بسیاری از افراد شهرستان را باغبانی تشکیل می دهد. نهال انار حداقل سه سال نیاز به رشد دارد و از سال چهارم به بعد شروع به باردهی می کند. درخت انار بیش از 40 سال عمر مفید داشته و در مقابل کم آبی مقاوم است؛ اما میزان و نوع آب در کیفیت و کمیت میوه آن تاثیر مهمی دارد. مصرف انار در سال های اخیر افزایش یافته است. خواص این میوه و ابعاد مختلف محصولی آن از دلایل افزایش مصرف است. از انار در تولید داروهای تقویتی و صنایع آرایشی و بهداشتی استفاده می شود. مصرف رب و آب انار نیز از گذشته رواج داشته و در سال های اخیر به اشکال متنوع و بهتری انجام می شود. کاشت انار فعالیتی درآمدزا است که با ارزیابی نیاز بازار، به روز رسانی فرآیند بسته بندی و جابجایی و... می توان درآمد آن را افزایش داد."),n.a.createElement("p",null,"میوه های هسته داری همچون هلو و بادام و درخت مقاوم به خشکی و شوری همچون پسته از دیگر محصولات باغی شهرستان ارسنجان هستند. میوه های نام برده شده علاوه بر اینکه به عنوان تازه خوری با استقبال خوبی مواجه هستند، در صنایع غذایی مختلف نیز کاربرد زیادی دارند؛ بنابراین تولید آن ها از سوددهی خوبی برخوردار است و سرمایه گذاری در این زمینه می تواند توجیه اقتصادی مناسبی داشته باشد."),n.a.createElement("p",null,"گل محمدی از دیگر محصولات باغی شهرستان است که تولید انبوهی ندارد، اما پتانسیل بسیار خوبی برای توسعه کاشت آن‌ وجود دارد. گل محمدی از قدیمی ترین و مهم ترین رزهاست و در بیشتر نقاط ایران به منظور تولید عطر و گلاب و بـه عنوان گیاه تزیینی مـورد توجه بسیار قرار داشته و کاشته می شود. این گل، گیاهی مقاوم به کم آبی، آفات و امراض است، که علاوه بر خواص درمانی، دارای مزیت‌های اقتصادی فراوانی مانند ارزش صادراتی، جذب گردشگر و اشتغال زایی است."),n.a.createElement("p",null,"خرمالو درختی است که بیشتر در مناطق گرمسیر کشت می شود که این شرایط در شهرستان ارسنجان مهیا می باشد و کشت و کار آن انجام می شود. بازارپسندی میوه های این درخت و امکان فرآوری آن در کنار مهیا بودن شرایط محیطی باعث شده است تا امکان توسعه باغات خرمالو در شهرستان ارسنجان وجود داشته باشد. میوه این گیاه خوراکی می باشد و رنگ آن از نارنجی و زرد روشن تا نارنجی و قرمز تیره متفاوت است. خرمالو دارای مقادیر قابل توجهی ویتامین‌های ب3 ،ب2، ب۱ و C است، در ضمن این میوه مواد معدنی ضروری برای بدن مانند کلسیم، گوگرد، آهن، فسفر و پتاسیم و مقداری نیز سلولز دارد.")),n.a.createElement(r.a,{id:"agriculture",title:"زراعت",size:"h3"},n.a.createElement("p",null,"بخش زراعت در روند رشد و توسعه اقتصادی نقش‎های متعددی بر عهده دارد که از جمله مهم‎ترین آن‎ها می‎توان تامین مواد خام و اولیه مورد نیاز صنایع بالادستی، تامین نهاده‎هایی مانند نیروی کار، مصرف تولیدات سایر بخش‏های صنعتی از قبیل کودها و سموم شیمیایی و ماشین‌آلات کشاورزی، ارزآوری و تامین مالی سایر بخش‎های زیربنایی را نام برد. در شهرستان ارسنجان بیشتر آب مورد نیاز در زمینه کشاورزی را از طریق چشمه ها و آب رودخانه به دست می آورند. با توجه به نظر کارشناسان، کاشت گندم، جو و یونجه و در اولویت های پایین تر کاشت کلزا و چغندر قند در شهرستان در حوزه زراعت دارای مزیت می باشد."),n.a.createElement("p",null,"درصد زیادی از زراعت گندم و جو در شهرستان به صورت دیم انجام می‌شود و درصد بسیار کمی از آن نیز به صورت آبی می باشد و در حالت آبی، بازدهی بیشتر است. در میان محصولات کشاورزی، کاشت گندم نسبت به بقیه کم دردسرتر است. گندم در بازه زمانی حدودا هشت ماهه به ثمر نشسته و به مرحله سود دهی می رسد و در دو نوع بهاره و پاییزه کشت می شود که محصول دهی پاییزه آن بیشتر است. علاوه بر خود گندم، کاه آن نیز مشتری خاص خودش را دارد. بعد از برداشت محصول، زمین آن هم چراگاه گوسفندان می‌شود. همچنین مورد حمایت های دولت واقع شده و کود شیمیایی و بذر نیز به شکل یارانه ای در اختیار کشاورزان قرار می گیرد؛ بنابراین توسعه این کشت توجیه اقتصادی خوبی در شهرستان ارسنجان دارد."),n.a.createElement("p",null,"جو از دیرباز در سبد تغذیه دام جایگاه ویژه داشته و به صورت دانه و علوفه سبز مورد استفاده قرار می‌گیرد. از جو برای تهیه نان و پخت سوپ استفاده می شود و در تهیه غذای کودک نیز کاربرد دارد. جوشانده جو داروي خوبي براي مبتلايان به تب و كم خوني و سوء هاضمه بوده و پایین آورنده کلسترول خون است. کاشت این گیاه زراعی در کنار گندم، در شهرستان ارسنجان رایج بوده و با توجه به سازگاری خوب برخی ارقام جو برای شرایط شهرستان، امکان استفاده از اراضی دیم وجود دارد. بازار فروش مطمئن، کاشت، داشت و برداشت مکانیزه، از عواملی است که باعث ایجاد مزیت برای کاشت این گیاه شده و با توجه به تولید پایین جو در کشور نسبت به میزان مورد نیاز، توسعه کاشت این محصول پیشنهاد می شود."),n.a.createElement("p",null,"شهرستان ارسنجان در زمینه تولید علوفه های دامی مانند یونجه وضعیت خوبی دارد. این محصول را می توان به میزان بیشتری در شهرستان تولید و کسب سود کرد. در میان گیاهان علوفه ای، یونجه به دلیل ارزش غذایی بالا و عملکرد خوب آن از اهمیت خاصی برخوردار است. همچنین یونجه به علت وجود باکتریهای ریزوبیوم در ریشه های خود قادر به تثبیت ازت هوا بوده و به این طریق باعث باروری خاک می شود. از طرفی دیگر یونجه گیاهی چندساله است و تا چند سال می توان آن را برداشت کرد؛ بنابراین توسعه کشت یونجه توجیه اقتصادی داشته و امکان تامین بخشی از علوفه مورد نیاز دام، فراهم می‌شود که علاوه بر ایجاد اشتغال مولد و حصول درآمد مناسب برای زارعین، در استفاده بهینه از ظرفیت های پرورش دام نیز موثر خواهد بود و نیاز شهرستان برای وارد کردن نهاده های مورد نیاز دام از سایر مناطق را برطرف خواهد کرد. هر مقدار تولید یونجه در شهرستان ارسنجان به دلیل وجود دامداران زیاد در شهرستان فورا به فروش می رسد و اکثر مواقع یونجه را پس از برداشت در محل خریداری می کنند؛ از این رو کاشت این محصول با توجه به سازگاری خوبی که با شرایط جغرافیایی شهرستان دارد، از مزیت و ظرفیت خوبی برخوردار است."),n.a.createElement("p",null,"در اولویت های پایین تر، کاشت گیاه کلزا در شهرستان ارسنجان قابل پیشنهاد است. کشت کلزا باعث از بین رفتن چرخه زندگی آفات، بیماری ها و علف های هرز گردیده و با کاهش جمعیت آن‌ها، موجب صرفه جویی در مصرف سموم و کاهش آلودگی های زیست محیطی می گردد. با توجه به آنکه زمان کشت کلزا در فصل پاییز است، نیاز گیاه به آب کم می‌باشد و با استفاده از نزولات جوی می توان به کشت آن در مناطقی از شهرستان که در فصل تابستان آبی در آنجا نیست، اقدام نمود. کلزا با تولید مقادیر قابل توجهی علوفه می تواند در تغذیه دام های منطقه نقش موثری داشته باشد. همچنین برگرداندن بقایای کلزا به خاک باعث افزایش حاصلخیزی خاک و بهبود ساختمان خاک خواهد شد."),n.a.createElement("p",null,"بخشی از زمین های شهرستان ارسنجان، زیرکشت چغندر قند قرار دارد. کشت چغندرقند علاوه بر این که از نظر اقتصادی بسیار سودآور است، می تواند در اصلاح خاک و کنترل علف های هرز نیز مؤثر بوده و از این رو به راحتی می توان این گیاه را در تناوب با گیاهان دیگر قرار داد. به دلیل سازگاری زیاد این گیاه با شرایط مختلف آب و هوایی و خاکی شهرستان، در اکثر نقاط می توان آن را کشت کرد. این گیاه با ریشه های عمیق خود نفوذپذیری خاک را افزایش داده و خاک مناسبی را برای کشت های بعدی آماده می کند. با توجه به مزیت های مطرح شده، توسعه کاشت این گیاه در شهرستان پیشنهاد می شود.")),n.a.createElement(r.a,{id:"livestock",title:"دامداری",size:"h3"},n.a.createElement("p",null,"دامداری یکی از مشاغل رایج در شهرستان ارسنجان است. دامداری با یکی از مهم ترین نیازهای انسان، یعنی نیاز به منابع غذایی و خصوصا منابع پروتئینی مرتبط می‌باشد، که توجه به آن امری ضروری است. فعالیت در این حوزه شغلی و افزایش بهره وری در آن، می تواند از راهکارهای توسعه اقتصادی و عاملی برای توسعه صنایع غذایی، تبدیلی و تکمیلی محسوب شود. درصد بالایی از مردم این شهرستان به دامداری مشغول هستند. دامداری در این شهرستان عمدتا به هر دو صورت سنتی و صنعتی انجام می شود."," "),n.a.createElement("p",null,"در شهرستان ارسنجان، پرورش گوسفند بومی نسبت به سایر دام ها، بیشتر مورد توجه دامداران قرار گرفته است؛ زیرا توان گوسفند بومی برای تحمل اقلیم های متفاوت شهرستان بیشتر است و جیره غذایی کمتر و در نتیجه هزینه کمتری نیز دارد. همچنین تمامی فرآورده های دامی مانند گوشت، شیر، ماست، کره و حتی پشم و پوست دام پرکاربرد هستند و فروش این فرآورده ها در تمامی روزهای سال انجام می‌شود. قیمت گوشت گوسفند بالا است و به علاوه اجزای گوسفند مانند دل، قلوه، جگر، سیرابی، کله پاچه، خوش گوشت و… نیز قابل فروش هستند. خرید دام برای قربانی کردن در ایام مذهبی مانند عید قربان و… موجب رونق این کسب و کار می‌شود؛ بنابراین گسترش پرورش گوسفند سوددهی خوبی را در شهرستان به همراه خواهد داشت."),n.a.createElement("p",null,"در شهرستان ارسنجان، گاوهای نژاد بومی، هلشتاین و سمینتال نیز پرورش داده می شوند که نژاد هلشتاین و سمینتال عملکرد بیشتری دارند و نژاد بومی مقاومتر و به شرایط محیطی حساسیت خیلی کمی دارد. پرورش گاو و پرواربندی گوساله در راستای تامین گوشت و شیر می تواند مزیت موفقی باشد. پرورش گاو به صورت داشتی ضمن کمک به تولید گوساله برای پرواربندی، نقش مهمی در تولید لبنیات دارد. بخش عظیمی از گوشت قرمز شهرستان توسط گاوهای پروار شده تأمین می‌شود؛ بنابراین این بخش از دامپروری و دامداری دارای ظرفیت زیادی برای توسعه و پیشرفت دارد. مزایای ورود به عرصه پرواربندی گوساله در شهرستان دارای جنبه های مختلف است. از یک طرف، از پوست گاو گوشتی در چرم‌سازی استفاده می‌شود؛ از طرف دیگر از گوشت آن جهت استفاده در تغذیه انسان بهره برداری می شود. همچنین برای جلوگیری از واردات گوشت گوساله از کشورهای دیگر، تولید آن اهمیت دارد. پرواربندی گوساله به هر دو صورت صنعتی و سنتی در شهرستان انجام می گیرد. در پرواربندی علمی و صنعتی از دام‌های نر جوان نژادهای گوشتی استفاده می‌شود. استفاده از ظرفیت ژنتیکی دام‌های گوشتی و کاهش کشتار دام‌های شیری و مولد از دیگر محاسن پرواربندی با روش‌های نوین است. پرواربندی گوساله با روش‌های علمی و نوین سبب کاهش دوره پرواربندی، کاهش هزینه تولید گوشت و جلوگیری از تخریب مراتع می‌شود که همین موضوع مزیت خوبی برای پرورش گاو در شهرستان ایجاد کرده است."),n.a.createElement("p",null,"در اولویت های پایین تر، پرورش بز در شهرستان ارسنجان دارای مزیت می باشد. بزهای پرورشی در این شهرستان بیشتر از نوع بومی هستند و قابلیت توسعه پرورش آن‌ها در شهرستان وجود دارد. با توجه به وجود فصل گرم و تمایل بزهای بومی برای چریدن در مناطق مختلف شهرستان، به نظر می رسد، پرورش بز بومی در شهرستان از امتیاز خوبی نسبت به بسیاری از دام های دیگر داشته باشد. به طورکلی بز به‌عنوان یک حیوان اهلی نشخوارکننده جهت تولید شیر و گوشت در مناطق مختلف شهرستان پرورش داده می‌شود. جثه کوچک، کم توقع بودن و مقاومت در برابر بیماری‌ها و شرایط سخت محیطی، باعث شده تا بز به‌عنوان حیوانی محبوب در بین دامداران شهرستان ارسنجان نیز تلقی شود."),n.a.createElement("p",null,"با توجه به آب هوای شهرستان و منابع طبیعی فراوان، فعالیت در حوزه زنبورداری برای شهرستان مزیت خوبی دارد. این مسئله باعث شده ارزش اقتصادی پرورش زنبور عسل بسیار بیشتر از ارزش ریالی حاصل از تولید عسل باشد. در طی فرآیند پرورش و نگهداری از زنبورعسل، با ازدیاد کندوها می توان به فروش کندو نیز فکر کرد و به سود خوبی دست یافت. در ضمن محصول مزرعه پرورش زنبور عسل صرفاً کندو و عسل نیست، بلکه موم، زهر و ژل آن نیز قابلیت فروش دارد. در نتیجه افزایش این فعالیت در شهرستان دارای توجیه مناسبی است و می توان این فعالیت را به میزان بیشتری انجام داد.")),n.a.createElement(r.a,{id:"food_industry",title:"صنایع غذایی",size:"h3"},n.a.createElement("p",null,"راه‌اندازی کسب و کار فرآوری محصولات کشاورزی مهم ترین روش برای ایجاد اشتغال پایدار و عاملی مهم در توسعه پایدار امنیت غذایی شهرستان محسوب می‌شود. ایجاد صنایع غذایی در شهرستان ارسنجان، علاوه بر اشتغالزایی، باعث ارتقای سطح درآمد ارزی نیز می‌شود که کاهش هدررفت محصولات کشاورزی و ایجاد ارزش افزوده از منابع را تا حد زیادی ممکن می‌سازد. شهرستان ارسنجان ظرفیت های بسیار خوبی در زمینه احداث صنایع غذایی مختلف دارد، اما تا به حال آنگونه که باید از این ظرفیت ها استفاده نشده است؛ بنابراین سرمایه گذاری در زمینه ایجاد واحدهای تولید مواد غذایی در این شهرستان امکان پذیر بوده و ارزش افزوده خوبی را ایجاد خواهد کرد."),n.a.createElement("p",null,"با توجه به میزان تولید انار در شهرستان، فراوری انار می‌تواند ضمن انحصاری بودن محصولات، سودآوری خوبی را به همراه داشته باشد. فراوری انار شامل تهیه رب، سس، آب و شربت انار، مربا، مارمالاد، ژله انار، نوشابه، بستنی انار، ترشی، سرکه و لواشک انار است که با استقبال خوب و بازار مناسب همراه خواهد بود."),n.a.createElement("p",null,"مطابق با نظر کارشناسان، به نظر می رسد فعالیت در زمینه تولید لبنیات در شهرستان نسبت به سایر فعالیت های صنایع غذایی ارجحیت داشته باشد و ظرفیت خوبی برای توسعه آن در شهرستان وجود دارد؛ بنابراین احداث واحد تولید لبنیات در شهرستان ارسنجان مزیت دارد. امروزه این فعالیت بیشتر به صورت خانگی انجام می شود. شیر و فرآورده هاي آن از مهمترین اجزاي تشکیل دهنده جیره غذایی انسان است که از لحاظ ارزش غذایی از کلسیم، فسفر، ویتامین هاي مورد نیاز و پروتئین غنی می‌باشد و همیشه استقبال بسیار خوبی از این نوع محصولات می شود. با توجه به وضعیت دامپروری در شهرستان، ایجاد واحدی برای تولید لبنیات قابل پیشنهاد است.")),n.a.createElement(r.a,{id:"conversion_complementary_industries",title:"صنایع تبدیلی و تکمیلی",size:"h3"},n.a.createElement("p",null,"با وجود تنوع تولید محصولات کشاورزی در شهرستان ارسنجان، بررسی‌ها نشان می‌دهد که سالانه مقداری از این محصولات در مراحل کاشت، داشت و برداشت به ضایعات تبدیل می‌شوند. از سوی دیگر برداشت و یا تولید بسیاری از محصولات کشاورزی در یک بازه زمانی مشخصی رخ می‌دهد؛ اما مصرف آن‌ها در طی سال صورت می‌پذیرد؛ لذا توسعه صنایع تبدیلی در شهرستان ارسنجان می‌تواند زمان استفاده از محصولات را افزایش دهد و از میزان ضایعات آن بکاهد. همچنین گسترش این صنایع موجب اشتغال‌زایی، ایجاد درآمد و جلوگیری از اتلاف و ضایع شدن محصولات در این شهرستان خواهد شد."),n.a.createElement("p",null,"شهرستان ارسنجان، از نظر میزان تولید عسل، در جایگاه مناسبی در استان قرار دارد. بسته بندی عسل از جمله مزیت های زود بازده این شهرستان در حوزه صنایع تبدیلی است؛ زیرا میزان تولید عسل در سال های اخیر افزایش یافته است؛ اما واحدهای بسته بندی متناسب با میزان تولید، توسعه نیافته اند و بخش بزرگی از عسل تولیدی شهرستان به صورت فله ای به فروش می‌رسد. ویژگی مهم عسل آن است که این ماده طبیعی به دلیل دارا نبودن آب، به شرط دوری از رطوبت، در مدت طولانی فاسد نمی‌شود. این ماده فقط در صورتی ارزش غذایی خود را از دست می‌دهد که بیش از ۶۰ درجه سانتی گراد حرارت ببیند. بدین منظور امروزه بسته بندی عسل به شکل های جذاب، خلاقانه و مناسب جهت نگهداری لازم است. بسته‌بندی های شکیل، باعث جذب بیشتر مشتری و همچنن تسهیل در حمل و نقل عسل می‌شود؛ لذا کار کردن در حوزه بسته بندی عسل تولیدی شهرستان علاوه بر سودآوری باعث ایجاد اشتغال پایدار نیز می‌شود."),n.a.createElement("p",null,"با توجه به اینکه شهرستان ارسنجان فاقد کشتارگاه طیور است و طیور ده ها مرغداری صنعتی این شهرستان برای کشتار به شهرستان های اطراف فرستاده می‌شود؛ بنابراین ایجاد چنین واحدی علاوه بر درآمدزایی باعث کاهش هزینه های حمل و نقل می‌شود. فرد سرمایه‌گذار با احداث کشتارگاه صنعتی و خرید طیور، سود اقتصادی خوبی کسب می‌کند. احداث کشتارگاه به لحاظ رعایت موارد بهداشتی، اشتغالزایی، کاهش آلودگی محیطی زیست، بالا بردن ظرفیت تولید و... در هر منطقه ای از شهرستان که تعداد طیور پرورشی زیادی دارد و ذبح آن ها به صورت سنتی انجام می شود، لازم است."),n.a.createElement("p",null,"از مهمترین مزایای استفاده از سردخانه میوه و سبزی می توان به جلوگیری از فساد محصول اشاره کرد. از دیگر مزایای با اهمیت احداث سردخانه میوه، کنترل قیمت بازار است. در واقع با ساخت سردخانه میوه تعادلی در عرضه و تقاضا ایجاد می گردد و موجب خواهد شد از افزایش بی رویه قیمت که به ضرر مصرف کننده است و از کاهش بیش از حد آن که به ضرر باغداران است جلوگیری به عمل آید؛ بنابراین نقش سردخانه های میوه جات به عنوان حد واسط بازار و باغداران در این بین بسیار پر رنگ و حیاتی است. با توجه به میزان تولید زیاد انواع میوه در شهرستان، احداث چنین واحدی دارای مزیت است."),n.a.createElement("p",null,"لازم به ذکر است در صورت دقت نکردن به بازار فروش و ظرفیت شهرستان، مشاغلی که در این حوزه نام برده شد با مسئله اشباع روبه رو خواهند شد؛ بنابراین در نظر گرفتن نیاز و ظرفیت شهرستان و همچنین بازارفروش در شهرستان های اطراف در زمینه راه انداری این مشاغل مهم است.")),n.a.createElement(r.a,{id:"birds",title:"طیور",size:"h3"},n.a.createElement("p",null,"امروزه یکی از معیارهای پیشرفت جوامع، وضعیت تغذیه به ویژه تغذیه پروتئین توسط افراد آن جامعه است. وظیفه متخصصین علوم دامی تولید پروتئین با حداکثر کیفیت و عملکرد و حداقل هزینه و بار محیطی می باشد. فراورده های طیور از مهم ترین منابع پروتئینی در اغلب کشورها هستند، به طوری که تولید و مصرف آن ها در اغلب کشورها با سرعت روز افزون در حال افزایش است. در این زمینه برای پرورش بسیاری از طیور شرایط مساعدی در شهرستان ارسنجان وجود دارد، اما نکته مهم و قابل توجه، بازاریابی جهت فروش محصولات تولیدی است. در واقع وجود بازار فروش دائمی و مطمئن می تواند زمینه را برای گسترش طرح های پرورش طیور چه به صورت سنتی و چه صنعتی فراهم نماید؛ بنابراین در ادامه به تنها طیوری که مزیت خوبی در شهرستان دارد، یعنی پرورش مرغ گوشتی، پرداخته می شود."),n.a.createElement("p",null,"در شهرستان ارسنجان10 واحد مرغداری صنعتی فعال می باشد که ضمن تامین نیاز شهرستان، مابقی به شهرستان ها و استان های اطراف صادر می شود. مرغ از جمله طیوری است که در این شهرستان توجه خوبی به پرورش آن شده است. مرغداری های موجود در این شهرستان به پرورش مرغ گوشتی می پردازند و می توان نسبت به توسعه پرورش آن ها اقدام کرد. گوشت مرغ به سبب مزیت‌های فراوانی که دارد به عنوان منابع تامین پروتئین می تواند مطرح و نیاز عمومی منطقه و کشور را در این زمینه مرتفع نماید. ارزش غذایی بالای گوشت طیور، کم بودن افت پس از کشتار، سالم و بهداشتی بودن گوشت طیور، قابلیت هضم و جذب بالا، سرعت رشد سریع، صرفه جویی در جایگاه و زمین و برگشت سریع سرمایه از فواید پرورش مرغ گوشتی است.")),n.a.createElement(r.a,{id:"services",title:"خدمات",size:"h3"},n.a.createElement("p",null,"تنوع در حوزه مشاغل خدماتی بسیار زیاد است و حرفه های زیادی را در برمی گیرد که میزان سودآوری حرفه ها با توجه به نیاز و ظرفیت شهرستان با یکدیگر تفاوت هایی دارد. برای مثال گردشگری از جمله فعالیت هایی است که با سودآوری بسیاری همراه است و در شهرستان ارسنجان نیز ظرفیت های بسیار خوبی برای سرمایه گذاری در زمینه گردشگری وجود دارد. با توجه به اظهار نظر کارشناسان، با توجه به موقعیت خاص شهرستان، خدماتی همچون ایجاد تعمیرگاه ماشین، احداث اقامتگاه بومگردی و شهر بازی در شهرستان مزیت دارد."),n.a.createElement("p",null,"دسترسی شهرستان به جاده های اصلی یکی از مزیت های این شهرستان است. این شهرستان در سه مسیر از ارسنجان به نی ریز، هرات و سروستان جاده در دست احداث دارد و در صورت تكمیل شدن این سه پروژه، ارسنجان در مسیر چهارراه ارتباطی قرار می گیرد و با توجه به تردد زیاد ماشین های سبک و سنگین در این جاده ها، ایجاد تعمیرگاه ماشین در بین راه یک کسب و کار پرسودی را برای متقاضی آن خواهد داشت. امروزه نیز قسمت هایی از جاده های مذکور به بهره برداری رسیده و کسانی که تعمیرگاه ماشین ایجاد کرده اند بسیار موفق بوده اند؛ از این رو ایجاد تعمیرگاه ماشین سنگین و سبک در کنار این جاده ها دارای مزیت می باشد."),n.a.createElement("p",null,"حدود 111 مورد آثار باستانی در شهرستان ارسنجان همچون مدرسه سعیدیه، مسجد جامع، چشمه گنبان، ایوان قدمگاه چاشت خور، قلعه گچی، تل خندقی، قصر منوچهر، سرآسیاب، پیر با صفا، بی بی خاتون، بند و بس، سد دختر و پناهگاه های صخره ای به ثبت رسیده اند . از نظر مناظر طبیعی نیز در شهرستان حدود 34 هزار هکتار جنگل وجود دارد که بیشترین سطح جنگل های استان فارس را تشکیل می دهد . موارد باستانی و گردشگری ذکر شده باعث می شود تا همه ساله گردشگران زیادی از شهرها و کشورهای مختلف جذب شهرستان شوند و مزیت خاصی برای ایجاد اقامتگاه بومگردی و شهربازی به وجود آید. امروزه در شهرستان ارسنجان اقامتگاه بومگردی وجود دارد، ولی به دلیل استقبال گسترده از این طرح، ظرفیت برای اقامتگاه های جدید نیز وجود دارد و فقدان شهربازی های مدرن و مجهز در شهرستان، ایجاد یک شهربازی مدرن را با توجه به تعداد زیاد گردشگران و افراد بومی مشتاق، توجیه پذیر کرده است.")),n.a.createElement(r.a,{id:"handicrafts",title:"صنایع دستی",size:"h3"},n.a.createElement("p",null,"صنایع دستی چکیده و ثمره ذوق و هنر هنرمندان و صنعتگرانی است که با دستان پرتوانشان روح و جان فرهنگ و هنر به یادگار مانده از نیاکان خویش را جاودانه می‌سازند. صنایع دستی نماد فرهنگ اصیل، تاریخ کهن و هنر بی‌مثال مردم این سرزمین اسطوره‌ای است."),n.a.createElement("p",null,"نکته ای که در رابطه با صنایع دستی وجود دارد این است که برخلاف زحمتی که هنرمند می کشد تا یکی از صنایع دستی را تولید کند، متاسفانه به علت عدم حمایت و بازار فروش داخلی و خارجی، رغبت به سمت فعالیت در این زمینه کاهش یافته است و بسیاری از هنرهای دستی به همین دلیل به فراموشی سپرده شده اند. در ادامه توضیحاتی راجع به یکی از از پتانسیل های صنایع دستی در شهرستان ارسنجان یعنی قالی بافی آورده شده است، اما در هر صورت اگر توجهی به آن از سمت دولت و نهادهای مربوطه نشود، نمی توان انتظار موفقیت برای آن داشت."),n.a.createElement("p",null,"قالی بافی که به صورت بافت فرش و تابلوفرش در این شهرستان انجام می شود، از جمله صنایع دستی شهرستان است که قابلیت گسترش دارد. بافندگی فرش با سرمایه کم و بدون نیاز به مدرک، یک شغل امن و پردرآمدی را در محیطی سالم برای تمام افراد به ویژه دختران و زنان ایجاد می کند. این کسب و کار را می توان در فضایی کوچک حتی در منزل و با سرمایه اولیه کم راه اندازی کرد و نیازی به کسب مجوز و یا تکنولوژی خاصی نیست. از طرفی، قالیبافی از مشاغل خانگی پردرآمدی است که تسهیلات خوبی به آن تعلق می گیرد. بسیاری از زنان خانه دار این هنر را یک فرصت شغلی مناسب می دانند چون در کنار کارهای روزمره می توانند به این کار بپردازند و درآمد خوبی کسب کنند. در کل قالیبافی در شهرستان ارسنجان رونق خوبی داشته و برای سرمایه گذاری قابل پیشنهاد است. شاید یکی از دلایل مزیت داشتن قالی بافی را در شهرستان دسترسی به مواد اولیه ارزان (پشم گوسفند) بتوان عنوان کرد.")),n.a.createElement(r.a,{id:"industry",title:"صنعت",size:"h3"},n.a.createElement("p",null,"مشاغل صنعتی از مهم ترین عوامل توسعه اقتصادی است، اما در شهرستان ارسنجان آن گونه که باید به آن توجه نشده است و لازم است از پتانسیل های شهرستان در این حوزه برای پیشرفت اقتصادی استفاده شود. در شهرستان ارسنجان، تعدادی واحد صنعتی در زمینه های مختلف وجود دارد، اما با توجه به شرایط جغرافیایی خاص شهرستان و عدم دسترسی به بسیاری از زیرساخت های صنعتی و یا دسترسی مشکل به آن‌ها، لذا ایجاد واحدهای صنعتی جدید در شهرستان توجیه اقتصادی خوبی ندارد و لازم است سرمایه گذاران به این نکته توجه کافی را مبذول نمایند.")),n.a.createElement(r.a,{id:"fisheries",title:"شیلات",size:"h3"},n.a.createElement("p",null,"شیلات از حوزه های شغلی سوددهی است که ظرفیت بسیار خوبی برای آن وجود دارد که اگر از این ظرفیت‌های شیلاتی استفاده شود، ارزآوری بسیار خوبی به همراه خواهد شد؛ اما متاسفانه خشکسالی های پی درپی و به دنبال آن کمبود آب در شهرستان ارسنجان باعث شده است تا اجرای طرح های شیلاتی در شهرستان بسیار مشکل و گاها ناممکن شود و از این رو برای سرمایه گذاری پیشنهاد نمی شود."))))}}}]);
