(window.webpackJsonp=window.webpackJsonp||[]).push([[367],{kozz:function(e,l,t){"use strict";t.r(l);var a=t("q1tI"),n=t.n(a),r=t("cvUp"),c=t("CKC9"),m=t("gEOm");l.default=function(){var e=c["IR-20"].cities["IR-2001"];return n.a.createElement("div",{className:"w-full"},n.a.createElement(m.a,{menu:{introduction:"آشنایی با شهرستان",analyze:"تحلیل شهرستان",jobs:{value:"تعیین کسب‌وکارهای مزیت‌دار شهرستان",children:{gardening:"باغداری",fisheries:"شیلات",services:"خدمات",agriculture:"زراعت",food_industry:"صنایع غذایی",birds:"طیور",industry:"صنعت",conversion_complementary_industries:"صنایع تبدیلی و تکمیلی",handicrafts:"صنایع دستی"}}},city:e},n.a.createElement(r.a,{id:"introduction",title:"آشنایی با شهرستان"},n.a.createElement("p",null,"بانه شهری در استان کردستان و از شهرهای مرزی بین ایران و عراق است. شهر بانه با وسعتی حدود 1584 کیلومتر در 270 كيلومتري غرب سنندج مركز استان كردستان و در ۲۰ کیلومتری نوار مرزی ایران با عراق قرار گرفته‌است. بانه از شمال به شهرستان سردشت، از شرق به شهرستان سقز و از جنوب و غرب به سلیمانیه و کشور عراق محدود می‌گردد."),n.a.createElement("p",null,"بانه با شهرستان‌های مریوان، سردشت، سقز و اقلیم کردستان هم‌مرز و دارای ارتباط مناسب جاده‌ای است. این شهر دارای جنگل‌های طبیعی و بزرگ بلوط می‌باشد. جمعیت این شهرستان بر طبق سرشماری سال ۱۳۹۵، برابر با ۱۵۸،۶۰ نفر بوده‌است. این شهرستان قطب نگین اقتصادی غرب کشور است و سالانه صدها هزار نفر از این شهر بازدید می‌کنند و به یک قطب گردشگری در استان تبدیل شده است . شکل زیر موقعیت شهرستان بانه را نشان می‌دهد."),n.a.createElement("p",null,"آب و هوای بانه، به دلیل بارش‌های زمستانی دارای زمستانی سرد و تابستانی نسبتا خشک و آب و هوای نیمه مرطوب سرد و در مقیاس وسیع تر دارای اقلیم نیمه خشک است. بارش سالیانه شهرستان بانه به طور میانگین ۷۰۰ میلی متر است . این امر تأثیر بسیاری در سرسبزی و آبادی روستا‌ها و تفرجگاه‌های اطراف آن دارد.")),n.a.createElement(r.a,{id:"analyze",title:"تحلیل شهرستان"},n.a.createElement("p",null,"مزيت‌سنجي و اولويت‌بندي كسب‌وكارهاي موجود در هر شهرستان بر مبناي يک روش تحقيق انجام مي‌شود. در اين روش تحقيق، يک شناسنامه براي هر شهرستان و يک شناسنامه براي هر كسب‌وكار تدوين مي‌شود. اين شناسنامه‌ها به عنوان ورودي به نرم‌افزار داده‌ شده و خروجي نرم‌افزار هم‌پوشاني اين دو شناسنامه و تعيين مشاغل مزيت‌دار شهرستان است. اين اولويت‌بندي‌ها توسط كارشناسان و افراد خبره هر شهرستان بازبيني و اصلاح می‌شوند. يكي از منابع تحليل همين ليست مزيت‌سنجي اصلاح ‌شده ‌است. يكي ديگر از منابع تحليل، مصاحبه با كارشناسان و متخصصين حوزه اشتغال هر شهرستان است. در هر شهرستان با حداقل 3 نفر از اين افراد مصاحبه انجام مي‌شود. اين مصاحبه‌ها پياده‌سازي و ارزيابي مي‌گردد. در صورت وجود تناقض و ابهام بين اين 3 مصاحبه و ليست مزيت‌سنجي، مصاحبه‌هاي تكميلي و حتي مصاحبه بيشتر هم صورت مي‌گيرد تا تيم تحليل به نتيجه نهايي مطلوب دست ‌يابد؛ بنابراين خروجي هر شهرستان از تجميع مصاحبه‌ها و ليست مزيت‌سنجي حاصل مي‌شود. در ادامه تحليل شهرستان سپیدان ارائه ‌شده ‌است.")),n.a.createElement(r.a,{id:"jobs",title:"تعیین کسب‌وکارهای مزیت‌دار شهرستان"},n.a.createElement("p",null,"همان‌طوري‌كه در قسمت قبل اشاره ‌شد مبناي تحليل، مصاحبه‌ها و ليست مزيت‌سنجي است. از بررسي اين دو منبع رسته‌هاي رايج و اولويت‌دار شهرستان تعيين شده ‌است. مبناي اين اولويت‌بندي، فراواني و ميزان اشاره افراد كارشناس به كسب‌وكارهاي رايج در هر شهرستان و اولويتي كه اين مشاغل در ليست مزيت‌سنجي دارند، است. در هر رسته كسب‌وكارهاي رايج به ترتيب اولويت مشخص شده ‌است. در تحليل به مشاغل موجود، مشاغل قابل توسعه و مشاغلي كه هنوز در شهرستان راه‌اندازي نشده و پتانسيل ايجاد آن‌ها در شهرستان وجود دارد، اشاره شده ‌است. مزيت‌هاي شغلي به ترتيب اولويت در ادامه آورده شده ‌است.")),n.a.createElement(r.a,{id:"gardening",title:"باغداری",size:"h3"},n.a.createElement("p",null,"دامداری"),n.a.createElement("p",null,"کسب و کارهای حوزه دامداری از مشاغل پررونق در شهرستان بانه است. در این شهرستان هم دام سبک و هم دام سنگین پرورش داده می‌شود. پرواربندی گوسفند به دلیل سودآوری بالا و ریسک کم، طرفداران بسیاری پیدا کرده و با اقبال عمومی مواجه شده است. از مزیت‌های اقتصادی این کسب و کار می توان به اشتغال زایی در مناطق مختلف، تولید گوشت مرغوب و پشم اشاره نمود. گوسفند در مقايسه با ساير حيوانات به جيره نگهدارى کمترى احتياج دارد و بيشتر غذاى دريافتى را صرف رشد و نمو مى‌کند. همچنین نسبت به سایر دام‌ها مثل بز و طیور، مقاومت خوبی به بیماری‌ها و شرایط نامناسب رطوبتی و دمایی دارد. بنابراین میزان تلفات در این دام بسیار پایین‌تر بوده و ضرر اقتصادی کمی را از این جهت به دامدار وارد می‌کند. پرواربندی گوسفند نیاز به تجهیزات ارزان قیمتی داشته و به دلیل ریسک کم و بازگشت سریع سرمایه در کوتاه مدت، از جذابیت مالی مطلوبی برخوردار است."),n.a.createElement("p",null,"طرح پرواربندی گوسفند از نظر اقتصادی ظرفیت بالایی داشته و به دلیل نیاز مداوم بازار به گوشت گوسفند و میزان تولید کم آن در داخل کشور، تقاضا برای گوشت گوسفند پا برجا بوده و هر میزان تولید به راحتی به فروش خواهد رسید. از این رو بحث رقابت در این حوزه چندان مطرح نبوده و جزء دغدغه‌های پرورش‌دهندگان نیست. این طرح در شهرستان بانه نیز قابلیت اجرایی شدن دارد و می توان به عنوان کسب وکاری مطمئن به آن روی آورد."),n.a.createElement("p",null,"بز به‌عنوان یکی از نشخوارکنندگان اهلی، در نقاط مختلف دنیا پرورش داده می‌شود. این دام محصولات متنوعی را تولید می‌کند. یکی از اهداف عمده پرورش‌دهندگان بز، تولید گوشت و شیر است. جثه کوچک، قدرت سازگاری زیاد، کم‌توقعی و مقاومت زیاد بز به بسیاری از بیماری‌ها و شرایط نامساعد محیطی باعث شده تا نگهداری و پرورش این حیوان به‌ویژه در شرایط روستایی و در سطح دامداران خُرده‌پا موردتوجه قرار گیرد. در بسیاری از نقاط جهان بزها علاوه بر اینکه به‌عنوان منابع تولید مواد غذایی هستند، پوست مفید و در بعضی کشورها الیاف با ارزشی نیز تولید می‌کنند. بزها معمولاً توسط خانواده‌هایی نگهداری می‌شوند که در مناطق با خاک فقیر، بارندگی کم و مزارع با مساحت کوچک زندگی می‌کنند. در این نواحی شیر و گوشتی که بزها تولید می‌کنند، می‌تواند بخش باارزشی از خوراک روزانه کشاورزان را تأمین کند.علاوه بر این موارد از پشم نوعی بز به نام مزخر شال‌هایی تولید می شود که قیمت بالایی دارد و این شال از پوشش های محلی مردمان کردزبان است. به همین جهت می توان در زمینه پرورش بز مزخر اقدام کرد. قطعا این طرح نیز با موفقیت همراه است."),n.a.createElement("p",null,"در زمینه پرورش دام سنگین هم، گاوهای اصلاح نژادی همچون سمینتال پرورش داده می‌شود و قابلیت توسعه پرورش وجود دارد. سمینتال یکی از بهترین نژادهای دو منظورۀ شیری - گوشتی در جهان از لحاظ تولید می‌باشد. تولید شیر و گوشت گاو سیمنتال در جهان به خاطر سه خاصیت تولید گوشت، شیر و سلامتی آن نسبت به نژادهای دیگر گاو دارای اهمیت است. نسبت گوشت به ضایعات (نظیر چربی لاشه و استخوان) در مقایسه با سایر نژادها بالاتر است. گوشت مطبوع، نرم، ترد و رنگ قرمز بازار پسند دارد. گاو سمینتال برای اصلاح نژاد نیز کاملا مناسب است. اصلاح نژاد با گاو سمینتال در مقایسه با دیگر نژادهای اصلاحی از آن جهت مناسب است که نه تنها در توانایی تولید شیر بلکه درعین حال در توانایی تولید گوشت ارجحیت دارد. یک نکته قابل توجه این است که در هیچ صورتی از اصلاح نژاد نباید از گاو نر آمیخته (دورگه) استفاده کرد، زیرا در اغلب موارد ممکن است منجر به شکاف‌های نژادی ناخواسته و نامناسب شود ."," "),n.a.createElement("p",null,"شهرستان بانه از جمله مکان های مناسب برای پرورش زنبور عسل است و این حرفه درحال حاضر در شهرستان انجام می شود. صنعت زنبورداری منحصر به تولید عسل نیست بلکه تولید گرده، موم، زهر زنبور و ژل رویال که با عنوان شاه انگبین معروف است دیگر موارد این صنعت است که فعالیت در آن ها درآمد زایی خوبی برای تولیدکننده دارد. مسلما هر شغلی مزایا و معایب خاص خود را دارد . اما زنبوردار می بایست شناخت کاملی از حشرات و مخصوصا زنبور عسل داشته باشد . بیماری ها و خطرات احتمالی که ممکن است خطری برای کندوی عسل به وجود بیاورد را بشناسد و نسبت به رفتار و غریزه زنبوران اطلاعات کافی داشته باشد. همچنین باید شناخت کافی از مکان های جغرافیایی مختلف با پوشش های گیاهی متفاوت داشته باشد.")),n.a.createElement(r.a,{id:"fisheries",title:"شیلات",size:"h3"},n.a.createElement("p",null,"شهرستان مرزی بانه یکی از قطب های تولید انگور سیاه است و رتبه سوم در تولید انگور سیاه دیم را در کشور دارد که بخشی از آن به صورت تازه خوری مصرف می‌شود و بخشی نیز به استان های همجوار و اقلیم کردستان عراق صادر می‌شود ؛ بنابراین شرایط احداث باغ انگور سیاه در بانه بسیار مطلوب بوده و در صورت مبادرت به این کار سود خوبی عاید شخص خواهد شد."),n.a.createElement("p",null,"شهرستان بانه از جمله تولید کنندگان گردو در استان کردستان به شمار می رود که بیشتر ارقام کاشت شده در آن از نوع پیوندی است. با استفاده از نهال‌های پیوندی، می‌توان باغ یکنواخت و یکدستی را از نظر ارقام احداث کرد و با به کارگیری روش های صحیح باغداری، ضمن کاهش هزینه های تولید، محصول مرغوب و باکیفیتی را روانه بازار کرد. کاشت نهال‌های پیوندی در باغ گردو، عملیات مکانیزه داشت و برداشت آن را امکانپذیر و صادرات محصول را فراهم می‌سازد. از آن‌جایی که شهرستان شرایط خوبی برای پرورش نهال گردو دارد، می توان در این زمینه ورود پیدا کرد."),n.a.createElement("p",null,"با توجه به نیاز روزافزون بازار، چه از نظر تولید گل و گیاهان زینتی و چه از نظر سبزیجات و صیفی جات خارج از فصل، امروزه تولید و کشت در گلخانه، به یکی از سود آورترین بخش های کشاورزی تبدیل شده است.کشاورزی در گلخانه ها نسبت به کشاورزی در فضای آزاد و در زمین های کشاورزی دارای دردسر و مشکلات کمتری است. به علاوه بازه‌ی زمانی برای به ثمر نشستن و برداشت محصولات نیز نسبت به کشاورزی سنتی، کوتاه تر است. بنابراین با در نظر گرفتن این موارد می توان به این نتیجه رسید که کشاورزی گلخانه ای سودآوری بیشتری دارد. در ادامه تعدادی از محصولاتی که در گلخانه‌های شهرستان قابلیت تولید دارند، معرفی شده‌است."),n.a.createElement("p",null,"از جمله پرسودترین محصولات گلخانه ای می توان به توت فرنگی اشاره کرد. در مورد کاشت توت فرنگی باید گفت که برای شروع کار به سرمایه ی چندان زیادی نیاز نیست و می توان با حداقل هزینه برای کاشت این گیاه پر خاصیت اقدام نمود."),n.a.createElement("p",null,"گیاهان دارویی مانند سرخارگل، به لیمو، آلوئه ورا، کدو پوست کاغذی و به صورت پایلوت نعناء فلفلی و مریم گلی با توجه به اینکه مقاوم به خشکی و کم توقع هستند و به لحاظ اقتصادی مقرون به صرفه‌اند، می توانند بهترین گزینه فعالیت باشند."),n.a.createElement("p",null,"گیاهان زینتی مثل گل‌های جعفری، اطلسی، داوودی، آهار، کلم زینتی و همچنین سبزی و صیفی جات نظیر ریحان، شاهی، شنبلیله، خیار، گوجه فرنگی از جمله گیاهانی است که کشت می‌شوند و قابلیت سودآوری و نیز جذب اقبال عمومی برای خرید و فروش را دارند."),n.a.createElement("p",null,"زعفران نیز که از آن به عنوان طلای سرخ یاد می شود طبق پیشرفت های حاصل شده در محیط گلخانه ای قابل کشت و برداشت است که در اینصورت یک مخزن درآمدزا برای هر سرمایه گذاری می تواند باشد."),n.a.createElement("p",null,"با پیشرفت تکنولوژی امکان استفاده از تکنیکهای جدید کشاورزی مانند کشت طبقاتی در بحث گلخانه ها (کشت هیدروپونیک)، نکته بسیار مهمی تلقی می شود ."," "),n.a.createElement("p",null,"یکی دیگر از کسب و کارهای پر سود، پرورش قارچ است. برای فعالیت در زمینه ی پرورش قارچ، حداقل دو سالن تولیدی، یک سیستم سرمایشی و تجهیزات دیگر که برای پرورش قارچ مورد نیاز است و هزینه ی معقولی دارد.تولید تمامی محصولات فوق با کیفیت بالا می تواند زمینه ساز صادرات به کشور عراق که یک کشور کاملا مصرف کننده است نیز باشد."),n.a.createElement("p",null,"شهرستان مرزی بانه به دلیل داشتن شرایط مناسب آب و هوایی، وجود زمین های مستعد کشت گیاهان دارویی بویژه گل محمدی است. در چند سال اخیر کشاورزان با حمایت های جهاد کشاورزی اقدام به کشت گل محمدی و گلاب گیری کردند ."," "),n.a.createElement("p",null," ","از آنجایی که بحران کمبود آب در کشور در چند سال اخیر به حد هشدار خود رسیده، تغییر الگوی کشت در کلیه فعالیت های کشاورزی متناسب با شرایط منطقه جزء سیاست های دستگاه های ذی‌ربط قرار گرفته است. گل محمدی، مصرف آب کمتری نسبت به دیگر محصولات دارد از این رو توجه به کشت این محصول ضمن افزایش راندمان آب و به دست آوردن ارزش افزوده محصول کشاورزی، حائز اهمیت است ."," "),n.a.createElement("p",null,"گوجه فرنگی یکی از سبزیجات مفید است که در مصرف روزانه خانوار ها مورد استفاده قرار می گیرد، همچنین این محصول قابلیت فرآوری داشته و محصولاتی مانند رب، سس و گوجه خشک از آن تولید می شود. تولید این محصول با قیمت مناسب تر حجم فروش این محصول را بیشتر خواهد کرد. در فصولی که امکان کاشت گوجه در فضای باز وجود دارد هزینه های تولید آن نسبت به تولید در شرایط گلخانه پایین آمده و قیمت مناسب تری خواهد داشت. در شرایطی که در این روش امکان پیش رس کردن گوجه با روش های کشت نشا زیر پلاستیک و انتقال نشا به زمین اصلی در فصل مناسب امکان پذیر بوده و در فصول پرمصرف امکان رساندن محصول مناسب به بازار وجود خواهد داشت. اجرای این طرح در شهرستان بانه قابلیت اجرا داشته و توصیه می گردد."),n.a.createElement("p",null,"احداث باغ گیلاس باتوجه به اینکه این گیاه در مناطق سردسیری قابلیت رشد دارد نیز توصیه می شود و در صورت تمایل می توان روی آن سرمایه گذاری نمود.")),n.a.createElement(r.a,{id:"services",title:"خدمات",size:"h3"},n.a.createElement("p",null,"ماهی از اصلی‌ترین موادی است که در سبد غذایی هر خانواده از جایگاه ویژه‌ای برخوردار است و همین توجه ویژه سبب شده است که پرورش ماهی در خیلی از مناطق از لحاظ اقتصادی توجیه‌پذیر شود.در شهر بانه هم از چند سال گذشته پرورش ماهی سردآبی رشد مناسبی داشته و با تلاش مسئولان و برنامه‌ریزی کارشناسان، شهرستان به سمت خودکفایی در این حوزه گام برداشته است."),n.a.createElement("p",null,"اما در بانه به دلیل مسافر پذیر بودن شهرستان و نیز استقبال مناسب مردم از طبیعت این بستر فراهم است که در کنار واحدهای پرورش ماهی بحث پخت ماهی نیز مطرح شود و این دو کسب و کار در کنار هم انجام شود. همین عامل سبب شده که افرادی هم در این بخش مشغول به کار شوند. کارشناسان آبخیزداری شهرستان بانه بر این باورند که در صورت تأمین آب کافی، چشم انداز توسعه این صنعت در بانه از آینده‌ای روشن برخوردار است و رونق گرفتن آن می‌تواند زمینه صادرات ماهی سردآبی به کشور عراق را نیز فراهم کرده و از این راه درآمد ارزی نصیب کشور کند ."," ")),n.a.createElement(r.a,{id:"agriculture",title:"زراعت",size:"h3"},n.a.createElement("p",null,"بانه علاوه بر داشتن جاذبه های فراوان طبیعی به واسطه ارائه اجناس خارجی در بازار این شهر به یکی از مناطق گردشگر پذیر و قطب گردشگری استان کردستان تبدیل شده است و سالانه میزبان میلیون ها مسافر و گردشگر از جای جای ایران اسلامی است."),n.a.createElement("p",null,"کوه های سرسبز و بلندقامت بانه از جاذبه های گردشگری این شهر هستند، دوزین و بابوس از جمله کوه های بانه هستند و نماد این زیبایی را می شود در کوه آربابا این شهر یافت، کوهی که از شهرت زیادی هم برخوردار است، شهر بانه هم در دامنه آن بنا شده است."),n.a.createElement("p",null,"مشكلات شهرنشینی و فطرت طبیعت‌ دوست آدمی، او را به دامان طبیعت و مناطق آرام می ‌كشاند؛ بنابراین اقامتگاه های بوم گردی فرصت خوبی است تا اسباب توسعه روستایی با گردشگری و خدمات آن فراهم آید."),n.a.createElement("p",null,"اقامتگاه بوم گردی به معنای محل اقامت مسافر است ولی منطبق بر فرهنگ و جغرافیای آن منطقه به طور مثال هتل ‌ها و مهمانسراها در كل ویژگی ‌های مشترك زیادی با همدیگر دارند و فرق چندانی بین یك هتل در یك شهر با هتلی در شهر دیگر نیست. این همان نقطه مقابل هتل و مهمانسراها با اقامتگاه ‌های بوم گردی است، اقامتگاه‌های بوم گردی تشابه كمی با همدیگر دارند حتی اگر در یك منطقه جغرافیایی واقع شده باشند، چراكه قاعده خاصی برای ساخت چنین اقامتگاه هایی وجود ندارد. بومیان هر منطقه جغرافیایی بر اساس شرایط ساخت و ساز و مهم تر از آن فرهنگ منطقه خود دست به ساخت چنین اقامتگاه هایی می ‌زنند."),n.a.createElement("p",null,"بوم گردی شبیه هیچ یك از تجربه های یك سفر معمولی نیست. در این سفر، فرد به دل طبیعت اطراف می زند و سفری آموزشی با اندوخته های باورنكردنی را تجربه می كند. اقامت در اقامتگاه بوم گردی به گردشگر كمك می كند تا راجع به جامعه محلی و مردمانش بیشتر یاد بگیرد، از محیط طبیعی اطراف بیشتر بداند و راه و روش یك زندگی پایدار و دوستدار محیط زیست را تجربه كند."),n.a.createElement("p",null,"از لحاظ موقعیت قرارگیری، بیشتر اقامتگاه های بوم گردی در مكان هایی دور از شهر قرار دارند، مكان هایی كه می توان تا حدی آن ها را بكر نامید، نظیر كلبه ای در دل یك جنگل، پلاژی در كنار ساحل و یا خانه ای سنگی در كوهستان و همین مسئله می تواند یكی از جذابیت های این گونه مكان ها باشد."),n.a.createElement("p",null,"اما مزایای اقامتگاه های بوم گردی به همین جا ختم نمی شود این مكان ها هم برای مهمانان خود و هم برای جوامع محلی و میزبان فواید دیگری نیز دارند، به طور قطع پولی كه گردشگر با خود به منطقه می آورد كمكی است به مردم روستا تا انگیزه ای برای ماندن پیدا كنند و به شهر مهاجرت نكنند و بتوانند كنترل بیشتر و بهتری بر آینده خود در روستا داشته باشند، از فایده های اقامت در اقامتگاه بوم گردی برای مهمانان نیز می تواند تعامل بیشتر گردشگر با محیط بومی و در معرض طبیعت قرار گرفتن باشد."),n.a.createElement("p",null,"گردشگری علاوه بر فرهنگ سازی ، به رشد اقتصاد و سرمایه گذاری کمک بخصوصی می کند.رستوران بومی برای گردشگران داخلی و خارجی جذاب است. گردشگران برای خوردن غذاهای محلی و بومی و حضور در فضاهای بومی به رستوران های بومی مناطق مختلف علاقه بیشتری دارند. نوع غذاهایی که در رستوران بومی سرو می شود اغلب سنتی، محلی و فصلی است، نوشیدنی هایی که رنگ و بوی محلی دارند، مهمانان در محیطی مشابه فرهنگ منطقه پذیرایی می شوند، کارکنان رستوران با لباس محلی عموما خدمت رسانی می کنند. تمامی مناطق ایران با وجود تنوع آب هوایی و جغرافیایی ، در راه اندازی رستوران های بومی و محلی همخوانی دارد مخصوصا شهرستانی مانند بانه که با وجود جاذبه های متنوع گردشگری و تجاری پتانسیل اجرای چنین طرح هایی را دارد. طراحی داخلی و خارجی و تمیزی رستوران بومی در جذب گردشگران بسیار مهم است. با رعایت نکات فوق می توان در کسب و کار راه اندازی رستوران با الگوی بومی موفق بود.")),n.a.createElement(r.a,{id:"food_industry",title:"صنایع غذایی",size:"h3"},n.a.createElement("p",null,"در بانه بیشتر کشت‌های گندم به صورت دیم انجام می‌شود. با توجه کاهش بارندگی‌ها در چند سال اخیر گرایش به دیم کاری در بین کشاورزان بانه بیشتر شده است.کشت جو نیز به همین روش در شهرستان بانه انجام می شود."),n.a.createElement("p",null,"در میان محصولات کشاورزی، کشت گندم نسبت به بقیه کم دردسرتر است.گندم در بازه زمانی حدودا هشت ماهه به ثمر نشسته و به مرحله سوددهی می‌رسد. گندم در دو نوع بهاره و پاییزه کشت می شود که محصول دهی پاییزه آن بیشتر است. بنابراین می توان در هر منطقه ای گندم برداشت کرد. علاوه بر خود گندم، کاه آن نیز مشتری خاص خودش را دارد. بعد از برداشت محصول، زمین آن هم چراگاه گوسفندان است. خرید تضمینی گندم همه ساله توسط دولت انجام می گیرد. خود کشاورز هم می تواند گندم را بفروشد و درآمد خوبی کسب کند. کود شیمیایی و بذر نیز به شکل یارانه ای در اختیار کشاورزان قرار می گیرد. به همین خاطر است که در حوزه زراعت اولین پیشنهاد به علاقه مندان، کشت گندم است."),n.a.createElement("p",null,"جو نیز گیاهی است نسبتاً مقاوم که می‌توان آن‌ را بصورت بهاره یا پاییزه کشت کرد. کشت پاییزه گیاه جو نسبت به بقیه کشت‌ها بهتر است زیرا در این روش گیاه فرصت کافی برای رشد را خواهد داشت و در ضمن کیفیت و کمیت آن هنگام برداشت افزایش می‌یابد. در مورد کیفیت جو پاییزه باید گفت که دارای دانه های بزرگتر،پروتئین بیشتر است و کشت آن مانند کشت گندم در شهرستان بانه مزیت دارد."),n.a.createElement("p",null,"شهرستان‌ بانه که دارای آب و هوای سرد و معتدل است از جمله شهرستان های استان کردستان است که بیشترین سطح کشت نخود را به خود اختصاص داده است.کشت پاییزه نخود در مهر ماه و برداشت آن در نیمه اوّل تیر ماه است و کشت بهاره در اواخر اسفند و اوایل بهار و برداشت آن در اوایل مرداد است. عملکرد حبوبات از جمله عدس و نخود در کشت‌های بهاره و سنتی به دلیل کوتاه بودن دوره رشد پایین بوده و بارندگی‌های پاییزه نیز در این روش از دسترس خارج می‌شود."),n.a.createElement("p",null,"در کشت پاییزه که عمدتا آذر ماه انجام می‌گیرد، انتخاب رقم مناسب بسیار حایز اهمیت است و در این راستا در کشت نخود استفاده از ارقام «سعید» و «آنا» که از عملکرد مناسبی برخوردارند به کشاورزان توصیه می‌شود ."," "),n.a.createElement("p",null,"در کشت انتظاری یا خفته نیز که عمدتا نیمه نخست اسفند ماه انجام می‌شود، اطلاع از شرایط آب و هوایی بسیار حایز اهمیت است به طوری که باید از پایین آمدن دما اطمینان حاصل کرده و مطمئن شد که احتمال جوانه زدن گیاه وجود ندارد.در کشت انتظاری، حبوبات با مساعد شدن شرایط آب و هوایی در نخستین فرصت، کشت انجام گرفته و بذر در داخل خاک قرار داده می‌شود به طوری که به محض آغاز بارش‌های اواخر زمستان و اوایل بهار، گیاه به تدریج جوانه زده و سر از خاک بیرون می‌آورد."),n.a.createElement("p",null,"در کشت‌های بهاره در مناطق مختلف همه ساله معمولا در اوایل بهار به دلیل بارش‌های بهاره امکان وارد شدن به مزرعه وجود ندارد و این موضوع سبب می‌شود عمده بارندگی‌ها از دسترس خارج شود، در حالیکه استفاده حداکثری از بارندگی‌ها، حسن و مزیت بزرگی برای کشت انتظاری و پاییزه محسوب می‌شود و می توان با این روش ها کشت نخود را انجام داد. در شهرستان بانه کشت نخود با بهره گیری از این آب ها مزیت دارد."),n.a.createElement("p",null,"عدس از جمله حبوباتی است که کاشت و برداشت آن نسبت به سایر حبوبات ساده تر بوده و در هر آب و هوایی با دو روش دیمی و آبی قابل کشت است. این گیاه با هوای سرد هم سازگاری دارد و در فصل پاییز میزان ثمردهی آن بیشتر از بهار است. این گیاه به حاصلخیزی خاک کمک کرده و همچنین به عنوان کود سبز برای تقویت خاک استفاده می شود.کشت آن در بانه جواب داده و گسترش آن پیشنهاد داده می شود."),n.a.createElement("p",null,"کشت گیاهان دارویی می تواند نسخه شفابخش ایجاد فرصت های شغلی جدید، افزایش تولید و پیشگیری از تخریب مراتع به عنوان سه چالش مهم جامعه باشد. نیاز به سرمایه اندک، وجود بازار فروش مناسب و درآمد مطلوب، تولید گیاهان دارویی را به قابلیتی برای تحقق شعار اقتصاد مقاومتی، تبدیل کرده است. گیاهان دارویی به عنوان ذخایر و گنجینه های باارزش می تواند بزرگ ترین ثروت ملی برای هر کشوری به عنوان یکی از تولیدات مهم در بخش کشاورزی محسوب شود. گیاهان دارویی از ارزش خاصی در علوم زیستی، پزشکی و دام پزشکی به لحاظ پیشگیری و درمان بیماری برخوردار است. در سال های اخیر استفاده از گیاهان دارویی به دلیل اثبات اثرات مفید آن، ارزان بودن، نداشتن اثرات جانبی و همچنین سازگار بودن با محیط زیست روزبه روز در حال افزایش است، ایجاد فرصت های مناسب سرمایه گذاری جهت تولید بیشتر گیاهان دارویی در بخش کشاورزی حائز اهمیت است و لازم است در زمینه ی فراورده های مختلف تولید این گیاهان به خصوص در ایران، تحقیقات گسترده ای انجام شود چرا که شرایط جغرافیایی و تنوع آب و هوایی ایران و امکان رویش اکثر گیاهان دارویی در آن فرصتی طلایی نصیب کشور گشته است تا از آن به بهترین نحو استفاده نماید. بنابراین کشت گیاهان دارویی و تولید فراورده های آن می تواند به عنوان یک ضرورت ملی و در چارچوب یک برنامه مشخص و جامعه، خودکفایی دارویی، اشتغال و توسعه بخش کشاورزی را به دنبال داشته و به عنوان عامل مهمی در ارز آوری برای کشور اثرگذار باشد.بنابراین با شناخت دقیق از گیاهان دارویی منطقه و گسترش کشت محصولات پرفروش می توان ایجاد کسب و کار نمود."),n.a.createElement("p",null,"باقلا یکی دیگر از محصولاتی است که در شهرستان انجام می‌شود ولی اولویت پایین‌تری نسبت به سایر محصولات زراعی دارد.")),n.a.createElement(r.a,{id:"birds",title:"طیور",size:"h3"},n.a.createElement("p",null,"صنعت غذا در ایران از پتانسیل بالایی برخوردار است که می‌توان علاوه بر مصرف داخلی، محصولات موردنیاز مصرفی کشورهای همسایه را نیز تولید و تامین کرد. صنعت غذا دارای ویژگی‌های مهمی است و در درجه اول باید به تامین امنیت غذایی کشور اشاره کرد که از اهمیت بالایی برخوردار است. در درجه‌ دوم کشورهای اطراف ایران هستند که در زمینه تولید محصولات غذایی ضعیف بوده و می‌توان با تقویت سیستم‌های تولید، نیازهای کشورهای اطراف را تامین کرد. در درجه‌ سوم مساله ظرفیت‌های خاص کشور و همچنین، موضوع مواد اولیه ارگانیک و طبیعی است. محصولات و مواد اولیه‌ خاص و کمیابی در کشور وجود دارد که معمولا در کشورهای توسعه‌یافته نیز کمتر یافت می‌شود. رویکرد مصرف جهانی نیز به سمت مصرف محصولات تغییر ژنتیک نیافته است.این موضوع نیز فرصتی برای صنعت غذایی کشور است و بنابراین، صنعت غذا می‌تواند یکی از تکیه‌گاه‌های توسعه‌ کشور باشد."),n.a.createElement("p",null,"خشک کردن میوه‌جات، یکی از روش‌هاي جلوگیري از فساد میکروبی آن‌ها است. در این روش، در اثر کاهش میزان رطوبت، امکان فساد میکروبی بسیار کم شده و سرعت دیگر واکنش‌هاي مضر نیز، به مقدار قابل توجهی کاهش می‌یابد. خشک کردن، ضمن اینکه روي محصول اثر حفاظتی دارد، وزن و حجم آن را نیز به میزان چشم‌گیری کاهش می‌دهد، در نتیجه از هزینه‌های حمل و نقل و ذخیره‌سازي نیز می‌کاهد."),n.a.createElement("p",null,"با توجه به اینکه بخشی از بازار میوه خشک ایران، سهم محصولات وارداتی است، با تولید مشابه داخلی این میوه‌ها (از نظر نوع و کیفیت)، می‌توان مانع واردات و خروج میلیون‌ها دلار ارز از کشور شد. همچنین نیاز بیش از حد برخی از کشورها به این گونه محصولات (به دلیل شرایط ویژه اقلیمی آن‌ها، هزینه بالای حمل و نقل سبزیجات و میوه‌های تازه و محدود بودن زمان نگه‌داری آن‌ها)، فرصت مناسبی برای رونق تولید و صادرات چیپس میوه فراهم آورده است. وجود باغات میوه نظیر سیب، آلبالو، گیلاس و ... زمینه خوبی را برای ایجاد این کسب و کار فراهم نموده است."),n.a.createElement("p",null,"تولید لبنیات چه به صورت صنعتی و چه به صورت سنتی از جایگاه ویژه‌ای برخوردار است زیرا هم به سلامت افراد جامعه ارتباط پیدا می‌کند و هم از لحاظ سودآوری جایگاه مناسبی دارد.بنابراین با تولید لبنیات باکیفیت و مرغوب می توان در شهرستان بانه بازار فروش خوبی برای خود پیدا کرد. درحال حاضر تولید لبنیات در این منطقه به شکل سنتی انجام می شود و در مقیاس بزرگتر نیز می توان اقدام به راه اندازی این کسب و کار نمود."),n.a.createElement("p",null,"پیش از این به میزان بالای تولید انگور سیاه در این شهرستان اشاره کردیم. با توجه به اینکه سطح زیادی از باغات بانه تولید محصول انگور سیاه می کنند، به همین جهت می توان برای فرآوری آن اقداماتی انجام داد. تولید شیره انگور، سرکه و کشمش که از جمله فرآورده های پرطرفدار انگور هستند می تواند در سطح خانگی و کارگاهی برای ایجاد کسب و کار، مزیت های مناسبی باشند."),n.a.createElement("p",null,"استفاده از عرقیجات و گیاهان دارویی از دیرباز تا به امروز در بین مردم ایران رواج داشته است، در این بین گلاب باتوجه به کاربردهای فراوانی که دارد نسبت به سایر گل‌ها و گیاهانی که از آنها عرق گرفته می‌شود سابقه طولانی‌تری دارد و بیش از سایرین مورد توجه قرار گرفته و مصرف می‌گردد. محصولات عمد‌ه‌ای که از گل محمد‌ی تهیه می‌شود‌ شامل گلاب، اسانس و گلبرگ خشک است که از آنها د‌ر صنایع د‌ارویی، عطرسازی، فرآورد‌ه‌های بهد‌اشتی و آرایشی و صنایع غذایی استفاد‌ه می‌شود‌."," "),n.a.createElement("p",null,"اما آنچه که گلاب را از سایر خوراکی‌ها متمایز می‌کند خواص و کاربردهای آن در مصرف طب سنتی با توجه به طبع گرم آن و همچنین استفاده آن در رفع و یا کاهش آثار بیماری‌ها از جمله: بیماری‌های گوارشی و دردهای روماتیسمی و گلودرد و بیماری‌های تنفسی و… بوده و همچنین در تقویت قلب و معده، رفع اضطراب، ایجاد آرامش، تقویت حواس درونی و حواس باطنی و… تاثیر بسزایی دارد. باتوجه به تولید گل محمدی در شهرستان می‌توان در این حوزه فعالیت کرد.")),n.a.createElement(r.a,{id:"industry",title:"صنعت",size:"h3"},n.a.createElement("p",null,"از مهمترین مسائلی که سبب رشد چشمگیر صنعت پرورش مرغ گوشتی شده است، ارزش غذایی بالای گوشت طیور، کم بودن افت پس از کشتار، سالم وبهداشتی بودن گوشت طیور ، قابلیت هضم وجذب بالا ، سرعت رشد سریع ، صرفه جویی در جایگاه و زمین و برگشت سریع سرمایه است. روند رو به افزایش جمعیت کشور ایجاب می کند که تولیدات متناسب با نیاز های غذایی بهبود و افزایش یابد ، که در این راستا نقش تولیدات گوشت سفید بر همگان روشن است. بدیهی است گوشت مرغ به سبب مزیت‌های فراوانی که دارد به عنوان منابع تامین پروتئین می تواند در کشور مطرح و نیاز عمومی را در این زمینه مرتفع نماید. طبق بررسی‌های انجام‌شده، ایجاد واحد پرورش مرغ گوشتی در شهرستان مزیت دارد و تنها باید بازاریابی کرد تا بتوان محصول را با قیمت مناسب به فروش رساند."),n.a.createElement("p",null,"مرغ بومی در مقایسه با طیور صنعتی مقاوم تر است. همچنین پرورش مرغ محلی نسبت به مرغ صنعتی تلفات کمتری دارد. مرغ بومی نسبت به بیماری‌ها، گرما و سرما مقاومت بیشتری دارد و این امر باعث سود دهی بیشتر می شود. هرچند تلفات در مرغداری ها اجتناب ناپذیر است اما در صورت رعایت بهداشت و واکسیناسیون می توان مرگ و میر مرغ ها را به حداقل رساند. اگر در پرورش مرغ بومی یا همان مرغ محلی از تجهیزات مدرن استفاده شود، سود بیشتری نصیب پرورش دهنده می شود. اما با خلاقیت می توان تجهیزات پرورش شامل :آبخوری ، دان خوری ، لانه تخم گذاری و … را با وسایل ساده ساخت.درواقع با هیچ تجهیزاتی می توان به پرورش مرغ بومی پرداخت."),n.a.createElement("p",null,"پرورش مرغ تخمگذار علاوه براینکه به تامین منابع غذایی طبیعی و رونق تولید کمک می کند نقش به‌سزایی در امر اشتغال‌زایی ، بهبود وضع اقتصادی و خودکفایی در تولید محصول ارگانیک و طبیعی را ایفا می کند. در صورتی که فرد بخواهد مشغول به پرورش مرغ بومی تخم‌گذار به صورت محدود شود نیاز به تکنولوژی پرهزینه ای ندارد و با بهره گیری از امکانات کم و موجود در روستا یا محل زندگی خود می تواند شرایط اجرای طرح را فراهم کند.پرورش مرغ تخم‌گذار حتی به صورت صنعتی و با تمام تجهیزات مدرن می‌تواند در صورت پیدا کردن بازار مناسب، سالیانه حدود 20 تا 30 درصد سودآوری داشته باشد که رقم مناسبی است.همچنین پیدا کردن بازار فروش با حاشیه سود بالا از امور مهم درمورد این کسب و کار است که با تبلیغات مناسب مخصوصا در فضای مجازی امکان پذیر است ."," "),n.a.createElement("p",null,"در طي چند دهه گذشته توجه به بوقلمون و محصولات آن موجب گشت تا پرورش آن به صورت صنعتي در ايران و جهان آغاز و طي چند سال اخير نيز به مرز قابل قبولي برسد تا جائيکه امروزه شاهد افزايش روز افزون مصرف گوشت و ساير فرآورده هاي آن در سراسر جهان هستيم. از آنجا که گوشت بوقلمون داراي خواص غذائي ويژه اي بوده و محدوديت مصرف خاصي نيز ندارد لذا مي تواند به عنوان جايگزين مناسبي براي گوشت قرمز علي الخصوص در بيماراني که مصرف گوشت قرمز براي آنها با محدوديت هائي همراه است، مطرح گردد."),n.a.createElement("p",null,"از ديگر امتيازات گوشت بوقلمون وجود هر دو نوع گوشت سفيد و قرمز در يک لاشه است. گوشت سفيد 65 درصد و گوشت قرمز 35 درصد وزن يک لاشه 8 کيلوئي را تشکيل مي‌دهد. عواملی چون افزایش استانداردهای سطح معیشتی و رشد سریع شهرنشینی، افزایش توجه به غذاهای غربی و همچنین اهمیت به جذب هرچه کمتر چربی، سبب افزایش تولید و مصرف گوشت بوقلمون در دهه گذشته شده است. پرورش بوقلمون گوشتی به دلیل به صرفه بودن تولید، در دسترس بودن نهاده های موردنیاز بوقلمون، نیاز بازار داخلی و نیز امکان انجام صادرات به کشورهای منطقه حائز اهمیت است ."),n.a.createElement("p",null,"محصولات کبک نسبت به دیگر طیور به دلیل طعم و کیفیت مناسب قیمتی بیشتر و مشتریان خاص خود را دارند و از جهت دیگر به دلیل مقاومت نسبتاً بالای این پرنده در مقابل بیماری‌ها، تلفات آن نسبت به دیگر پرندگان کمتر است. همچنین می‌توان به بازار جوجه و تخم کبک اشاره نمود که قابلیت تولید و عرضه بیشتر را دارد و از نظر صادرات در برخی از استان­ها در وضعیت مناسبی قرار دارد. با پرورش کبک به‌صورت صنعتی می‌توان بخشی از نیاز کشور در حوزه گوشت را تأمین کرد و از میزان واردات به داخل کشور کاست."," "),n.a.createElement("p",null,"پرورش پرندگان زینتی به دلیل مزایایی از قبیل درآمدزایی بالا، بازار عرضه و تقاضای خوب، کم دردسر بودن پرورش در مقایسه با سایر ماکیان و نیاز به سرمایه و تجهیزات پایین توانسته مقبولیت گسترده‌ای را در بین افراد جامعه پیدا کند.این قبیل از کسب‌وکارها به‌خوبی توانسته‌اند زمینه‌ساز ظهور فرصت‌های شغلی درآمدزا و زودبازده در کشور باشند. در همین راستا پرورش‌دهنده با اجرا و پیاده‌سازی طرح پرورش پرندگان زینتی می‌تواند در کنار اشتغال‌زایی و به حرکت درآوردن چرخ‌های اقتصادی کشور به سود دلخواه خود هم‌دست یابد. برای پرورش پرندگان پرسود می‌توان از پرنده‌های متنوع از قبیل قناری، کاسکو، فنچ، عروس هلندی، انواع طوطی، طاووس، قرقاول، مرغ عشق، مرغ شاخ‌دار، مرغ مینا، توکان‌ها و ... استفاده نمود که این ماکیان زیبا از مهم‌ترین پرندگان زینتی برای پرورش در کارگاه‌های خانگی هستند.")),n.a.createElement(r.a,{id:"conversion_complementary_industries",title:"صنایع تبدیلی و تکمیلی",size:"h3"},n.a.createElement("p",null,"در شهرستان بانه معادن سنگ از جمله سنگ لاشه وجود دارد که درحال حاضر استخراج از آن ها انجام می شود و می توان در زمینه فرآوری محصولات خارج شده از آن فعالیت نمود چون فرآوری‌ای روی این محصولات در شهرستان بانه انجام نمی شود."),n.a.createElement("p",null,"تولید مصنوعات چوبی آخرین مرحله کار در صنایع چوب است و پس از این مرحله محصولات به دست مصرف کننده می رسد به همین خاطر دارای ارزش افزوده بالایی بوده و اهمیت زیادی را در بخش صادرات می تواند داشته باشد. مهمترین مشکلات تولید و صادرات مصنوعات چوبی تامین مواد اولیه با کیفیت و وجود تعرفه بالا ، فرسودگی و استهلاک ماشین آلات ، عدم رعایت کیفیت مناسب محصولات چوبی و عدم آموزش کافی کارگران صنایع مصنوعات چوبی است. بنابراین تامین چوب و برطرف کردن مشکلات، می تواند در جهت ایجاد کسب و کار تولید مصنوعات چوبی مفید واقع شود. در حال حاضر تولید مصنوعات چوبی در حد کارگاه های کوچک تولید ام دی اف است."),n.a.createElement("p",null,"صنایع و کارگاه هایی که در حال حاضر در بانه فعال هستند عبارتند از: کارخانه لامپ کم مصرف فراز سعیدی، کشتارگاه، تولید لوله پلاستیکی، تولید ظروف یکبار مصرف و کارخانه بازیافت."," ")),n.a.createElement(r.a,{id:"handicrafts",title:"صنایع دستی",size:"h3"},n.a.createElement("p",null,"باتوجه به میزان تولیدات شهرستان در حوزه‌های مختلف، راه‌اندازی کسب‌ وکارهای مرتبط در حوزه صنایع تبدیلی، موجب افزایش تولید و دستیابی به سود بالاتر می‌شود. امروزه بسته‌بندی از اهمیت بالایی برخوردار است. بسته‌بندی اگر خوب و باکیفیت و با طراحی جدید انجام شود می‌تواند به‌عنوان زبان محصول شناخته شود، یعنی معرف محصول باکیفیت باشد و نظر خریدار را به خود جلب کند."),n.a.createElement("p",null,"بسته بندی خشکبار به عنوان بخش مهمی از زنجیره غذایی اهمیت زیادی دارد. خشکبارها عموما مواد غذایی سرشار از انرژی، پروتئین و ویتامین هستند و از ارزش غذایی بالایی برخوردارند و مصرف آن‌ها همیشه توسط پزشکان توصیه می شود. اما در عین حال این مواد غذایی قیمت بالایی دارند و عرضه این محصولات به دست مشتری باید مورد توجه تولید کنندگان قرار گیرد."),n.a.createElement("p",null,"میزان تولید خشکبار در کشور ایران رقم زیادی را به خود اختصاص می دهد ولی بیشتر این محصول چه برای صادرات، چه برای فروش در بازارهای داخلی به شکل فله عرضه می شود و فقط تعداد محدودی از برندها بسته بندی این گروه از مواد غذایی را انجام می دهند. باید توجه داشت بسته بندی مناسب هم در حفظ خواص محصول نقش به سزایی دارد و همچنین نقل و انتقال محصول بسته بندی شده ساده تر است. بنابراین اگر بتوان این گروه از مواد غذایی را در بسته بندی های مشتری پسند ارائه کرد، به شرط آنکه قیمت تمام شده در مقایسه با نوع فله ای خیلی بیشتر نباشد می توان از این طریق سود مناسبی به دست آورد."),n.a.createElement("p",null,"بسته بندی خشکبار از جمله مشاغلی است که به صورت خانگی می تواند انجام شود در این صورت هزینه تامین مکان از مجموع هزینه ها کم می شود. فراوانی محصولاتی که قابلیت بسته بندی دارند، نظیر حبوباتی همچون نخود و عدس و خشکبار نیازمند بسته بندی مناسب است. به همین جهت اجرای این شغل در شهرستان مزیت دارد."),n.a.createElement("p",null,"یکی از بهترین روش ها برای نگهداری و ماندگاری ماهی، بسته بندی مناسب آن است. هدف از انجام این کار جلوگیری از تماس با اکسیژن و فاسد شدن ماهی است. حفظ رطوبت و تازگی ماهی و کاهش اکسایش چربی های آن از دیگر اهداف بسته بندی است.ایجاد واحد بسته بندی ماهی در شهرستان می تواند زمینه ساز صادرات به عراق نیز گردد."),n.a.createElement("p",null,"با احداث سردخانه صنعتی برای نگهداری میوه‌ها می توان زمان ذخیره سازی آن‌ها را افزایش داد و با عرضه محصول در خارج از فصل، سود خوبی کسب کرد. به همین جهت احداث سردخانه صنعتی به عنوان مزیتی موفق در شهرستان بانه مطرح می گردد."),n.a.createElement("p",null,"مرغوبیت بالای عسل تولیدی و سطح برداشت سالانه از کندوها شرایط خوبی را برای بسته بندی عسل فراهم نموده است. با انجام این کار علاوه بر ایجاد ارزش افزوده می توان ایجاد اشتغال نیز نمود. لازم به ذکر است که در حال حاضر بسته بندی عسل به صورت سنتی توسط زنبورداران انجام می شود."),n.a.createElement("p",null,"امروزه خانم های خانه دار کمتر، زمان خود را صرف خرید، پاک کردن و بسته بندی سبزیجات می کنند و ترجیح می دهند با صرف هزینه ای بیشتر، از سبزیجات بسته بندی شده آماده طبخ استفاده کنند. به همین خاطر اگر افرادی باشند که در این میان به این نیاز پاسخ دهند و کار تهیه، فرآوری و بسته بندی سبزیجات را انجام دهند قطعا می توانند فروش خوبی داشته باشند. در صورت تأمین بازار فروش، حتی می توان این شغل خانگی را به کارگاه های بزرگتر با تعداد کارگران بالاتر ارتقا داد."),n.a.createElement("p",null,"همچنین به پیشنهاد کارشناسان باتوجه به پرورش طیور در این شهرستان می توان طرحی را در زمینه بسته بندی گوشت مرغ انجام داد. امید است با اجرای این طرح در صورت فراهم بودن بستر صادرات این محصول به کشورهای همسایه فراهم گردد."))))}}}]);
