(window.webpackJsonp=window.webpackJsonp||[]).push([[384],{"gW9/":function(e,l,t){"use strict";t.r(l);var a=t("q1tI"),n=t.n(a),r=t("cvUp"),c=t("CKC9"),i=t("gEOm");l.default=function(){var e=c["IR-28"].cities["IR-2801"];return n.a.createElement("div",{className:"w-full"},n.a.createElement(i.a,{menu:{introduction:"آشنایی با شهرستان",analyze:"تحلیل شهرستان",jobs:{value:"تعیین کسب‌وکارهای مزیت‌دار شهرستان",children:{livestock:"دامداری",gardening:"باغداری",agriculture:"زراعت",birds:"طیور",industry:"صنعت",fisheries:"شیلات",conversion_complementary_industries:"صنایع تبدیلی و تکمیلی",food_industry:"صنایع غذایی",handicrafts:"صنایع دستی",services:"خدمات"}}},city:e},n.a.createElement(r.a,{id:"introduction",title:"آشنایی با شهرستان"},n.a.createElement("p",null,"شهرستان آشتیان یکی از شهرستان‌های استان مرکزی است. شهرستان آشتیان به مرکزیت شهر آشتیان از شمال به تفرش و قم، از جنوب و جنوب شرق به شهرستان‌های دلیجان و اراک و از غرب به شهرستان فراهان محدود گشته‌است. طبق سرشماری سال 95 جمعیت شهرستان 16357 نفر گزارش شده‌است."),n.a.createElement("p",null,"شهرستان آشتیان در منطقه‌ای نسبتاً سردسیر در در دامنه رشته کوه‌های تفرش و آشتیان قرار دارد و مشرف به ارتفاعات زاگرس از طرف غرب واقع گشته و به همین خاطر دارای آب و هوای نسبتاً سرد و کوهستانی در زمستان و در تابستان ییلاق معتدل و مطبوعی برای گردشگران است. شکل زیر موقعیت شهرستان آشتیان را نشان می دهد.")),n.a.createElement(r.a,{id:"analyze",title:"تحلیل شهرستان"},n.a.createElement("p",null,"مزيت‌سنجي و اولويت‌بندي كسب‌وكارهاي موجود در هر شهرستان بر مبناي يک روش تحقيق انجام مي‌شود. در اين روش تحقيق، يک شناسنامه براي هر شهرستان و يک شناسنامه براي هر كسب‌وكار تدوين مي‌شود. اين شناسنامه‌ها به عنوان ورودي به نرم‌افزار داده‌ شده و خروجي نرم‌افزار هم‌پوشاني اين دو شناسنامه و تعيين مشاغل مزيت‌دار شهرستان است. اين اولويت‌بندي‌ها توسط كارشناسان و افراد خبره هر شهرستان بازبيني و اصلاح می‌شوند. يكي از منابع تحليل همين ليست مزيت‌سنجي اصلاح ‌شده ‌است. يكي ديگر از منابع تحليل، مصاحبه با كارشناسان و متخصصين حوزه اشتغال هر شهرستان است. در هر شهرستان با حداقل 3 نفر از اين افراد مصاحبه انجام مي‌شود. اين مصاحبه‌ها پياده‌سازي و ارزيابي مي‌گردد. در صورت وجود تناقض و ابهام بين اين 3 مصاحبه و ليست مزيت‌سنجي، مصاحبه‌هاي تكميلي و حتي مصاحبه بيشتر هم صورت مي‌گيرد تا تيم تحليل به نتيجه نهايي مطلوب دست ‌يابد؛ بنابراين خروجي هر شهرستان از تجميع مصاحبه‌ها و ليست مزيت‌سنجي حاصل مي‌شود. در ادامه تحليل شهرستان سپیدان ارائه ‌شده ‌است.")),n.a.createElement(r.a,{id:"jobs",title:"تعیین کسب‌وکارهای مزیت‌دار شهرستان"},n.a.createElement("p",null,"همان‌طوري‌كه در قسمت قبل اشاره ‌شد مبناي تحليل، مصاحبه‌ها و ليست مزيت‌سنجي است. از بررسي اين دو منبع رسته‌هاي رايج و اولويت‌دار شهرستان تعيين شده ‌است. مبناي اين اولويت‌بندي، فراواني و ميزان اشاره افراد كارشناس به كسب‌وكارهاي رايج در هر شهرستان و اولويتي كه اين مشاغل در ليست مزيت‌سنجي دارند، است. در هر رسته كسب‌وكارهاي رايج به ترتيب اولويت مشخص شده ‌است. در تحليل به مشاغل موجود، مشاغل قابل توسعه و مشاغلي كه هنوز در شهرستان راه‌اندازي نشده و پتانسيل ايجاد آن‌ها در شهرستان وجود دارد، اشاره شده ‌است. مزيت‌هاي شغلي به ترتيب اولويت در ادامه آورده شده ‌است.")),n.a.createElement(r.a,{id:"livestock",title:"دامداری",size:"h3"},n.a.createElement("p",null,"دامداری یکی از مشاغل موجود در شهرستان آشتیان است. دامداری با یکی از مهم ترین نیازهای انسان، یعنی نیاز به منابع غذایی و خصوصا منابع پروتئینی مرتبط می‌باشد، که توجه به آن امری ضروری است. فعالیت در این حوزه شغلی و افزایش بهره وری در آن، می تواند از راهکارهای توسعه اقتصادی و عاملی برای توسعه صنایع غذایی، تبدیلی و تکمیلی محسوب شود."),n.a.createElement("p",null,"در شهرستان آشتیان دام سبک و سنگین پرورش داده می‌شود. باتوجه به وجود مراتع در منطقه، پرورش دام سبک مزیت بیشتری دارد. در زمینه دام سبک بیشتر گوسفند (از نژاد فراهانی) و بز پرورش داده می‌شود. گوسفند فراهانی یکی از انواع نژادهای گوسفندان پشمی– گوشتی است. این نوع نژاد دارای گوشت و پوست مرغوب است و از این دو نظر سوددهی خوبی برای انسان دارد."," "),n.a.createElement("p",null,"به طورکلی بز به‌عنوان یک حیوان اهلی نشخوارکننده جهت تولید شیر و گوشت در مناطق مختلف پرورش داده می‌شود. جثه کوچک، کم توقع بودن و مقاومت در برابر بیماری‌ها و شرایط سخت محیطی، باعث شده تا بز به‌عنوان حیوانی محبوب در بین دامداران تلقی شود. در نقاط مختلف از این حیوان علاوه بر تولید شیر و گوشت، جهت تولید پوست و الیاف مویی نیز استفاده می‌شود."," "),n.a.createElement("p",null,"یکی از دام‌های سبک اصلاح نژادی، بز سانن است که با توجه به خنکی و معتدل بودن هوا در شهرستان آشتیان و سازگاری دمایی این دام با شرایط منطقه، پرورش آن به افرادی که تمایل دارند، توصیه می شود. بز سانن یک نژاد شیری پرتولید است که نسبت به بزهای بومی، تولید شیر بسیار بیشتری دارد و می‌تواند بخشی از نیاز کشور به شیر و پروتئین حیوانی را تأمین نماید. نرخ تبدیل مواد خوراکی به شیر در بزهای سانن بالاتر می‌باشد که این امر موجب افزایش بازدهی بز سانن از خوراک مصرف شده توسط آن می‌شود. کسب و کار پرورش بز سانن موجب اشتغال‌زایی و رغبت بیشتر جوانان به امر دام‌پروری می‌شود. هر کیلوگرم شیر بز با قیمت بیشتری نسبت به شیر گاو فروخته می‌شود و در نقاط مختلف کشور طرفداران خاص خود را دارد."),n.a.createElement("p",null,"گاو سمینتال جزء بهترین گاو های شیری و گوشتی جهان است. افزایش وزن روزانه فوق‌العاده و گوساله دهی با فاصله 365 روز، مقاومت بالا نسبت به بیماریهای عفونی و متابولیکی، عمر مفید بالا، مدیریت آسان این نژاد و همچنین سازگاری بسیار خوب و قابل توجه با محیط پرورش همان چیزی است که یک دامدار برای بقاء و پیشرفت به آن نیاز دارد. یکی دیگر از مزایای پرورش گاو سمینتال، به صرفه بودن در شرایط اقتصادی فعلی است زیرا این گاو با دادن کمترین علوفه و کنسانتره، بیشترین میزان شیر و گوشت را می‌دهد. دامداران آشتیانی از پرورش این نوع دام بسیار راضی بوده اند و پرورش آن را به همشهریان علاقه مند خود نیز پیشنهاد داده اند."),n.a.createElement("p",null,"الاغ حیوانی است که تا سال ها برای بارکشی در مسیرهای سخت و کوهستانی مورد استفاده قرار می گرفت. امروزه با پیشرفت طب سنتی و آگاهی از فواید فراوان شیر الاغ، این حیوان نیز وارد حیطه دامپروری شده است. با توجه به قیمت بالای شیر این حیوان و نیز فراوری و صادرات شیر الاغ بصورت پنیر به کشور های اروپایی می توان به سود قابل توجهی دست یافت. همچنین این کسب و کار در مراحل مختلف تولید و نگهداری با مشکلات زیادی مواجه نیست و نیاز به تخصص و مهارت خاصی برای راه اندازی ندارد. ویژگی‌های اقتصادی پرورش الاغ تنها به پنیر و پوست این حیوان ختم نمی‌شود، فضولات الاغ که با نام «عنبرنسارا» شناخته می‌شود، خواص درمانی دارد و در طب سنتی مورد استفاده قرار می‌گیرد. این شغل برای محرومیت زدایی و ایجاد کسب وکار در مناطقی که فرصت های شغلی چندانی ندارد بسیار مناسب بوده و باتوجه به سود قابل توجهی که دارد، در این شهرستان نیز پیشنهاد می شود."),n.a.createElement("p",null,"نوغانداری یا پرورش کرم ابریشم و تولید فرآورده‌های ابریشمی اگرچه دارای سابقه و قدمتی بالا در بسیاری از مناطق کشورمان بوده اما در برخی از مناطق و استان‌های کشورمان یک شغل نو و جدید و در ابتدای راه است که کم کم و گام به گام در حال ترویج بوده و از جمله فعالیت‌های جانبی به شمار می‌رود که در کنار فعالیت‌های اصلی کشاورزان، می‌تواند طی یک دوره کوتاه مدت ضمن ایجاد انگیزه اقتصادی و به کارگیری بخشی از نیروی کار روستاها به ویژه بانوان، درآمد مناسبی را برای روستانشینان فراهم کند. نوغانداری نیاز به دانش و تجربه دارد و در صورتی که در کنار تجربه، آموزش لازم به نوغانداران ارائه شود به طور قطع مقدار تولید و کیفیت آنها افزایش می‌یابد . رونق تولید و ترویج هر چه بیشتر این شغل به ویژه در روستاهای شهرستانهایی چون آشتیان به عنوان یک شغل نو و جانبی، سود خوبی برای روستاییان و متقاضیان علاقمند به همراه خواهد داشت."," "),n.a.createElement("p",null,"آشتیان شهری کوهستانی است که در ارتفاعات واقع شده و تابستان‌هایی معتدل و زمستان‌هایی سرد دارد. به واسطه کوهستانی بودن این منطقه گیاهان مختلف مانند گون در این منطقه رشد می‌کنند و عسل از دل این کوهستان‌ها به دست می آید. به همین دلیل این عسل یکی از خالص‌ترین و باکیفیت‌ترین عسل‌های موجود است. با توجه به این که عسل طبیعی دارای خواص بی‌نظیری است از آن برای ساخت دارو در صنعت پزشکی استفاده می‌شود. همچنین در صنعت تولید لوازم آرایشی نیز عسل از جایگاه ویژه‌ای برخوردار می‌باشد به همین دلیل برای تهیه کرم‌های مختلف و... از عسل که نقش پررنگی در شفاف سازی پوست و جوان شدن آن دارد استفاده می‌شود. می توان در کنار تولید عسل خالص طبیعی فعالیت‌های مرتبط با صنعت زنبورداری مانند تولید گرده گل، تولید بره موم، تولید زهر زنبور عسل و تولید محصولات طبیعی بر پایه عسل را نیز انجام داد. با توجه به توانایی تولید عسل و فرآورده‌های آن توسط کارآموزان این حوزه، با افزایش زنبورداری می‌توان اشتغال زایی مناسبی در شهرستان آشتیان ایجاد نمود."," ")),n.a.createElement(r.a,{id:"gardening",title:"باغداری",size:"h3"},n.a.createElement("p",null,"باغداری یکی از حوزه‌های رایج در شهرستان آشتیان است. تولید اشتغال توسط باغداری بسیار زیاد می‌باشد و به اقتصاد شهرستان و فضای کسب و کار رونق می‌بخشد. کشاورزی و ازجمله باغبانی در توسعه ملی و افزایش رفاه عمومی، فقرزدایی، تنوع بخشیدن و پایدار کردن الگوی توسعه شهری، توزیع درآمد و عدالت اجتماعی موثر است."),n.a.createElement("p",null,"گياهان براي داشتن رشد مطلوب نياز به شرايط خاصي از نظر شدت نور، دماي روزانه، دماي‌شبانه، ميزان رطوبت نسبي هوا و رطوبت خاک دارند. براي توليد و پرورش تجاري گياهان با کيفيت بالا و در تمام طول سال بايد شرايط محيطي مطلوب مهیا شده و عوامل خسارت زا نظير آفات و بیماری ها، باد، طوفان هاي ويرانگر، سرما و يخبندان و ...کنترل شوند. گلخانه محیطی است که به راحتی می توان در داخل آن شرایط محیطی را تحت کنترل درآورد و به سمت رشد مطلوب گیاه سوق داد. گلخانه‌ها که از نظر اندازه متغیرند گیاهان را از سرمای بیش از حد زمستان یا گرمای بیش از حد تابستان حفظ می‌کنند. کشاورزی در گلخانه ها نسبت به کشاورزی در فضای آزاد و در زمین های کشاورزی دارای دردسر و مشکلات کمتری است، به علاوه بازه ی زمانی برای به ثمر نشستن و برداشت محصولات نیز نسبت به کشاورزی سنتی، کوتاه تر است. اما با این وجود پرورش گلخانه ای محصولات نیز نیاز به مراقبت های خاص خود را دارد. توت فرنگی یکی از پرسودترین محصولات گلخانه ای است که می‌توان در آشتیان تولید کرد. در مورد کاشت توت فرنگی باید گفت که برای شروع کار به سرمایه ی چندان زیادی نیاز نیست و می توان با حداقل هزینه برای کاشت این گیاه پر خاصیت اقدام نمود."),n.a.createElement("p",null,"کشت گیاهان دارویی گران قیمت و پرسود در گلخانه نیز می تواند کسب وکار مناسبی باشد که قابلیت سودآوری و همچنین جذب اقبال عمومی برای خرید و فروش را به دنبال دارد. خیار، زعفران،گل و گیاه زینتی و قارچ از دیگر محصولاتی هستند که قابلیت کشت در گلخانه را دارند. البته قارچ دکمه‌ای در فضای خارج از گلخانه هم قابل کشت است که پیشنهاد می‌شود."),n.a.createElement("p",null,"با توجه به موضوع مهم کم‌آبی، کاشت درختان دیم و همچنین محصولات دیم بسیار پراهمیت و موردتوجه است. از درخت بادام به‌عنوان یک درخت مقاوم در برابر خشکی یاد می‌شود و در شرایطی که بی‌آبی وجود دارد، درخت بادام میوه خوبی می‌دهد و همچنین ازلحاظ شرایط کاشت در زمین‌های آهکی هم تا حد زیادی مقاومت دارد. با توجه به موارد گفته شده و ارزش افزوده خوبی که کاشت این محصول دارد، ایجاد باغات بادام، سوددهی خوبی را خواهد داشت."),n.a.createElement("p",null,"گردو از دیگر محصولات باغی است که افزایش تولید آن در آشتیان مزیت دارد. گردو، درختی چندساله و مقاوم است که پس از رشد کامل، هر درخت تا 18 کیلوگرم تولید محصول خواهد داشت. محصول نهایی قیمت بالایی دارد که هم در داخل کشور طرفداران زیادی داشته و هم قابلیت بالای صادرات دارد. در صورت احداث باغ گردو، تا سال های سال نیاز به سرمایه گذاری و کشت دوباره وجود ندارد؛ زیرا درخت گردو دارای عمر طولانی است و تا ده ها سال قابلیت برداشت دارد. میوه گردو هم جزء معدود میوه هایی است که هیچ گونه نیازی به هزینه انبار در سردخانه ندارد و به دلیل ماندگاری طولانی، می تواند چند ماه و حتی چند سال مورد مصرف قرار گیرد. در ضمن چوب درخت گردو هم برای منبت کاری، خراطی و کنده کاری، ساخت مجسمه و اشیاء دکوری، ساخت آلات موسیقی و همچنین در دکوراسیون داخلی منزل و ساخت روکش های چوب بسیار پرکاربرد است. این چوب نیمه سنگین و نیمه سخت بوده و مقاومت مناسبی در برابر فشار، خمش، رطوبت و کشش دارد."),n.a.createElement("p",null,"سنجد در آب و هوای معتدل کشت می شود و در صنایع دارویی، غذایی، عطرسازی و چوب کاربرد دارد. البته درخت سنجد، درختی مقاوم در برابر سرمازدگی، کم آبی، آفت و بیماری هاست. با توجه به سازگاری اقلیم شهرستان با کاشت این محصول و وجود مصارف متعددی که در رابطه با این محصول وجود دارد(مصرف به صورت تازه و یا پودرشده)، احداث باغ های بیشتر در این زمینه پیشنهاد می شود"),n.a.createElement("p",null,"گل محمدی، گیاهی مقاوم به کم آبی، آفات و امراض است، که علاوه بر خواص درمانی، دارای مزیت‌های اقتصادی فراوانی مانند ارزش صادراتی، جذب گردشگر و اشتغال زایی است. غنچه خشک، مایع گلاب، روغن و اسانس، فرآورده‌های برگرفته از گل محمدی است که علاوه بر عرضه در بازارهای داخلی، قابلیت صادرات به کشورهای دیگر را دارا است؛ بنابراین کارکردن در این زمینه مزیت دارد."),n.a.createElement("p",null,"طبق بررسی‌های انجام‌شده، آلوئه‌ورا قابلیت تولید در شهرستان را دارد. گونه های مختلف آلوئه ورا گیاهانی مقاوم به گرما، خشکی، بیماری‌ها، آفات و حتی شوری هستند. علت مقاومت زیاد آن ها ساختار مرفولوژیکی این گیاه است و بذر آن ها نیز در شرایط اقلیمی مناسب و در دمای 21درجه سانتی گراد سبز می شوند. البته این گیاهان به سرما بسیار حساس هستند به طوریکه در دمای زیر 10درجه سانتی گراد رشد آن ها متوقف می شود. آلوئه ورا به رطوبت بالا، آب ایستایی و آبیاری زیاد حساس است. از این رو آبیاری گیاهان باید زمانی انجام شود که خاک آن ها کاملا خشک باشد. چنانچه این گیاه در مناطق گرم و خشک کاشته شود باید تحت آبیاری به موقع قرار بگیرد. آلوئه ورا را در هر نوع خاکی می توان کاشت ولی خاک های سبک که از زهکشی مناسبی برخوردار باشند برای کاشت این گیاه توصیه می شود. بازار و جایگاه آلوئه ورا در کشور جدید می‌باشد و آن چنان که باید جایگاه خود را پیدا نکرده اما این گیاه از اندک گیاهان دارویی است که علاوه بر مصرف دارویی در حوزه های آرایشی (مایع، ژل، کرم، لوسیون) و تغذیه ای (عصاره و نوشیدنی) نیز کاربرد دارد."),n.a.createElement("p",null,"درخت پالونیا به دلیل تحمل بالا در برابر گرما، خشکی وعدم حساسیت به انواع خاک، کسب و کار کاشت درخت پالونیا را تبدیل به یک فرصت سودآور در تجارت جهانی کرده است. درخت پالونیا علاوه بر صنعت چوب، به علت زیبایی، سریع الرشد بودن و جذب آلاینده های هوا در جنگل کاری سریع و توسعه فضای سبز شهری مناسب است. رشد درخت پالونیا بسیار سریع است و در طی سه هفته از شروع کاشت به اندازه یک وجب رشد می کند. این درخت در سن 5 سالگی به بلوغ رسیده و چوب آن قابل بهره برداری و استفاده است. نرمی و فرم پذیری چوپ درخت پالونیا ،گزینه مناسبی در صنایع مبلمان سازی است. از چوپ درخت پالونیا علاوه بر تولید مبلمان در درب و پنجره، ساخت آلات موسیقی، روکش سازی، تخته های چند لایه، خمیرکاغذ، ساخت جعبه و کانتینرهای چوبی استفاده می شود.گرده زایی و شهدزایی بالای شکوفه های درخت پالونیا ،تغذیه ی مناسب برای زنبورعسل و مواد معدنی موجود در برگ بزرگ این درخت تقویت کننده ی خاک (خاک برگ و کود سبز) و تغذیه دام می باشد. همچینین در مصارف دارویی تأثیرات مثبتی بر روی کبد، کلیه و کیسه صفرا دارد. باتوجه به نکات گفته‌شده، فعالیت در زمینه کاشت درخت پالونیا پیشنهاد می‌شود.")),n.a.createElement(r.a,{id:"agriculture",title:"زراعت",size:"h3"},n.a.createElement("p",null,"بخش زراعت در روند رشد و توسعه اقتصادی نقش‎های متعددی بر عهده دارد که از جمله مهم‎ترین آن‎ها می‎توان تامین مواد خام و اولیه مورد نیاز صنایع بالادستی، تامین نهاده‎هایی مانند نیروی کار، مصرف تولیدات سایر بخش‏های صنعتی از قبیل کودها و سموم شیمیایی و ماشین‌آلات کشاورزی، ارزآوری برای کشور و تامین مالی سایر بخش‎های زیربنایی را نام برد."),n.a.createElement("p",null,"کلزا از جمله محصولات زراعی است که کشت آن در آشتیان اجرا شده و با توجه به موفقیت آمیز بودن طرح آزمایشی، گسترش آن پیشنهاد داده می‌شود. کشت کلزا باعث از بین رفتن چرخه زندگی آفات، بیماری ها و علف های هرز گردیده و با کاهش جمعیت آنها موجب صرفه جویی در مصرف سموم و کاهش آلودگی های زیست محیطی می گردد. با توجه به آنکه زمان کشت کلزا در فصل پاییز است، نیاز گیاه به آب کم می‌باشد و با استفاده از نزولات جوی می توان به کشت آن اقدام نمود. کلزا با تولید مقادیر قابل توجهی علوفه می تواند در تغذیه دام های منطقه نقش موثری داشته باشد. همچنین برگردان بقای کلزا به خاک باعث افزایش حاصلخیزی خاک و بهبود ساختمان خاک خواهد شد. کشت کلزا به عنوان یک دانه روغنی بسیار مهم در تولید محصولات حوزه صنایع غذایی و یا تولید انواع نهاده‌های خوراکی دام و طیور متداول است. روغن کلزا ازجمله مشتقات این گیاه است که امروزه به دلیل سالم بودن و داشتن کمترین کلسترول و اسیدهای چرب اشباع ‌شده مورد استقبال قرارگرفته است. تحقیقات دانشمندان نشان می‌دهد که دانه کلزا 40-30 درصد روغن و کنجاله‌اش50-40 درصد پروتئین دارد."),n.a.createElement("p",null,"تقاضای مصرف گیاهان دارویی به علت روی گردانی از داروهای شیمیایی و یا حتی استفاده برخی از این محصولات در تولیدات کارخانه‌های بزرگ دارویی همواره رو به افزایش است. گیاهان دارویی گونه‌ها و مصارف مختلفی دارند. از این گیاهان نه تنها برای درمان بیماری‌ها بلکه در صنایع کنسروسازی، نوشابه سازی، ‌عطر سازی و غذایی نیز استفاده می‌شود؛ بنابراین کشت آن ها علاوه بر فضای گلخانه ای در فضای باز نیز دارای مزیت است و پیشنهاد می گردد."),n.a.createElement("p",null,"عدس از جمله حبوباتی است که کاشت و برداشت آن نسبت به سایر حبوبات ساده‌تر است و در هر آب و هوایی با دو روش دیمی و آبی قابل کشت می‌باشد. این گیاه با هوای سرد هم سازگاری دارد و در فصل پاییز میزان ثمردهی آن بیشتر از بهار است. این گیاه به حاصلخیزی خاک کمک کرده و همچنین به عنوان کود سبز برای تقویت خاک استفاده می شود. طبق بررسی‌های انجام‌شده، کشت عدس حتی با روش دیم در شهرستان قابل انجام است."),n.a.createElement("p",null,"کشت نخود گامی در جهت کشاورزی پایدار و حفظ حاصلخیزی اراضی منطقه است. در صورت وجود شرایط مطلوب محیطی و رعایت اصول مدیریتی، نخود از عملکرد خوبی برخوردار ‌است. انتخاب ارقام مناسب و زمان بهینه کاشت از جمله عوامل مهم زراعی است."),n.a.createElement("p",null,"کشت گندم و جو در این شهرستان مانند بسیاری دیگر از مناطق کشور انجام می‌شود و می‌توان به صورت دیمی کاری آن را گسترش داد.")),n.a.createElement(r.a,{id:"birds",title:"طیور",size:"h3"},n.a.createElement("p",null,"پرورش طیور یکی از مشاغل سخت و پرکاری است که به دلیل نیاز جامعه به منابع غذایی، از اهمیت بالایی برخوردار است و سود سرشاری دارد. با افزایش روزافزون جمعیت، در راستای تامین نیازهای مردم، لازم است در زمینه تولید گوشت سفید و پرورش مرغ گوشتی فعالیت کرد. ارزش غذایی بالای گوشت طیور، کم بودن افت پس از کشتار، سالم وبهداشتی بودن گوشت طیور، قابلیت هضم و جذب بالا، سرعت رشد سریع، صرفه جویی در جایگاه و زمین و برگشت سریع سرمایه از فواید پرورش مرغ گوشتی است."," "),n.a.createElement("p",null,"در کنار پرورش مرغ گوشتی می‌توان جهت تأمین تخم مرغ مورد نیاز، به پرورش مرغ تخم‌گذار پرداخت. پرورش مرغ تخمگذار علاوه براینکه به تامین منابع غذایی طبیعی و رونق تولید کمک می کند نقش به‌سزایی در امر اشتغالزایی، بهبود وضع اقتصادی و خودکفایی در تولید محصول ارگانیک و طبیعی ایفا می‌کند. در صورتی که شخص بخواهد مشغول به پرورش مرغ تخم‌گذار به صورت محدود شود نیاز به تکنولوژی پرهزینه‌ای ندارد و با بهره گیری از امکانات کم و موجود در روستا یا محل زندگی خود می تواند شرایط اجرای طرح را فراهم کند. پرورش مرغ تخم‌گذار حتی به صورت صنعتی و با تمام تجهیزات مدرن می‌تواند در صورت پیدا کردن بازار مناسب، سالیانه حدود 20 تا 30 درصد سودآوری داشته باشد که رقم مناسبی است. همچنین پیدا کردن بازار فروش با حاشیه سود بالا از امور مهم درمورد این کسب و کار است که با تبلیغات مناسب مخصوصا در فضای مجازی امکان پذیر است."," "),n.a.createElement("p",null,"علاوه بر پرورش مرغ گوشتی و تخم‌گذار می‌توان در زمینه پرورش مرغ بومی که مزیت‌های فراوانی هم دارد فعالیت کرد. پرورش مرغ محلی نسبت به مرغ صنعتی تلفات کمتری دارد. مرغ بومی نسبت به بیماری‌ها، گرما و سرما مقاوت بیشتری دارد و این امر باعث سود دهی بیشتر می شود. هرچند تلفات در مرغداری ها اجتناب ناپذیر است اما در صورت رعایت بهداشت و واکسیناسیون می توان مرگ و میر مرغ ها را به حداقل رساند. اگر در پرورش مرغ بومی یا همان مرغ محلی از تجهیزات مدرن استفاده شود، سود بیشتری نصیب پرورش دهنده می شود. اما با خلاقیت می توان تجهیزات پرورش شامل :آبخوری ، دان خوری ، لانه تخم گذاری و…را با وسایل ساده ساخت. درواقع با کمترین تجهیزات می توان به پرورش مرغ بومی پرداخت."),n.a.createElement("p",null,"بلدرچین از دیگر طیوری است که پرورش آن در شهرستان پیشنهاد می‌شود. بلدرچین پرنده ای با جثه کوچک، در برابر بسیاری از بیماری ها مقاوم است. پرورش بلدرچین به دلیل هزینه های جاری پایین، اشباع نبودن بازار فروش، کوتاه بودن دوره پرورش(40 روز) و عدم پرورش انحصاری توسط شرکت های بزرگ دارای مزیت است. محصولات این پرنده شامل گوشت، تخم بلدرچین و کود است. گوشت و تخم این پرنده دارای ارزش غذایی و دارویی و همچنین سرشار از پروتئین است."," ")),n.a.createElement(r.a,{id:"industry",title:"صنعت",size:"h3"},n.a.createElement("p",null,"مشاغل صنعتی از مهم ترین عوامل توسعه اقتصادی کشور است. اما در کشور ما آن گونه که باید به آن توجه نشده است و لازم است از پتانسیل های هر شهرستان در این حوزه برای پیشرفت اقتصادی کشور استفاده شود. در شهرستان آشتیان، تعدادی واحد صنعتی در زمینه های مختلف وجود دارد اما پتانسیل های شهرستان نشان دهنده این موضوع است که همچنان برای ایجاد واحدهای صنعتی جدید ظرفیت وجود دارد"),n.a.createElement("p",null,'در میان اقتصاد سنتی و سوغات شهرستان آشتیان می‌توان از کارگاه صابون پزی سنتی نام برد. صابون سنتی آشتیان حدود 400 سال قدمت دارد و مرغوبترین صابون سرشوی سنتی تولیدی درکشور است. این محصول از چربی گاو و گوسفند تهیه می شود و تنها مواد شیمیایی موجود در این محصول"سود کاستیک"است که در مرحله آخر از آن جدا شده و این صابون تبدیل به یک محصول کاملا طبیعی می شود. این شغل بسیار پر زحمت وخطرناک است و افرادی که این کار را انجام می دهند، باید روزانه 10ساعت در گرمای شدید سر پا ایستاده و مراقب دیگ در حال جوشیدن صابون باشند. بعد از مرحله قالب زدن، محصولات را در زیر آفتاب چیده و بعد از خشک شدن، جمع آوری می کنند. در حال حاضر این کار در شهرستان آشتیان در کارگاه‌های کوچکی انجام می شود که قابلیت گسترش و صنعتی شدن دارد. همچنین برند سازی صابون آشتیان می تواند در جهت معرفی این محصول در تمام کشور و حتی کشورهای خارجی مفید واقع شود.'),n.a.createElement("p",null,"واحدهایی که در زمینه صنعت در حال حاضر در شهرستان آشتیان فعالیت می کنند عبارتند از: تولید لوازم پزشکی با نشان آواپزشک، کارخانه مدرن تولیدکننده چراغ اتومبیل، تولیدی شمش سرب و روی، تولید محصولات فلزی کوچک روستای زرنوشه و واحد رنگ سازی."," ")),n.a.createElement(r.a,{id:"fisheries",title:"شیلات",size:"h3"},n.a.createElement("p",null,"فعالیت در حوزه شیلات و به خصوص پرورش ماهی از جمله مشاغل پیشنهادی در شهرستان است. با توجه به ظرفیت های شهرستان امکان افزایش تولید محصولات آبزی پروری وجود دارد. طبق بررسی‌های انجام شده پرورش ماهیان سردآبی مانند قزل آلا و ماهیان گرمابی قابل سرمایه‌گذاری است. همچنین اگر در منابع آبی کشاورزی به خصوص استخرهای موجود در اراضی کشاورزی، پرورش ماهی توسعه پیدا کند، علاوه بر ایجاد اشتغال و تولید مخصوصا در نواحی روستایی، ضمن تأمین غذای سالم مورد نیاز جامعه، امنیت غذایی نیز ارتقاء می یابد."," ")),n.a.createElement(r.a,{id:"conversion_complementary_industries",title:"صنایع تبدیلی و تکمیلی",size:"h3"},n.a.createElement("p",null,"با وجود تنوع تولید محصولات کشاورزی، بررسی‌ها نشان می‌دهد که سالانه مقداری از آن در مراحل کاشت، داشت و برداشت به ضایعات تبدیل می‌شوند. از سوی دیگر برداشت و یا تولید بسیاری از محصولات کشاورزی در یک بازه زمانی مشخصی رخ می‌دهد؛ اما مصرف آن‌ها در طی سال صورت می‌پذیرد. لذا صنایع تبدیلی می‌تواند زمان استفاده را افزایش دهد و از میزان ضایعات آن بکاهد. همچنین گسترش این صنایع موجب اشتغال‌زایی، ایجاد درآمد و جلوگیری از اتلاف و ضایع شدن محصولات می‌گردد."),n.a.createElement("p",null,"به دلیل بالابودن سطح دامداری در شهرستان، احداث کشتارگاه دام مزیت دارد. کشتارگاه‌های صنعتی نقش مهمی در حفظ محیط زیست دارند؛ به دلیل اینکه ضایعات حاصل از کشتار دام نظیر خون و اجزای گوشتی غیر قابل مصرف، در محل تبدیل ضایعات به پودرگوشت که خوراک دام است، تبدیل می شود و علاوه بر قرار گرفتن مجدد در چرخه اقتصادی، از آلودگی محیط زیست نیز جلوگیری می‌کند. فاضلاب حاصل از کشتار در تصفیه خانه های مجهز تصفیه شده و پساب حاصله به مصرف کشاورزی می رسد. البته در کشتارگاه ها باید فضای مناسب برای کشتار دام سبک و سنگین به صورت همزمان وجود داشته باشد و همچنین باید آب مورد نیاز در زمان کشتار نیز تامین شده و تجهیزات و امکانات لازم برای رعایت مسائل بهداشتی و زیست محیطی فراهم شود."),n.a.createElement("p",null,"مراتع مهمترین بخش از منابع تجدید شونده است که به دلیل پایین بودن هزینه تأمین علوفه از آنها در مقایسه با هزینه تولید علوفه از طریق کشت آبی، فشار زیادی بر آن وارد می شود. با توجه به افزیش دامپروری و مرغداری‌ها و پایین بودن ظرفیت تولیدی مراتع، این فشار بیشتر و بیشتر می‌شود و برای جلوگیری از تخریب مراتع باید به فکر منابع غذایی غیر مرتعی آنها بود. با اجرای پروژه های صنعتی که از ضایعات کشاورزی و… خوراک دام تولید شود، هم غذای مورد نیاز دام‌ها تأمین می شود و شهرستان را به خودکفایی سوق می دهد و هم استفاده از مواد اولیه ارزانتر در تولید این محصول سبب خواهد شد تا قیمت تمام شده آن پایین تر و در نتیجه قیمت فروش آن نیز پایین باشد. تولید خوراک دام می تواند زمینه اشتغال خوبی را به طور مستقیم و غیر مستقیم برای افراد ایجاد کند. باتوجه به رونق دامداری و طیور در شهرستان و نیاز آن‌ها به استفاده از خوراک صنعتی می توان در جهت احداث واحدی در زمینه تولید خوراک اقدام نمود."),n.a.createElement("p",null,"امروزه در اکثر صنایع مساله بازیافت پسماندها مورد توجه خاص قرار گرفته است. صنایع تبدیلی، دیگر مانند گذشته پسماندها را جزء ضایعات به حساب نمی‌آورند و سعی می‌کنند با فرآوری آن‌ها، ارزش افزوده بیشتری برای صنایع خود ایجاد نمایند و همچنین به محیط زیست آسیب کمتری برسانند. یکی از پسماندهایی که اخیرا در کشور ما به شکل محدود مورد توجه قرار گرفته است، مساله پسماندهای حاصل از پوست کنی، فرآوری و بسته بندی خشکبار است. یکی از ساده‌ترین را‌ه‌های فرآوری پوست خشکبار، آسیاب کردن و تبدیل آنها به پودر است. با خرید یک دستگاه آسیاب مناسب جهت انجام این کار می توان پودر بدست آمده را به شکل ماده اولیه سایر صنایع از قبیل تولید خوارک دام وطیور، کاغذ، مقوا، MDF، نئوپان، فیبر، کف‌پوش و لمینیت به فروش رساند. بنابر مطالعات انجام شده قیمت پودر به دست آمده 3 تا 4 برابر قیمت پوست خشکبار است. در صورت نداشتن توان مالی جهت خرید دستگاه آسیاب می‌توان با کارگاه‌‌هایی که دارای این دستگاه‌ها هستند همکاری نموده تا با بستن قرارداد و دریافت مبلغی این پوست‌ها را آسیاب کرده و پودری شبیه آرد سفید تحویل دهند. نوع دوم فرآوری که در آن نیاز به سرمایه بالاتر و جذب سرمایه گذار است تولید کربن فعال می باشد. کربن فعال ماده‌ای است که برای تصفیه استفاده می‌شود و از آن می‌توان در تصفیه خانه‌های بزرگ آب، صنایع پتروشیمی، نفت و گاز، صنایع غذایی و دارویی و همچنین پزشکی استفاده کرد. برای تولید هر کیلو کربن فعال به 12 کیلو پوست پسته نیاز است و هم اکنون چین بیشترین صادرات کربن فعال به کشورمان را دارد. متاسفانه در کشور ما بخش زیادی از پوست خشکبار به شکل خام و با قیمت بسیار پایین به دو کشور چین و ترکیه صادر می‌شود . در حالیکه با راه‌اندازی صنایع فرآوری مربوطه، می‌توان از این پتانسیل قدرتمند برای ایجاد کسب و کاری پرسود به عنوان تولید مواد اولیه برای صنایع مختلف بهره برد و از خروج ارز از کشور جلوگیری کرد. از فواید دیگر این صنعت این است که سلولزهای پوست خشکبار می‌تواند جایگزین مناسبی برای چوب درختان و کاهش نیاز به سلولز درختان در صنعت باشد و کمک شایانی به بهبود شرایط زیست‌محیطی و نابودی کمتر جنگل‌های کشورمان نماید. به دلیل تولید خشکبار در شهرستان، فعالیت در این زمینه قابل سرمایه‌گذاری است."),n.a.createElement("p",null,"بسته بندی ترشیجات و شور به صورتی که دارای مجوزهای بهداشتی و برند مناسبی باشد جهت عرضه به بازار بسیار مناسب بوده و قابلیت ایجاد دارند."),n.a.createElement("p",null,"تولید و بسته بندی کمپوست (افزودن کود شیمیایی به کود حیوانی) هم از کسب و کار های خوبی است که قابلیت سرمایه گذاری را دارد؛ زیرا در این شهرستان کشاورزی و دامداری به حد مطلوبی انجام می‌شود؛ لذا مشکلی در خصوص تولید و بازار مصرف این مورد وجود ندارد. كود حيواني علاوه بر تأمين قسمتي از نياز غذایي محصول، ساختمان فيزيكي خاك را بهبود بخشيده تا ريشه محصول بهتر بتواند تنفس كند و فضاي بهتري را برای رشد و گسترش ريشه در اختيار داشته باشد؛ بنابراین طرفداران زیادی مخصوصا در بین باغداران دارد."),n.a.createElement("p",null,"لازم به ذکر است در صورت دقت نکردن به بازارفروش و ظرفیت شهرستان، مشاغلی که در این حوزه نام برده شد با مسئله اشباع روبه رو خواهند شد. بنابراین در نظر گرفتن نیاز و ظرفیت شهرستان و همچنین بازارفروش در شهرستان های اطراف در زمینه راه انداری این مشاغل مهم است.")),n.a.createElement(r.a,{id:"food_industry",title:"صنایع غذایی",size:"h3"},n.a.createElement("p",null,"راه‌اندازی کسب و کار فراوری محصولات کشاورزی مهم ترین روش برای ایجاد اشتغال و عاملی مهم در توسعه پایدار امنیت غذایی محسوب می‌شود که کاهش هدررفت محصولات کشاورزی و ایجاد ارزش افزوده از منابع را تا حد امکان ممکن می‌کند."),n.a.createElement("p",null,"در کنار پرورش گیاهان دارویی در شهرستان، با فرآوری این گیاهان می توان علاوه بر ایجاد کسب و کار زمینه توسعه کشت گیاهان دارویی را فراهم نمود. فرآوری گياهان دارويي، علاوه بر ارزش افزوده، نياز به سرمايه‌گذاري زيادي نداشته و مي‌تواند نقش مهمي در کارآفرینی داشته باشد. یکی از فرآوری‌های گیاهان دارویی تولید عرقیات است. تولید عرقیات دارای تکنولوژی و دانش فنی پیچیده ای نمی باشد و با توجه به ظرفیت تولید گیاهان دارویی در شهرستان آشتیان و نیز سهولت صادرات این محصولات به مناطق دیگر، می توان با سرمایه‌ای اندک به این کسب و کار مشغول شد. خشک کردن و بسته‌بندی نیز از دیگر فرآوری‌های گیاهان دارویی است که می‌توان با فعالیت در آن‌ها به سود رسید."," "),n.a.createElement("p",null,"صنایع تبدیلی با فرآوری محصولات کشاورزی می‌تواند عاملی برای استفاده‌ی بهتر از این محصولات باشد. بسیاری از محصولات کشاورزی دارای زمان تولید و مصرف متفاوتی هستند و تولید یا برداشت آنها ‌معمولا در یک زمان مشخص و کوتاه انجام می‌شود؛ ولی ‌در زمان طولانی‌تری مصرف می‌شوند. مربا و مارمالاد بخاطر ماندگاری نسبتا خوبی که دارند، ریسک از بین رفتن یا کاهش کیفیت در آن‌ها ‌کم است. همچنین چون محصولی مصرفی هستند، بازار آن همیشه در دسترس است. این عوامل در کنار سرمایه نسبتا پایین، این شغل را از لحاظ مالی جذاب‌تر می‌کند. با توجه به تولیدات باغی شهرستان و فراهم بودن بازار فروش، راه‌اندازی کسب‌وکار تهیه مربا و مارمالاد مزیت دارد."),n.a.createElement("p",null,"یکی دیگر از مشاغل پیشنهادی در شهرستان، شیرینی‌پزی است که در مقیاس خانگی و یا کارگاهی قابل انجام می‌باشد. پخت شیرینی در ابعاد وسیع و کارگاهی و حتی در محیطی کوچک (در خانه) امکان پذیر است. شیرینی محصولی است که در انواع متفاوت و با طعم های متنوعی تهیه می شود. به علاوه این که با تغییر در مواد تشکیل دهنده آن می توان برای افراد رژیمی هم شیرینی تهیه کرده و این افراد را نیز به بازار هدف خود اضافه کرد. این کسب و کار را می توان با تجهیزات کم و سرمایه پایینی شروع کرد و در زمان کوتاه با فروش محصول به سود بالایی رسید. پخت شیرینی یکی از مشاغل زودبازده است و یک فرصت شغلی مناسب مخصوصا برای زنان خانه دار محسوب می شود. یافتن بازار فروش محصول یکی از ضروریات این حرفه می‌باشد که با تبلیغات مناسب مخصوصا در فضای مجازی می توان به آن دست یافت . توجه به این نکته ضروری است که یکی از لازمه های موفقیت در این شغل گذراندن دوره های آموزشی مرتبط با پخت شیرینی است."),n.a.createElement("p",null,"امروزه شغل نانوایی یکی از مشاغل پایه‌ای و پر رونق است که در طول سال همیشه درآمدزا می باشد. نان محصولی است که با توجه به سلیقه یا نوع غذای مصرفی خانواده ها، همواره بر سر سفره ها قرار دارد. نان‌ها فقط به صورت نان های ساده و معمول مانند لواش، سنگک و … نیستند بلکه نان های فانتزی نیز تولید می شوند که مشتریان خاص خود را دارند. امروزه راه اندازی کسب و کار تولید نان فانتزی بازاری رو به رشد دارد. این کسب و کار با تولید نان هایی با اندازه و طعم های متفاوت با استقبال مردم مواجه شده‌است. باتوجه به شرایط شهرستان، فعالیت در این زمینه مزیت دارد."),n.a.createElement("p",null,"همچنین کارشناسان با توجه به سطح زیر کشت گندم در شهرستان آشتیان، پیشنهاد ایجاد واحد تولید آرد گندم را در این شهرستان داده اند. با آسیاب کردن گندم، جوانه و سبوس یا همان پوسته از بخش آردی آن تفکیک می‌گردد. آرد با توجه به میزان سبوس جدا شده، به انواع مختلفی تقسیم‌بندی می‌گردد. از انواع آرد می‌توان به آرد قنادی، آرد ستاره، آرد سبوس گرفته و آرد کامل اشاره نمود که از این میان آرد کامل به لحاظ میزان سبوس بالای آن، دارای ارزش غذایی قابل توجهی است. باتوجه به سرانه مصرف آرد در منطقه، ورود به این حرفه مزیت دارد. البته باید توجه داشت که احداث واحدهای بیشتر نیاز به بررسی ظرفیت شهرستان دارد.")),n.a.createElement(r.a,{id:"handicrafts",title:"صنایع دستی",size:"h3"},n.a.createElement("p",null,"آشتيان از جمله مناطقي است كه تاريخ غني دارد و به واسطه اين تاريخ، صنايع دستي متنوع و اصيلي توسط مردمان اين سرزمين توليد مي شود كه بخش اعظمي از آن امروز از بين رفته است. سفالگري هنر بومي منطقه آشتيان به شمار مي رفته به طوري كه عنوان كاسه كاران آشتياني در تمامي نقاط كشور شهره بوده است و حتي سفالگران همداني براي يادگيري فنون اين هنر به آشتيان سفر مي كردند و امروز اين هنرصنعت در آشتيان نيازمند احيا است و بايد از اين قابليت ها در توليد ثروت و اشتغال زايي به نحو احسن بهره برد. از رونق افتادن هنر سفالگري در آشتيان در سال هاي قبل به دليل مقرون به صرفه نبودن آن بوده و اگر زنجيره بازاريابي و توليد منطبق با ذائقه مخاطب و بازار ملاك عمل باشد، اين هنرصنعت مي تواند جايگاه خود را در اقتصاد و معيشت و دايره هنرهاي سنتي آشتيان احيا كند."),n.a.createElement("p",null,"تولید فرش، قالی، قالیچه و تابلو فرش نیز در شهرستان انجام می شود و مهارت آن در بین بانوان و هنرمندان آشتیانی وجود دارد. این تولیدات نیز در صورت بازاریابی قابل سرمایه‌گذاری هستند."),n.a.createElement("p",null,"گيوه دوزي، خورجين دوزي، جوراب بافي و جاروبافي نيز از جمله هنرهايي است كه امروزه جايي در بين صنايع دستي شهرستان ندارد و از دست رفته است؛ اما، آهنگري جزء صنايع قديم شهرستان است كه همچنان در آشتيان فعال است. پرداخت مس و مس گري، منبت كاري، معرق و مشبك، ميناكاري و نقاشي روي چرم از ديگر هنرهاي آشتيان است كه هنرمندان بسياري به آن مي پردازند و آثار شاخصي نيز توليد مي شود. عمده مشكل هنرمندان صنايع دستي در شهرستان آشتيان مقرون به صرفه نبودن توليدات و نبود بازار فروش است كه بتواند توليدات خود را با قيمتي مناسب عرضه كنند و اين امر ناشي از نداشتن اطلاعات و آگاهي از برگزاري نمايشگاه هاي صنايع دستي در نقاط مختلف استان و كشور است که با ایجاد بازارچه دائمی صنایع دستی این مشکل تا حدودی حل شده است. بنابراین روی آوردن به تولیدات صنایع دستی می تواند رونق خوبی داشته باشد."," ")),n.a.createElement(r.a,{id:"services",title:"خدمات",size:"h3"},n.a.createElement("p",null,"آرایشگری یک شغل بسیار زودبازده می باشد. به این معنا که فرد پس از دیدن آموزش های لازم، در مدت زمان کوتاهی می تواند درآمد کسب کند. درآمد آرایشگر بسیار متفاوت است. میزان مهارت، توانمندی، به روز بودن و تجربه آرایشگر و میزان رضایت مشتری در موفقیت او و کسب درآمد بیشتر، بسیار مهم و موثر می باشد. در مجموع آرایشگری درآمد مناسبی دارد. آرایشگرانی که آرایشگاه یا آموزشگاه آرایشگری مستقل دارند، درآمد بالاتر و گاهی بسیار بالاتری نسبت به سایر آرایشگران دارند. البته به شرطی که همیشه خدمات مطلوب و مناسبی به مشتریان ارائه دهند. طبق بررسی‌های انجام شده و به گفته کارشناسان راه‌اندازی کسب و کار آرایشگری زنانه و مردانه در شهرستان مزیت دارد. البته با راه‌اندازی سالن آرایشگاه زنانه می‌توان در کنار آرایشگری، خدمات بهداشتی، زیبایی و پوستی را ارائه و میزان سوددهی را افزایش داد."))))}}}]);
