(window.webpackJsonp=window.webpackJsonp||[]).push([[393],{BA6H:function(e,t,l){"use strict";l.r(t);var a=l("q1tI"),n=l.n(a),r=l("cvUp"),i=l("CKC9"),c=l("gEOm");t.default=function(){var e=i["IR-27"].cities["IR-2720"];return n.a.createElement("div",{className:"w-full"},n.a.createElement(c.a,{menu:{introduction:"آشنایی با شهرستان",analyze:"تحلیل شهرستان",jobs:{value:"تعیین کسب‌وکارهای مزیت‌دار شهرستان",children:{gardening:"باغداری",fisheries:"شیلات",conversion_complementary_industries:"صنایع تبدیلی و تکمیلی",food_industry:"صنایع غذایی",services:"خدمات",livestock:"دامداری",agriculture:"زراعت",birds:"طیور",handicrafts:"صنایع دستی",industry:"صنعت"}}},city:e},n.a.createElement(r.a,{id:"introduction",title:"آشنایی با شهرستان"},n.a.createElement("p",null,"نکا یکی از شهرستان‌های شرقی استان مازندران است که از شمال با دریای خزر، از شرق با شهرستان بهشهر، از جنوب و جنوب غربی با ساری و از غرب با میاندرود همسایه است. طبق سرشماری مرکز آمار ایران در سال 1395، جمعیتی بالغ بر 119511 نفر را در خود جای داده است. شرق مازندران به لحاظ شرایط آب و هوایی کم رطوبت‌تر و گرم‌تر از مناطق مرکزی و غربی است. همچنین از بارندگی سالانه کمتری برخوردار بوده و بر خلاف غرب، رودخانه‌های دائمی و فصلی زیادی ندارد. شکل زیر موقعیت شهرستان نکا را نشان می‌دهد.")),n.a.createElement(r.a,{id:"analyze",title:"تحلیل شهرستان"},n.a.createElement("p",null,"مزيت‌سنجي و اولويت‌بندي كسب‌وكارهاي موجود در هر شهرستان بر مبناي يک روش تحقيق انجام مي‌شود. در اين روش تحقيق، يک شناسنامه براي هر شهرستان و يک شناسنامه براي هر كسب‌وكار تدوين مي‌شود. اين شناسنامه‌ها به عنوان ورودي به نرم‌افزار داده‌ شده و خروجي نرم‌افزار هم‌پوشاني اين دو شناسنامه و تعيين مشاغل مزيت‌دار شهرستان است. اين اولويت‌بندي‌ها توسط كارشناسان و افراد خبره هر شهرستان بازبيني و اصلاح می‌شوند. يكي از منابع تحليل همين ليست مزيت‌سنجي اصلاح ‌شده ‌است. يكي ديگر از منابع تحليل، مصاحبه با كارشناسان و متخصصين حوزه اشتغال هر شهرستان است. در هر شهرستان با حداقل 3 نفر از اين افراد مصاحبه انجام مي‌شود. اين مصاحبه‌ها پياده‌سازي و ارزيابي مي‌گردد. در صورت وجود تناقض و ابهام بين اين 3 مصاحبه و ليست مزيت‌سنجي، مصاحبه‌هاي تكميلي و حتي مصاحبه بيشتر هم صورت مي‌گيرد تا تيم تحليل به نتيجه نهايي مطلوب دست ‌يابد؛ بنابراين خروجي هر شهرستان از تجميع مصاحبه‌ها و ليست مزيت‌سنجي حاصل مي‌شود. در ادامه تحليل شهرستان سپیدان ارائه ‌شده ‌است.")),n.a.createElement(r.a,{id:"jobs",title:"تعیین کسب‌وکارهای مزیت‌دار شهرستان"},n.a.createElement("p",null,"همان‌طوري‌كه در قسمت قبل اشاره ‌شد مبناي تحليل، مصاحبه‌ها و ليست مزيت‌سنجي است. از بررسي اين دو منبع رسته‌هاي رايج و اولويت‌دار شهرستان تعيين شده ‌است. مبناي اين اولويت‌بندي، فراواني و ميزان اشاره افراد كارشناس به كسب‌وكارهاي رايج در هر شهرستان و اولويتي كه اين مشاغل در ليست مزيت‌سنجي دارند، است. در هر رسته كسب‌وكارهاي رايج به ترتيب اولويت مشخص شده ‌است. در تحليل به مشاغل موجود، مشاغل قابل توسعه و مشاغلي كه هنوز در شهرستان راه‌اندازي نشده و پتانسيل ايجاد آن‌ها در شهرستان وجود دارد، اشاره شده ‌است. مزيت‌هاي شغلي به ترتيب اولويت در ادامه آورده شده ‌است.")),n.a.createElement(r.a,{id:"gardening",title:"باغداری",size:"h3"},n.a.createElement("p",null,"شهرستان نکا با دارا بودن خاک حاصلخیز و شرایط آب و هوایی مناسب یکی از مناطق مستعد باغداری در استان مازندران شناخته می‌شود. همچنین باغداری یکی از اصلی‌ترین مشاغل مردم در سطح این شهرستان است که طبق بررسی‌های انجام شده رونق مناسبی داشته و علاقه به باغ و باغداری در بین مردم وجود دارد. مازندران، البرز و تهران از جمله استان‌هاي توليدكننده هلو و شليل در كشور هستند كه براساس اعلام مراجع رسمي، مازندران رتبه سوم توليد هلو و شليل و بر اساس منابع غير رسمي رتبه دوم توليد اين دو محصول را در سطح کشور دارا است. شرايط اقليمي و محیطی مناسب، عدم يخبندان و سرمازدگي بهاره و تجهيز باغ‌ها به سيستم‌هاي نوين آبياري از عومل رونق توليد محصولات باغی هلو و شليل در استان مازندران به‌ویژه شهرستان نکا عنوان شده است. شهرستان نکا يكي از قطب‌هاي توليد هلو و شلیل در سطح استان شناخته شده است. قابل ذکر است که سه رقم هلو و شلیل زودرس، میان رس و دیررس در این شهرستان کشت می‌شود که انوعی مانند لاکدشتی، دشت ‌مغان، پلنگی، کیوتو، داوودی، سوپر کیوتو، انجیری، کرست، زعفرانی، ردتاب و شمس است. وجود مزیت نسبی برای تولید این دو محصول در نکا، لزوم کار در زمینه صنایع تبدیلی و فرآوری آن را برای ایجاد رونق و انگیزه بیشتر در کشاورزان یادآور می‌شود. علاوه بر این با توجه به اینکه میزان کشت این دو محصول قابل ملاحظه بوده و از کیفیت خوبی نیز برخوردار هستند، در صورت ایجاد شرایط می‌توان به صادرات این محصولات و فرآورده های آن به سراسر کشور و حتی کشورهای منطقه فکر کرد."," "),n.a.createElement("p",null,"میوه باغی تمشک یکی دیگر از محصولات شهرستان نکا بوده که از مزیت اقتصادی مطلوبی برخوردار است. تمشک میوه‌ای شبیه شاه‌توت و توت فرنگی است و به رنگ زرد یا سرخ مایل به سیاه یافت می‌شود. این میوه دارای آنتی اکسیدان و خاصیت ضدسرطانی بوده و خوردن آن به جلوگیری از روند پیر شدن سلول‌های بدن کمک می‌کند. همچنین به لحاظ مقاوم بودن در برابر سرما و بیماری‌ها به‌عنوان یک میوه ارگانیک، می‌تواند جایگزین مناسبی برای محصولات باغی آسیب‌پذیر باشد. ایجاد توسعه در زمینه کشت تمشک، باعث خواهد شد تا علاوه بر این که صنایع تبدیلی و فرآوری آن رونق پیدا کند، اوضاع اقتصادی شهرستان تغییر محسوسی را تجربه کند و بتواند از این محصول و مزیت نسبی تولید آن به نحو احسن استفاده نماید؛ چرا که شهرستان نکا به علت شراط خاص آب و هوایی، محل مناسبی برای رشد و تکثیر این گیاه است."," "),n.a.createElement("p",null,"در نکا مانند هر کدام از شهرستان‌های دیگر استان مازندران، تولید مرکبات از رونق خوبی برخوردار است. از مرکبات کشت شده در سطح نکا، باید به انواع نارنگی و پرتقال اشاره کرد. واحدهایی نیز در زمینه سورتینگ و درجه‌بندی این محصولات فعالیت دارند. در صورت ایجاد توسعه در زمینه کشت این مرکبات، علاوه بر داشتن مزیت‌هایی که در فوق به آنها اشاره شد، به افزایش صرفه اقتصادی فعالیت در زمینه تبدیل و فرآوری کمک شایانی خواهد شد. در این شهرستان پرورش قارچ نیز مدنظر قرار گرفته و بسیاری از ساکنین آن در این بخش مشغول به فعالیت هستند. کمک به کاهش وزن، مناسب بودن برای افراد دیابتی، خواص ضد پیری و تقویت سیستم ایمنی بدن از فواید مصرف قارچ به شمار می‌آید. همچنین بزرگترین و مجهزترین شرکت تولید قارچ خوراکی دکمه‌ای استان در این منطقه واقع شده است . در ضمن در لیست مزیت‌سنجی به مزیت تولید هر دو نوع قارچ دکمه‌ای و صدفی اشاره شده است و سرمایه‌گذاری در این دو زمینه سود مناسبی را عاید شخص پرورش دهنده می‌کند."," "),n.a.createElement("p",null,"گلخانه‌داری از شاخه‌های نوین حوزه باغداری بوده و در شهرستان نکا عمدتاً با هدف افزایش بهره‌وری در تولید، صرفه جویی در مصرف آب، صرفه جویی در سوخت و افزایش بازده و تولید انجام می‌شود. احداث گلخانه صیفی‌جات و سبزیجات از جمله مشاغل پیشنهادی در شهرستان است."),n.a.createElement("p",null,"علاوه بر تولید و پرورش انواع صیفی‌جات و سبزیجات در گلخانه‌ها، این محصولات در فضای آزاد و به صورت زراعی نیز تولید می‌شوند. بازار مصرف دائمی، اصلی‌ترین دلیل برای توسعه کشت و تولید این محصولات می‌باشد لذا بازده اقتصادی و سودآوری قابل ملاحظه‌ای به همراه دارد."," "),n.a.createElement("p",null,"زیتون محصول باغی ارزشمند و اقتصادی دیگری است که به محصول ثروتمند خاک‌های فقیر مشهور بوده و در شهرستان نکا عملکرد مناسبی برای آن مشاهده شده است. با توجه به امكان توسعه و گسترش باغات زيتون در این شهرستان و کیفیت بالای زیتون تولیدی، می‌توان در این زمینه توسعه قابل ملاحظه‌ای ایجاد کرد. گردو پیشنهاد دیگری در حوزه باغداری این شهرستان است. محصول گردو از جمله خشکباری است که با شرایط محیطی و آب و هوایی نکا سازگاری خوبی داشته و در این شهرستان تولید می شود. طبق بررسی‌ها و آمار موجود سطح باغات‌ شهرستان بالغ بر 272 هکتار بوده است که از آن مساحتی حدود 206 هکتار بارور است. در ضمن از این اراضی، 650 تن محصول برداشت شده است. از این‌رو می‌توان از ظرفیت بالقوه شهرستان جهت کشت و توسعه بیشتر این محصول بهره برده و درآمدزایی بالایی را برای باغداران به ارمغان آورد. در ضمن در لیست مزیت سنجی به قابلیت کشت خرمالو به علت داشتن مهارت و تجربه باغداران شهرستان در این زمینه اشاره شده و مورد پیشنهاد است. همچنین کشت انار نیز دارای مزیت بوده و به علت بازار عرضه و مصرف پر رونق و همین‌طور قابلیت فرآوری مطلوب آن در صنایع مرتبط مورد توجه بوده و قابلیت سرمایه‌گذاری و سودآوری مناسبی دارند."," ")),n.a.createElement(r.a,{id:"fisheries",title:"شیلات",size:"h3"},n.a.createElement("p",null,"آبزی‌پروری و پرورش انواع ماهی به علت صرف انرژی کمتر، قابلیت بالای تولید مثل و هزینه تولید ارزان‌تر، مورد توجه قرار داشته و علاوه بر ایجاد فرصت‌های شغلی جدید در سطح شهرستان، سود و درآمد بالایی را عاید افراد مشغول در این زمینه می‌کند. این شهرستان دارای ۴۰ قطعه مزارع گرمابی به مساحت ۱۳۸/۲ هکتار و ۲ قطعه آب بندان عمومی با مساحت ۱۱۸ هکتار بوده که علاوه بر مصرف زراعی و کشاورزی، در پرورش ماهی نیز مورد استفاده قرار می‌گیرند و موجب تولید ۲۱۵ تن محصول آبزی شده است. در این شهرستان پرورش ماهیان سردآبی به دلیل افزایش وزن و رشد بهینه دارای سودآوری بوده و مورد پیشنهاد است. ماهیان سردآبی دارای گوشتی لذیذ است که طرفداران زیادی دارد و طعم و مزه این ماهی حتی در بین کسانی که کمتر ماهی را به‌عنوان غذای اصلی خود مصرف می‌کنند، خوشایند است. پرورش ماهی های سردآبی در کوتاه مدت به سوددهی بالایی می‌رسد و ظرفیت ایجاد این طرح در شهرستان وجود دارد."," "),n.a.createElement("p",null,"طبق بررسی‌ها یک قطعه مزرعه سردآبی با مساحت یک و نیم هکتار و تولید ۸ تن و یک شرکت تعاونی صید پره با تولید ۲۰ تن نیز در نکا در حال فعالیت هستند. به مزیت پرورش اردک ماهی به علت نبود این نوع از ماهی در منطقه و پرورش ماهی در قفس به علت جدید بودن این نوع از پرورش، اشاره شده است. افزایش تنوع گونه‌ای و انتخاب گونه‌های پر تولید به منظور افزایش تولید در واحد سطح از مهمترین اهداف در زمینه آبزی پروری است. در این حالت بایستی از گونه‌هایی استفاده گردد که بتواند ضمن بومی بودن، از رشد و بازارپسندی مناسبی برخوردار باشد. برای این منظور اردک ماهی مورد پیشنهاد می‌باشد. اردک ماهی از راسته اردک ماهی شکلان و خانواده اردک ماهیان می‌باشد. این گونه از ماهیان آب شیرین و سطح‌زی محسوب گردیده که در رودخانه‌ها، دریاچه‌های آب شیرین و تالاب‌ها در داخل گیاهان بخصوص نیزارها زیست می‌نماید. این ماهی به عنوان شکارچی می‌تواند از موجوداتی نظیر لارو سنجاقک، حلزون، قورباغه و ماهیانی همچون سوف، سیم، کپور، لای ماهی به خوبی نغذیه نماید. بچه ماهیان یک تا دو ساله آن قادرند روزانه به میزان 10 تا 15 درصد از وزن خود از ماهیان تغذیه نمایند که این نسبت گاهی تا حدود 30 درصد نیز می‌رسد."," "),n.a.createElement("p",null,"روند رو به رشد جمعیت، محدودیت منابع غذایی و کاهش صید برخی از گونه‌های دریایی، اولویت استفاده از منابع آب شیرین برای شرب و کشاورزی، فعالان حوزه شیلات را به پرورش ماهیان دریایی در قفس ترغیب کرده است. کسب دانش فنی، مکان یابی مناسب برای استقرار قفس در محیط دریا، بررسی شرایط فیزیکی و شیمیایی آب دریا و شرایط اقلیمی دریا مهمترین مولفه‌ها جهت کسب درآمد اقتصادی از این نوع پرورش می‌باشد."," ")),n.a.createElement(r.a,{id:"conversion_complementary_industries",title:"صنایع تبدیلی و تکمیلی",size:"h3"},n.a.createElement("p",null,"با وجود تنوع تولید انواع محصولات کشاورزی، بررسی‌ها نشان می‌دهد که سالانه بخشی از این محصولات در مراحل کاشت، داشت و برداشت به ضایعات تبدیل می‌شوند. از سوی دیگر برداشت و یا تولید بسیاری از محصولات کشاورزی در یک بازه زمانی مشخصی رخ می‌دهد اما مصرف آن‌ها در طی سال صورت می‌پذیرد. لذا صنایع تبدیلی می‌تواند زمان استفاده را افزایش دهد و از میزان ضایعات آن بکاهد. همچنین گسترش این صنایع موجب اشتغال‌زایی، ایجاد درآمد و جلوگیری از اتلاف و ضایع شدن محصولات می‌گردد. هلو میوه‌ای است که تمام اجزای آن قابل استفاده و مفید است. از برگ درخت می‌توان جوهرهای زرد و سبز و از میوه هلو جوهرهای خاکستری تیره و سبز تهیه نمود. از صمغ ساقه درخت چسب تهیه می گردد، از له شده برگ‌های هلو جهت تمیز کردن و برطرف کردن بوهای نافذ و قوی مانند سیر و یا میخک از ظروف استفاده می‌کنند. همچنین از مغز دانه هلو روغنی استخراج می شود که مانند روغن بادام می‌تواند در کرم‌های پوستی مورد استفاده قرار گیرد. میوه درخت هلو که اقتصادی‌ترین جزء آن است در حوزه صنایع تبدیلی و تکمیلی کاربرد داشته و به دلیل مزیت شهرستان در تولید انبوه این محصول، بسته بندی هلو مورد پیشنهاد می‌باشد. همچنین باید به مزیت اقتصادی بسته‌بندی شلیل و دیگر مرکبات نیز اشاره کرد. این دو محصول نیز به دلیل بازار عرضه و فروش مناسب در طول سال، پر منفعت و اقتصادی می‌باشند."),n.a.createElement("p",null,"شهرستان نکا به دلیل دارا بودن شرایط خاص آب و هوایی، محل مناسبی برای رشد و تکثیر گیاه تمشک است و این گیاه خودرو و خدادادی منبع درآمد بسیاری از مردم منطقه و روستانشینان است. طبق آمار موجود در بازه ۵۰ روزه برداشت تمشک در شهرستان نکا ۵۶ هزار نفر روز بکار گرفته می‌شوند که به نوبه خود سهم بسزایی در اشتغال جمعیت شهرستان دارد. ایجاد واحد یا واحدهای سورتینگ و بسته‌بندی این محصول می‌تواند میزان اشتغال را افزایش داده و باعث رونق هر چه بیشتر تولید در این زمینه شود. درصورت وجود شرایط حتی می‌توان بر روی ارسال و صادرات این محصولات به دیگر استان‌ها و مناطق کشور نیز فکر کرد. به علت وجود ظرفیت و تراکم پوشش گیاهی مناسب منطقه در زمینه زنبورداری، سالانه توناژ قابل توجهی عسل در سطح شهرستان بدست می‌آید. از این‌رو بسته‌بندی عسل به علت تولید عسل انبوه و با کیفیت، از نظر اقتصادی مقرون به صرفه بوده و پیشنهاد می‌شود. فعالیت در این حیطه به نوبه خود می‌تواند انگیزه افرادی که در حال حاضر به پرورش زنبور عسل مشغول هستند را بیشتر کند و هم می‌تواند انگیزه ورود به زنبورداری را در افراد جدید ایجاد کند. در ادامه باید اشاره کرد که فرآیند تبدیل شالی به برنج (شالیکوبی) نیز به علت حجم بالای تولیدات شهرستان مزیت دارد. امروزه شالیکوبی به منظور جلوگیری از افزایش تلفات و افزایش سود و درآمد شالیکاران مدنظر بوده و در این میان استفاده از دستگاه‌های مدرن به منظور کاهش ضایعات محصول در حد استاندارد ضروری است. از این‌رو با توجه جایگاه استراتژیک برنج در سبد غذایی جمعیت و همین‌طور میزان تولیدات برنج در سطح شهرستان و سطح تقاضا برای آن، شالیکوبی دارای مزیت اقتصادی خواهد بود. البته در حال حاضر به صورت محدود در این زمینه فعالیت‌هایی انجام می‌شود ولی با توجه به سطح زیرکشت و بررسی شرایط بازار، می‌توان در این زمینه توسعه بیشتری ایجاد کرد."),n.a.createElement("p",null,"بسته‌بندی انواع محصولات غذایی مانند میوه خشک، کمپوت، مربا و ترشیجات، روغن کلزا، فرآورده‌های سویا و... دارای مزیت خواهند بود و سود تولید این موارد را بیشتر خواهند کرد."),n.a.createElement("p",null,"با توجه به میزان کشت مرکبات و میوه‌جات در سطح شهرستان نکا و وجود مزیت برای ایجاد توسعه در این زمینه، ایجاد سردخانه به‌ منظور نگهداری از این محصولات مزیت خوبی خواهد داشت. البته اگر فرآوری و صنایع تبدیلی رونق بیشتری پیدا کنند، میوه‌ها به سرعت یا فرآوری و یا بسته بندی می‌شود در نتیجه نیاز زیاد به وجود سردخانه نخواهد بود. همچنین باید اشاره کرد واحدهایی مانند سردخانه علی رغم سود زیاد، با احداث چند واحد محدود به حالت اشباع می‌رسند."),n.a.createElement("p",null,"در حوزه صنایع تبدیلی و تکمیلی، احداث کشتارگاه صنعتی و بهداشتی با توجه به رونق دامداری می‌تواند یک پیشنهاد باشد. طبق بررسی‌ها تنها کشتارگاه صنعتی و بهداشتی در شرق استان مازندران، با ظرفیت نگهداری یک هزار و 500 راس گوسفند و بز، یکی از بزرگترین کشتارگاه های صنعتی دام در استان است. این واحد به تنهایی می‌تواند به شهرستان‌های ساری، قائمشهر، نکا، بهشهر، جویبار و بابلسر خدمت‌رسانی کند. در نتیجه در شرایط فعلی نیازی به ایجاد کشتارگاه در سطح نکا نمی‌باشد.")),n.a.createElement(r.a,{id:"food_industry",title:"صنایع غذایی",size:"h3"},n.a.createElement("p",null,"راه‌اندازی کسب و کار فرآوری انواع محصولات کشاورزی مهمترین روش برای ایجاد اشتغال پایدار و کاهش وابستگی به درآمدهای نفتی و عاملی مهم در توسعه پایدار امنیت غذایی است. ایجاد صنایع غذایی متناسب با ظرفیت شهرستان، علاوه بر اشتغال‌زایی، باعث ارتقاء سطح درآمد اهالی آن می‌شود که کاهش هدررفت محصولات کشاورزی و ایجاد ارزش افزوده از منابع را تا حد امکان ممکن می‌کند."),n.a.createElement("p",null,"تمشک از جمله میوه‌های خوش عطر و طعم جنگلی است و سالانه یک ‌هزار تن تمشک جنگلی در نکا جمع‌آوری می‌شود که تنها ۳۰ درصد آن استفاده می‌شود و ۷۰ درصد آن به علت نبود کارخانه‌ای جهت فرآوری از بین می‌رود. لذا مزیت نسبی بالایی که نکا در تولید این محصول نسبت به دیگر مناطق دارد، باعث ایجاد صرفه اقتصادی برای فعالیت در زمینه فرآوری این محصول خواهد شد. در همین راستا فعالیت در حوزه تولید آبمیوه، مربا، شربت، کمپوت و پودر ژله پیشنهاد می‌شود. علاوه بر تمشک، هلو، شلیل و مرکبات نیز از جمله محصولات باغی دارای مزیت جهت استفاده در حوزه صنایع غذایی هستند که فرآوری آنها علاوه بر ایجاد انگیزه برای تولید بیشتر، ارزش افزوده خوبی را عاید کشاورزان خواهد کرد. تولید چیپس، میوه خشک، آبمیوه، کمپوت، شربت، کنسانتره، لواشک و مربا از انواع محصولاتی می‌باشند که به راحتی از میوه‌های فوق بدست آمده و مورد پیشنهاد می‌باشند."),n.a.createElement("p",null,"فرآوری و بسته‌بندی انواع گیاهان دارویی به علت افزایش تمایل مردم به استفاده از محصولات طبیعی و گیاهی و همچنین سطح زیرکشت قابل توجه این گیاهان در شهرستان نکا، پیشنهاد می‌شود. با توجه به اين كه از تمام گلبرگ‌ها و غنچه‌های گياهان دارويي از جمله گل محمدي مي‌توان به صورت تازه و خشك استفاده كرد، داراي ارزش اقتصادي بسيار بالايي هستند. همچنین به علت بهره‌برداري سريع از باغ‌هاي گل محمدي، كشت اين گياه براي باغداران بسيار مقرون به صرفه است. از گل محمدي به عنوان گل تازه، گل خشك شده، غنچه، اسانس‌گيري، گلاب، گرده گل، مرباي گل محمدي و... مي‌توان استفاده كرد و سود و درآمد افراد مشغول به کار در این زمینه را افزایش داد. در واقع در كنار همه اين مزيت‌هاي اقتصادي توليد گل محمدي و ظرفيت‌هايي كه در استان مازندران در توليد اين گياه وجود دارد، ايجاد صنايع تبديلي در اين بخش، يك ضرورت مهم و اساسي است."," "),n.a.createElement("p",null,"در حوزه صنایع غذایی، فرآوری گندم به دلیل سطح زیر کشت بالا و کاربردهای متعدد در تولیدات غذایی بسیار حائز اهمیت است. فرآوری گندم به منظور تولید نان، آرد، شیرینی و رشته آشی و پلویی و سایر فرآورده‌های حاصل از گندم مانند کلوچه و بیسکویت، پفک گندمی، نشاسته گندم، نان بستنی و آرد سوخاری از نظر ایجاد ارزش افزوده و سودآوری دوچندان مورد پیشنهاد است. این محصولات همواره توسط مردم مورد استفاده بوده و به علت وجود بازار مصرف، پیشنهاد توسعه این موارد داده می‌شود."," "),n.a.createElement("p",null,"روغن‌گیری از گیاه ارزشمند کلزا با توجه به میزان کشت آن در سطح شهرستان و افزایش تمایل مردم به استفاده از روغن آن، دارای مزیت اقتصادی بوده و پیشنهاد می‌شود. کلزا به عنوان یک دانه روغنی بسیار مهم در تولید محصولات حوزه صنایع غذایی و یا تولید انواع نهاده‌های خوراکی دام و طیور در بسیاری از کشورها متداول است. روغن کلزا از جمله مشتقات این گیاه سودآور است. سویا محصول مفید دیگری است که دارای انواع و اقسام خاصیت‌های پزشکی و درمانی است؛ در زمینه پیشگری از آلزایمر بسیار موثر بوده و مکمل مناسبی برای افراد دارای فشار خون است. لوبیای سبز سویا، تنقلات سویا مثل آجیل سویا، جوانه‌ها، شیر سویا و توفو (پنیر سویا) از جمله محصولات غیر تخمیری سویا هستند؛ در حالی که محصولاتی از قبیل سس سویا، تمپه، میسو، ناتو و تافوی تخمیری از دسته محصولات تخمیری سویا به حساب می‌آیند. هر چند در حال حاضر در بازارهای ایران آجیل سویا، شیر سویا، سس سویا و روغن سویا رواج دارد. کار در زمینه تولید این محصولات پیشنهاد می‌شود و می‌تواند محرک خوبی برای توسعه کشت باشد ولی قبل از شروع کار باید به فرهنگ‌سازی استفاده از هرکدام از موارد فوق و بازار فروش آنها دقت ویژه‌ای داشت. در ضمن با توجه به میزان استفاده از فرآورده‌های سویا در کشورهای آسیای شرقی، در صورت وجود صرفه اقتصادی، می‌توان به صادرات نیز فکر کرد."," "),n.a.createElement("p",null,"فرآوری انواع صیفی‌جات کشت شده در گلخانه‌ها و فضای زراعی نظیر تولید رب گوجه فرنگی و خیارشور به علت وجود بازار مصرف و تقاضا دارای مزیت خواهد بود و پیشنهاد می‌شود. فرآوری شیر نیز در حوزه صنایع غذایی به دلیل رونق بخش دامداری از اهمیت بالایی برخوردار است. علی‌رغم این که مقداری از شیرهای تولیدی در سطح نکا به خارج از شهرستان برای فرآوری فرستاده می‌شود ولی وجود تقاضا برای محصولاتی مانند کره، ماست، پنیر و دیگر فرآوردهای شیری باعث ایجاد صرفه اقتصادی کار و فعالیت در این حوزه خواهد شد. در ضمن طبق لیست مزیت سنجی، فرآوری انار و قارچ نیز دارای مزیت دانسته شده‌اند که البته در صورت افزایش میزان تولید و وجود بازار فروش دارای صرفه اقتصادی خواهند بود. در پایان باید اشاره کرد که بصورت کلی کار در زمینه تولید انواع محصولات صنایع غذایی در شهرستان نکا، با توجه به مهارت کشاورزان و کیفیت بالای محصولات تولیدی دارای مزیت اقتصادی بالایی است."," ")),n.a.createElement(r.a,{id:"services",title:"خدمات",size:"h3"},n.a.createElement("p",null,"طبق بررسی‌های انجام شده، آثار باستانی خاصی در سطح شهرستان نکا مشاهده نمی‌شود اما میزان جاذبه‌های طبیعی بکر و خاصی وجود دارد. همچنین میزان بازدید از این موارد به علت نبود زیرساخت‌های اقامتی و رفاهی خیلی زیاد نیست. در همین راستا ارائه خدمات گردشگری مانند ایجاد اقامتگاه‌های بومگردی و رستوران‌های بومی پیشنهاد می‌شود. غار گمیشان جاذبه‌ای است ارزشمند از دل تاریخ و دوران فراپارینه سنگی که در روستای کمیشان قرار دارد. پس از باز شدن در ورودی غار ابزار کشاورزی، استخوا‌ن‌های جانوران و ابزارهای مربوط به ۸ تا ۱۲ هزار سال پیش در این مکان کشف و ثبت شده است. دریاچه استخر پشت نام گودالی بزرگ و دایره ای شکل است که در روستای استخر پشت نکا و در میان دره رودخانه زارم قرار دارد. اطراف این دریاچه مملو از کشتزارهای برنج و همچنین نیزار بوده که چشم انداز زیبایی را خلق کرده است. در سه کیلومتری روستای درویشان شهرستان نکا، آبشاری زیبا در میان مزارع و همچنین جنگل درختان پهن برگ با نام آبشار دزده قرار دارد. آبشار دزده یکی از پر آب‌ترین آبشارهای منطقه بشمار آمده و حوضچه‌ای بسیار عمیق دارد. این آبشار از فاصله ۵ متری و از میان دو صخره سنگی سرازیر شده و همچنین در انتهای آن حوضچه‌ای با ارتفاع ۱۵ متر قرار دارد. موارد ذکر شده در بالا از جمله جاذبه‌های طبیعی منحصر به فرد سطح شهرستان نکا است که قابلیت سرمایه‌گذاری در زمینه ارائه خدمات گردشگری دارند."," ")),n.a.createElement(r.a,{id:"livestock",title:"دامداری",size:"h3"},n.a.createElement("p",null,"شهرستان نکا، با دارا بودن شرایط آب و هوایی خاص، یکی از قطب‌های مهم صنعت دامداری استان مازندران محسوب می شود. شرایط شهرستان نکا، برای پرورش دام‌های سبک و سنگین مانند گاومیش، گوسفند، بز و گاوهایی با نژادهای بومی و دورگه مهیاست. در ضمن تعدادی واحد جمع آوری شیر نیز در نکا وجود دارد که شیرهای تولیدی را برای فرآوری به واحدهای داخل و خارج شهرستان منتقل می‌کند. توسعه دامداری‌های روستایی باعث افزایش تولید و اشتغال در جوامع روستایی شده و نقش مهمی در افزایش و بهبود اوضاع اقتصادی روستائیان دارد .در واقع نیاز و تقاضا به محصولات دامی، زودبازده بودن این صنعت نسبت به کشاورزی و همچنین سود خالص قابل توجهی که از فعالیت در این حیطه عاید افراد می شود، کار در این زمینه و مشاغل وابسته و مرتبط با آن را دارای مزیت می کند. شرایط اقلیمی مساعد و مهارت زنبورداران این منطقه، همچنین میزان تقاضا برای این محصول، مزیت کار در زمینه زنبورداری را چندین برابر کرده است."," ")),n.a.createElement(r.a,{id:"agriculture",title:"زراعت",size:"h3"},n.a.createElement("p",null,"بخش زراعت در روند رشد و توسعه اقتصادی بخش‌های مختلف شهرستان نکا نقش‎های متعددی بر عهده دارد که از جمله مهمترین آن‎ها می‎توان تامین مواد خام و اولیه مورد نیاز صنایع، تامین نهاده‎هایی مانند نیروی کار، مصرف تولیدات سایر بخش‏های صنعتی از قبیل کودها و سموم شیمیایی و ماشین‌آلات کشاورزی، سودآوری برای فعالان این بخش و تامین مالی سایر بخش‎های زیربنایی را نام برد. بطور کلی گندم در مناطق جلگه‌ای مازندران بصورت آبی و در مناطق میان بند و کوهستانی، بصورت دیم کشت می‌شود. بیشترین سطح زیر کشت این محصول در استان نیز مربوط به مناطق بهشهر، نکا و گلوگاه است."," "),n.a.createElement("p",null,"مزیت توسعه کشت گندم علاوه بر خرید تضمینی دولت و استراتژیک بودن این محصول برای کشور، استفاده و مصرف آن در بسیاری ازمواد غذایی اصلی و پر تقاضا است. از این‌رو هر میزان گندم که کشت شود به فروش رفته و همواره مورد توجه می‌باشد."," "),n.a.createElement("p",null,"طبق بررسی‌ها در اراضی کشاورزی شهرستان نکا ۳۰۰ هکتار جو کشت شده که ۲۷۰ هکتار به صورت دیم و ۳۰۰ هکتار به صورت آبی بوده است. تامین نیاز شهرستان به این محصول و استراتژیک بودن آن از مزیت‌هایی است که باعث می شود بتوان توسعه کشت جو را پیشنهاد داد. سویا از جمله دانه‌های روغنی سودآوری است که در سطح شهرستان تولید می‌شود. توسعه کشت و زراعت سویا نیز به دليل قابليت سازگاري بالا و بخصوص امكان زراعت آن به عنوان كشت دوم در اكثر مناطق همچنين با دارا بودن 45-35 درصد پروتئين، 24-16 درصد روغن، از ارزش بالایی از نظر توليد روغن، پروتئين گياهي و علوفه برخوردار است و به دلیل پتانسیل اقتصادی بالا پیشنهاد می‌شود. در استان مازندران بیش از 750 هکتار از اراضی به کشت گیاهان دارویی اختصاص دارد که از این مقدار، 350 هکتار متعلق به شهرستان نکا می‌باشد. بیشترین میزان سطح زیرکشت در نکا نیز به گیاه دارویی گل گاوزبان در حدود 300 هکتار اختصاص داشته است. از جمله سایر گیاهان دارویی باید به گل‌ محمدی، آویشن، مریم‌گلی، نعناع ‌فلفلی، عروسک‌پشته و... اشاره کرد. توسعه کشت گیاهان دارویی، یکی از اصلی‌ترین مزیت‌های رسته زراعت در نکا محسوب می‌شود. ایجاد اشتغال، رونق و ایجاد صنایع تبدیلی و فرآوری و همچنین درآمدزا بودن این مشاغل از مهمترین مزیت‌های کار در این حوزه است. همچنین وجود تقاضا و افزایش میل مردم به مصرف این محصولات، مقرون به صرفه بودن کار در این زمینه را یادآور می شود."," "),n.a.createElement("p",null,"توسعه کشت دانه‌های روغنی کلزا با هدف کاهش وابستگی کشور به واردات دانه‌های روغنی، روغن، کنجاله و حرکت در مسیر خودکفایی، از اهمیت بالایی برخوردار بوده و باعث کاهش میزان خروج ارز از کشور خواهد شد. طبق بررسی‌ها ۲۸۰۰ هکتار از زمین‌های کشاورزی شهرستان نکا زیر کشت یکی از مهمترین و پر تقاضاترین گیاهان زراعی یعنی کلزا رفته است. لذا ایجاد توسعه و ادامه فعالیت در این حیطه به علت وجود تقاضا و مزیت افزایش تولید، پیشنهاد می‌شود."),n.a.createElement("p",null,"کشت شبدر باعث حاصلخیزی زمین، تامین علوفه دامداران و صرفه جویی در مصرف کود شیمیایی برای کشت برنج می‌شود. تا کنون در سطح ۵۰۰ هکتار از اراضی زراعی شبدر کشت شده است که پیش‌بینی می‌شود این میزان به رشدی معادل دو هزار و ۲۰۰ هکتار برسد. توسعه در زمینه تولید شبدر به عنوان کشت دوم و به علت فوایدی که در ابتدا ذکر شد، می‌تواند سود قابل ملاحظه‌ای برای کشاورزان بومی شهرستان به همراه داشته باشد. برنج، یکی از اصلی‌ترین محصولات زراعی در سطح نکاست. این محصول بعد از گندم، دارای جایگاه دوم مصرف در سطح کشور نیز می‌باشد. در شهرستان نکا به مساحت هفت هزار و 500 هکتار شالیزار وجود دارد که 650 هکتار از آن زیر کشت برنج رفته است. این محصول منبع درآمد و امرار معاش بسیاری از مردم روستایی و بومی منطقه است و همچنین سازگاری این محصول با آب وهوای نکا، از اصلی‌ترین علل برای پیشنهاد ادامه کار در این حیطه است."," "),n.a.createElement("p",null,"در لیست مزیت سنجی به علت وجود بازار مصرف مناسب، کشت سیب زمینی دارای مزیت شمرده شده است. همچنین به کشت هندوانه به دلیل جوابگو بودن فضای منطقه اشاره شده است که البته در این رابطه باید گفت به علت نیاز آبی زیاد این محصول، برای کشت و زراعت آن مزیت زیادی وجود نخواهد داشت و در اولویت‌های آتی قرار می‌گیرد.")),n.a.createElement(r.a,{id:"birds",title:"طیور",size:"h3"},n.a.createElement("p",null,"پرورش طیور یکی از مشاغل سخت و پرکاری است که به دلیل نیاز به منابع غذایی، از اهمیت بالایی برخوردار است و سود سرشاری دارد. در این زمینه برای پرورش بسیاری از طیور شرایط مساعدی در شهرستان وجود دارد اما نکته مهم و قابل توجه، بازاریابی جهت فروش محصولات تولیدی است. در واقع وجود بازار فروش دائمی و مطمئن می‌تواند زمینه را برای گسترش طرح های پرورش طیور چه به صورت سنتی و چه صنعتی فراهم نماید. بنابراین طیوری که به آن پرداخته می‌شوند، به شرط بازاریابی مناسب مزیت پرورش دارند. بلدرچین نوع خاصی از ماکیان است که برای پرورش به آب و هوایی معتدل نیاز دارد و نمی تواند در دمای بسیار بالا یا بسیار پایین رشد کند. به صورت کلی آب و هوای شمال ایران ازجمله نکا متعادل بوده و برای پرورش انواع طیور و ماکیان مناسب است. بر اساس بررسی‌ها پرورش بلدرچین در شهرستان انجام می‌شود و با توجه به ذائقه مردم منطقه می تواند توسعه پیدا کند. کبک، بوقلمون و اردک نیز همین وضعیت را دارند. وجود بازار فروش، مهارت مرغداران و آب و هوای مساعد نکا برای کار در این حیطه، از مهمترین علل وجود مزیت برای توسعه مرغداری است البته با توجه به قوانین وضع شده جدید برای اخذ مجوز واحدهای جدید، قبل از هر اقدامی در این زمینه باید شرایط بازار فروش و معیارهای لازم برای کار در این زمینه را بررسی کرد. در ضمن در لیست مزیت سنجی به مزیت پرورش غاز به علت فراهم بودن شرایط پرورش و بازار مصرف اشاره شده است و مورد پیشنهاد است."," ")),n.a.createElement(r.a,{id:"handicrafts",title:"صنایع دستی",size:"h3"},n.a.createElement("p",null,"شهرستان نکا، شهرستانی توریستی و گردشگر پذیر است. آمد و شد توریست و گردشگر به منطقه، تاثیر مستقیمی بر روی فروش محصولات صنایع دستی تولیدی بومیان دارد. با توجه به اینکه بازار فروش یکی از اصلی ترین عوامل افزایش و توسعه تولیدات صنایع دستی و ایجاد انگیزه بافندگان و افراد فعال در مشاغل مرتبط این صنعت است، توسعه گردشگری و ایجاد بستری برای فروش این محصولات حائز اهمیت خواهد بود. تولید محصولاتی با چوب مانند معرق کاری، منبت کاری، بافت قالی و گلیم در شهرستان نکا انجام می‌شود که در صورت ایجاد بستر مناسب برای فروش و تامین ارزان مواد اولیه می‌توانند توسعه پیدا کنند و شغل پایداری را برای افراد مستقر در این حیطه ایجاد کنند."," ")),n.a.createElement(r.a,{id:"industry",title:"صنعت",size:"h3"},n.a.createElement("p",null,"شهرستان نکا یکی از صنعتی ترین شهرستان های استان مازندران است. واحدهای صنعتی زیادی در این شهرستان مشغول به فعالیت هستند که از جمله آنها می‌توان به کارخانه مازن گاز، نیروگاه شهید سلیمی و کارخانه سیمان اشاره کرد. در ضمن در لیست مزیت سنجی به تولید مصنوعات چوبی (ذغال) به علت وجود مواد اولیه اشاره شده است که مستعد سرمایه گذاری و کسب درآمد اقتصادی است."," "))))}}}]);
